REPLACE INTO `traducoes` (`IdTraducao`, `NomeTraducao`, `ObservacaoTraducao`, `AnoTraducao`, `IdIdioma`) VALUES
	(1, 'Tradução - CNBB', 'Tradução segundo a Conferência Nacional dos Bispos do Brasil, consultada em 2025.', '2025-01-28', 1);
REPLACE INTO `versiculos` (`IdVersiculo`, `TextoVersiculo`, `NumeroVersiculo`, `Observacao`, `IdCapitulo`, `IdTraducao`) VALUES
	(1, 'No princípio, Deus criou o céu e a terra.', 1, b'0', 1, 1),
	(2, 'A terra estava deserta e vazia, as trevas cobriam o abismo e o Espírito de Deus pairava sobre as águas.', 2, b'0', 1, 1),
	(3, 'Deus disse: “Faça-se a luz”! E a luz se fez. 4 Deus viu que a luz era boa. Deus separou a luz das trevas.', 3, b'0', 1, 1),
	(4, 'À luz Deus chamou “dia” e às trevas chamou “noite”. Houve uma tarde e uma manhã: o primeiro dia.', 4, b'0', 1, 1),
	(5, 'À luz Deus chamou “dia” e às trevas chamou “noite”. Houve uma tarde e uma manhã: o primeiro dia.', 5, b'0', 1, 1),
	(6, 'Deus disse: “Faça-se um firmamento entre as águas, separando umas das outras”.', 6, b'0', 1, 1),
	(7, 'E Deus fez o firmamento. Separou as águas debaixo do firmamento, das águas acima do firmamento. E assim se fez.', 7, b'0', 1, 1),
	(8, 'Ao firmamento Deus chamou “céu”. Houve uma tarde e uma manhã: o segundo dia.', 8, b'0', 1, 1),
	(9, 'Deus disse: “Juntem-se num único lugar as águas que estão debaixo do céu, para que apareça o solo firme”. E assim se fez.', 9, b'0', 1, 1),
	(10, 'Ao solo firme Deus chamou “terra” e ao ajuntamento das águas, “mar”. E Deus viu que era bom.', 10, b'0', 1, 1),
	(11, 'Deus disse: “A terra faça brotar vegetação: plantas, que dêem semente, e árvores frutíferas, que dêem fruto sobre a terra, tendo em si a semente de sua espécie”. E assim se fez.', 11, b'0', 1, 1),
	(12, 'A terra produziu vegetação: plantas, que dão a semente de sua espécie, e árvores, que dão seu fruto com a semente de sua espécie. E Deus viu que era bom.', 12, b'0', 1, 1),
	(13, 'Houve uma tarde e uma manhã: o terceiro dia.', 13, b'0', 1, 1),
	(14, 'Deus disse: “Façam-se luzeiros no firmamento do céu, para separar o dia da noite. Que sirvam de sinais para marcar as festas, os dias e os anos.', 14, b'0', 1, 1),
	(15, 'E, como luzeiros no firmamento do céu, sirvam para iluminar a terra”. E assim se fez. 16 Deus fez os dois grandes luzeiros, o luzeiro maior para presidir ao dia e o luzeiro menor para presidir à noite, e também as estrelas.', 15, b'0', 1, 1),
	(16, 'Deus fez os dois grandes luzeiros, o luzeiro maior para presidir ao dia e o luzeiro menor para presidir à noite, e também as estrelas.', 16, b'0', 1, 1),
	(17, 'Deus colocou-os no firmamento do céu para iluminar a terra,', 17, b'0', 1, 1),
	(18, 'presidir ao dia e à noite e separar a luz das trevas. E Deus viu que era bom.', 18, b'0', 1, 1),
	(19, 'Houve uma tarde e uma manhã: o quarto dia.', 19, b'0', 1, 1),
	(20, 'Deus disse: “Fervilhem as águas de seres vivos e voem pássaros sobre a terra, debaixo do firmamento do céu”.', 20, b'0', 1, 1),
	(21, 'Deus criou os grandes monstros marinhos e todos os seres vivos', 21, b'0', 1, 1),
	(22, 'Deus os abençoou, dizendo: “Sede fecundos, multiplicai-vos e enchei as', 22, b'0', 1, 1),
	(23, 'Houve uma tarde e uma manhã: o quinto dia.', 23, b'0', 1, 1),
	(24, 'Deus disse: “Produza a terra seres vivos segundo suas espécies,animais domésticos, animais pequenos e animais selvagens, segundo suas espécies”. E assim se fez.', 24, b'0', 1, 1),
	(25, 'Deus fez os animais selvagens segundo suas espécies, os animais domésticos segundo suas espécies e todos os animais pequenos do chão segundo suas espécies. E Deus viu que era bom.', 25, b'0', 1, 1),
	(26, 'Deus disse: “Façamos o ser humano à nossa imagem e segundo nossa semelhança, para que domine sobre os peixes do mar, as aves do céu, os animais domésticos, todos os animais selvagens e todos os animais que se movem pelo chão”.', 26, b'0', 1, 1),
	(27, 'Deus criou o ser humano à sua imagem, à imagem de Deus o criou. Homem e mulher ele os criou.', 27, b'0', 1, 1),
	(28, 'E Deus os abençoou e lhes disse: “Sede fecundos e multiplicai-vos, enchei a terra e submetei-a! Dominai sobre os peixes do mar, as aves do céu e todos os animais que se movem pelo chão”.', 28, b'0', 1, 1),
	(29, 'Deus disse: “Eis que vos dou, sobre toda a terra, todas as plantas que dão semente e todas as árvores que produzem seu fruto com sua semente, para vos servirem de alimento.', 29, b'0', 1, 1),
	(30, 'E a todos os animais da terra, a todas as aves do céu e a todos os animais que se movem pelo chão, eu lhes dou todos os vegetais para alimento”. E assim se fez.', 30, b'0', 1, 1),
	(31, 'E Deus viu tudo quanto havia feito, e era muito bom. Houve uma tarde e uma manhã: o sexto dia.', 31, b'0', 1, 1),
	(32, 'Assim foram concluídos o céu e a terra com todos os seus elementos.', 1, b'0', 2, 1),
	(33, 'No sétimo dia, Deus concluiu toda a obra que tinha feito; e no sétimo dia repousou de toda a obra que fizera.', 2, b'0', 2, 1),
	(34, 'Deus abençoou o sétimo dia e o santificou, pois nesse dia Deus repousou de toda a obra da criação.', 3, b'0', 2, 1),
	(35, 'Essa é a história da criação do céu e da terra.', 4, b'0', 2, 1),
	(36, 'Quando o Senhor Deus fez a terra e o céu, ainda não havia nenhum arbusto do campo sobre a terra e ainda não tinha brotado a vegetação, porque o Senhor Deus ainda não tinha enviado chuva sobre a terra, e não havia ninguém para cultivar o solo.', 5, b'0', 2, 1),
	(37, 'Mas brotava da terra uma fonte, que lhe regava toda a superfície.', 6, b'0', 2, 1),
	(38, 'Então o Senhor Deus formou o ser humano com o pó do solo, soprou-lhe nas narinas o sopro da vida, e ele tornou-se um ser vivente.', 7, b'0', 2, 1),
	(39, 'Depois, o Senhor Deus plantou um jardim em Éden, a oriente, e pôs ali o homem que havia formado.', 8, b'0', 2, 1),
	(40, 'E o Senhor Deus fez brotar do solo toda sorte de árvores de aspecto atraente e de fruto saboroso, e, no meio do jardim, a árvore da vida e a árvore do conhecimento do bem e do mal.', 9, b'0', 2, 1),
	(41, 'De Éden nascia um rio que irrigava o jardim e, de lá, se dividia em quatro braços.', 10, b'0', 2, 1),
	(42, 'O primeiro chamava-se Fison; ele banha toda a terra de Hévila, onde se encontra o ouro,', 11, b'0', 2, 1),
	(43, 'um ouro muito puro, como também o bdélio e a pedra de ônix.', 12, b'0', 2, 1),
	(44, 'O nome do segundo rio é  Geon, o rio que banha toda a terra de Cuch.', 13, b'0', 2, 1),
	(45, 'O nome do terceiro rio é Tigre. Corre a oriente da Assíria. E o quarto rio é o Eufrates.', 14, b'0', 2, 1),
	(46, 'O Senhor Deus tomou o homem e o colocou no jardim de Éden, para o cultivar e guardar.', 15, b'0', 2, 1),
	(47, 'O Senhor Deus deu-lhe uma ordem, dizendo: “Podes comer de todas as árvores do jardim.', 16, b'0', 2, 1),
	(48, 'Mas da árvore do conhecimento do bem e do mal não deves comer, porque, no dia em que dele comeres, com certeza morrerás”.', 17, b'0', 2, 1),
	(49, 'E o Senhor Deus disse: “Não é bom que o homem esteja só. Vou fazer-lhe uma auxiliar que lhe corresponda”.', 18, b'0', 2, 1),
	(50, 'Então o Senhor Deus formou da terra todos os animais selvagens e todas as aves do céu, e apresentou-os ao homem para ver como os chamaria; cada ser vivo teria o nome que o homem lhe desse.', 19, b'0', 2, 1),
	(51, 'E o homem deu nome a todos os animais domésticos, a todas as aves do céu e a todos os animais selvagens, mas não encontrou uma auxiliar que lhe correspondesse.', 20, b'0', 2, 1),
	(52, 'Então o Senhor Deus fez vir sobre o homem um profundo sono, e ele adormeceu. Tirou-lhe uma das costelas e fechou o lugar com carne.', 21, b'0', 2, 1),
	(53, 'Depois, da costela tirada do homem, o Senhor Deus formou a mulher e apresentou-a ao homem.', 22, b'0', 2, 1),
	(54, 'E o homem exclamou: “Desta vez sim, é osso dos meus ossos e carne da minha carne! Ela será chamada ‘humana’ porque do homem foi tirada”.', 23, b'0', 2, 1),
	(55, 'Por isso deixará o homem o pai e a mãe e se unirá à sua mulher, e eles serão uma só carne.', 24, b'0', 2, 1),
	(56, 'O homem e sua mulher estavam nus, mas não se envergonhavam.', 25, b'0', 2, 1),
	(57, 'A serpente era o mais astuto de todos os animais selvagens que o Senhor Deus tinha feito. Ela disse à mulher: “É verdade que Deus vos disse: ‘Não comais de nenhuma das árvores do jardim?’”', 1, b'0', 3, 1),
	(58, 'A mulher respondeu à serpente: “Nós podemos comer do fruto das árvores do jardim.', 2, b'0', 3, 1),
	(59, 'Mas do fruto da árvore que está no meio do jardim, Deus nos disse: ‘Não comais dele nem sequer o toqueis, do contrário morrereis’”.', 3, b'0', 3, 1),
	(60, 'Mas a serpente respondeu à mulher: “De modo algum morrereis.', 4, b'0', 3, 1),
	(61, 'Pelo contrário, Deus sabe que, no dia em que comerdes da árvore, vossos olhos se abrirão, e sereis como Deus, conhecedores do bem e do mal”.', 5, b'0', 3, 1),
	(62, 'A mulher viu que seria bom comer da árvore, pois era atraente para os olhos e desejável para obter conhecimento. Colheu o fruto, comeu dele e o deu ao marido a seu lado, que também comeu.', 6, b'0', 3, 1),
	(63, 'Então os olhos de ambos se abriram, e, como reparassem que estavam nus, teceram para si tangas com folhas de figueira.', 7, b'0', 3, 1),
	(64, 'Quando ouviram o ruído do Senhor Deus, que passeava pelo jardim à brisa da tarde, o homem e a mulher esconderam-se do Senhor Deus no meio das árvores do jardim.', 8, b'0', 3, 1),
	(65, 'Mas o Senhor Deus chamou o homem e perguntou: “Onde estás?”', 9, b'0', 3, 1),
	(66, 'Ele respondeu: “Ouvi teu ruído no jardim. Fiquei com medo, porque estava nu, e escondi-me”.', 10, b'0', 3, 1),
	(67, 'Deus perguntou: “E quem te disse que estavas nu? Então comeste da árvore, de cujo fruto te proibi comer?”', 11, b'0', 3, 1),
	(68, 'O homem respondeu: “A mulher que me deste por companheira, foi ela que me fez provar  do fruto da árvore, e eu comi”.', 12, b'0', 3, 1),
	(69, 'Então o Senhor Deus perguntou à mulher: “Por que fizeste isso?” E a mulher respondeu: “A serpente enganou-me, e eu comi”.', 13, b'0', 3, 1),
	(70, 'E o Senhor Deus disse à serpente: “Porque fizeste isso, serás maldita entre todos os animais domésticos e entre todos os animais selvagens. Rastejarás sobre teu ventre e comerás pó todos os dias de tua vida.', 14, b'0', 3, 1),
	(71, 'Porei inimizade entre ti e a mulher, entre a tua descendência e a dela. Esta te ferirá a cabeça e tu lhe ferirás o calcanhar”.', 15, b'0', 3, 1),
	(72, 'À mulher ele disse: “Multiplicarei os sofrimentos de tua gravidez. Entre dores darás à luz os filhos. Teus desejos te arrastarão para teu marido, e ele te dominará”.', 16, b'0', 3, 1),
	(73, 'Ao homem ele disse: “Porque ouviste a voz da tua mulher e comeste da árvore, de cujo fruto te proibi comer, amaldiçoado será o solo por tua causa. Com sofrimento tirarás dele o alimento todos os dias de tua vida.', 17, b'0', 3, 1),
	(74, 'Ele produzirá para ti espinhos e ervas daninhas, e tu comerás das ervas do campo.', 18, b'0', 3, 1),
	(75, 'Comerás o pão com o suor do teu rosto, até voltares ao solo, do qual foste tirado. Porque tu és pó e ao pó hás de voltar”.', 19, b'0', 3, 1),
	(76, 'O homem chamou à sua mulher “Eva”, porque ela se tornou a mãe de todos os viventes.', 20, b'0', 3, 1),
	(77, 'E o Senhor Deus fez para o homem e sua mulher roupas de pele com as quais os vestiu.', 21, b'0', 3, 1),
	(78, 'Então o Senhor Deus disse: “Eis que o homem tornou-se como um de nós, capaz de conhecer o bem e o mal. Não ponha ele agora a mão na árvore da vida, para dela comer e viver para sempre”.', 22, b'0', 3, 1),
	(79, 'E o Senhor Deus o expulsou do jardim de Éden, para que cultivasse o solo do qual fora tirado.', 23, b'0', 3, 1),
	(80, 'Tendo expulso o ser humano, postou a oriente do jardim de Éden os querubins, com a espada fulgurante a cintilar, para guardarem o caminho da árvore da vida.', 24, b'0', 3, 1),
	(81, 'O homem se uniu a Eva, sua mulher, e ela concebeu e deu à luz Caim, dizendo: “Ganhei um homem com a ajuda do Senhor”.', 1, b'0', 4, 1),
	(82, 'Tornou a dar à luz e teve Abel, irmão de Caim. Abel tornou-se pastor de ovelhas e Caim pôs-se a cultivar o solo.', 2, b'0', 4, 1),
	(83, 'Aconteceu, tempos depois, que Caim apresentou ao Senhor frutos do solo como oferta.', 3, b'0', 4, 1),
	(84, 'Abel, por sua vez, ofereceu os primeiros cordeirinhos e a gordura das ovelhas. E o Senhor olhou para Abel e sua oferta,', 4, b'0', 4, 1),
	(85, 'mas não deu atenção a Caim com sua oferta. Caim ficou irritado e com o rosto abatido.', 5, b'0', 4, 1),
	(86, 'Então o Senhor perguntou a Caim: “Por que andas irritado e com o rosto abatido?', 6, b'0', 4, 1),
	(87, 'Não é verdade que, se fizeres o bem, andarás de cabeça erguida? E se fizeres o mal, não estará o pecado espreitando-te à porta? A ti vai seu desejo, mas tu deves dominá-lo”.', 7, b'0', 4, 1),
	(88, 'Caim disse a seu irmão Abel: “Vamos ao campo!” Mas, quando estavam no campo, Caim atirou-se sobre seu irmão Abel e o matou.', 8, b'0', 4, 1),
	(89, 'O Senhor perguntou a Caim: “Onde está teu irmão Abel?” Ele respondeu: “Não sei. Acaso sou o guarda do meu irmão?”', 9, b'0', 4, 1),
	(90, '“Que fizeste?”, perguntou ele. “Do solo está clamando por mim a voz do sangue do teu irmão!', 10, b'0', 4, 1),
	(91, 'Por isso, agora serás amaldiçoado pelo próprio  solo que engoliu o sangue de teu irmão que tu derramaste.', 11, b'0', 4, 1),
	(92, 'Quando cultivares o solo, ele te negará seus frutos e tu virás a ser um fugitivo, vagueando sobre a terra”.', 12, b'0', 4, 1),
	(93, 'Caim disse ao Senhor: “Meu castigo é grande demais para que eu o possa suportar.', 13, b'0', 4, 1),
	(94, 'Se hoje me expulsas deste chão, devo esconder-me de ti, quando estiver fugindo e vagueando pela terra; quem me encontrar vai matar-me”.', 14, b'0', 4, 1),
	(95, 'Mas o Senhor lhe disse: “Se matarem Caim, ele será vingado sete vezes”. O Senhor pôs então um sinal em Caim, para que ninguém, ao encontrá-lo, o matasse.', 15, b'0', 4, 1),
	(96, 'Caim afastou-se da presença do Senhor e foi habitar na região de Nod, a leste de Éden.', 16, b'0', 4, 1),
	(97, 'Caim uniu-se a sua mulher. Ela concebeu e deu à luz Henoc. Caim construiu uma cidade e lhe deu o nome de seu filho, Henoc.', 17, b'0', 4, 1),
	(98, 'Henoc gerou Maviael, Maviael gerou Matusael, e Matusael gerou Lamec.', 18, b'0', 4, 1),
	(99, 'Lamec casou-se com duas mulheres; uma se chamava Ada e a outra, Sela.', 19, b'0', 4, 1),
	(100, 'Sela teve um filho, Tubalcaim, que fabricava todo tipo de instrumentos de bronze e ferro. Noema era a irmã de Tubalcaim.', 20, b'0', 4, 1),
	(101, 'O nome de seu irmão era Jubal, antepassado de todos os tocadores de cítara e flauta.', 21, b'0', 4, 1),
	(102, 'Sela teve um filho, Tubalcaim, que fabricava todo tipo de instrumentos de bronze e ferro. Noema era a irmã de Tubalcaim.', 22, b'0', 4, 1),
	(103, 'Lamec disse às suas mulheres: “Ada e Sela, ouvi minha voz; mulheres de Lamec, escutai o que eu digo! Matei um homem por uma ferida, um jovem por causa de um arranhão.', 23, b'0', 4, 1),
	(104, 'Se Caim for vingado sete vezes, Lamec o será setenta e sete vezes”.', 24, b'0', 4, 1),
	(105, 'Adão uniu-se de novo à sua mulher. Ela deu à luz um filho, a quem chamou Set. “O Senhor – dizia ela – concedeu-me outro descendente no lugar de Abel, que Caim matou”.', 25, b'0', 4, 1),
	(106, 'Set também teve um filho, a quem chamou de Enós. Foi então que se começou a invocar o nome do Senhor.', 26, b'0', 4, 1),
	(107, 'Eis a lista dos descendentes de Adão. Quando Deus criou o ser humano, ele o criou à semelhança de Deus.', 1, b'0', 5, 1),
	(108, 'Criou-os homem e mulher, e os abençoou. E no dia em que os criou, Deus os chamou de “ser humano”.', 2, b'0', 5, 1),
	(109, 'Adão tinha cento e trinta anos quando gerou um filho, à sua semelhança e imagem,e chamou-o Set.', 3, b'0', 5, 1),
	(110, 'Adão viveu mais oitocentos anos e gerou filhos e filhas.', 4, b'0', 5, 1),
	(111, 'Ao todo, Adão viveu novecentos e trinta anos e depois morreu.', 5, b'0', 5, 1),
	(112, 'Set tinha cento e cinco anos quando gerou Enós. 7 Viveu mais oitocentos e sete nos e gerou filhos e filhas.', 6, b'0', 5, 1),
	(113, 'Viveu mais oitocentos e sete nos e gerou filhos e filhas.', 7, b'0', 5, 1),
	(114, 'Ao todo, Set viveu novecentos e doze anos e depois morreu.', 8, b'0', 5, 1),
	(115, 'Enós tinha noventa anos quando gerou Cainã.', 9, b'0', 5, 1),
	(116, 'Viveu mais oitocentos e quinze anos e gerou filhos e filhas.', 10, b'0', 5, 1),
	(117, 'Ao todo, Enós viveu novecentos e cinco anos e depois morreu.', 11, b'0', 5, 1),
	(118, 'Cainã tinha setenta anos quando gerou Malaleel.', 12, b'0', 5, 1),
	(119, 'Viveu mais oitocentos e quarenta anos e gerou filhos e filhas.', 13, b'0', 5, 1),
	(120, 'Ao todo, Cainã viveu novecentos e dez anos e depois morreu.', 14, b'0', 5, 1),
	(121, 'Malaleel tinha sessenta e cinco anos quando gerou Jared.', 15, b'0', 5, 1),
	(122, 'Viveu mais oitocentos e trinta anos e gerou filhos e filhas.', 16, b'0', 5, 1),
	(123, 'Ao todo, Malaleel viveu oitocentos e noventa e cinco anos e depois morreu.', 17, b'0', 5, 1),
	(124, 'Jared tinha cento e sessenta e dois anos quando gerou Henoc.', 18, b'0', 5, 1),
	(125, 'Viveu mais oitocentos anos e gerou filhos e filhas.', 19, b'0', 5, 1),
	(126, 'Ao todo, Jared viveu novecentos e sessenta e dois anos e depois morreu.', 20, b'0', 5, 1),
	(127, 'Henoc tinha sessenta e cinco anos quando gerou Matusalém.', 21, b'0', 5, 1),
	(128, 'Depois de gerar Matusalém, Henoc andou com Deus trezentos anos e gerou filhos e filhas.', 22, b'0', 5, 1),
	(129, 'Ao todo, Henoc viveu trezentos e sessenta e cinco anos.', 23, b'0', 5, 1),
	(130, 'Como Henoc andasse com Deus, desapareceu, pois Deus o havia arrebatado.', 24, b'0', 5, 1),
	(131, 'Matusalém tinha cento e oitenta e sete anos quando gerou Lamec.', 25, b'0', 5, 1),
	(132, 'Viveu mais setecentos e oitenta e dois anos e gerou filhos e filhas.', 26, b'0', 5, 1),
	(133, 'Ao todo, Matusalém viveu novecentos e sessenta e nove anos e depois morreu.', 27, b'0', 5, 1),
	(134, 'Lamec tinha cento e oitenta e dois anos quando gerou um filho,', 28, b'0', 5, 1),
	(135, 'a quem deu o nome de Noé, dizendo: “Este nos consolará do trabalho e do cansaço de nossas mãos, causados pela terra que o Senhor amaldiçoou”.', 29, b'0', 5, 1),
	(136, 'Depois de gerar Noé, Lamec viveu mais quinhentos e noventa e cinco anos e gerou filhos e filhas.', 30, b'0', 5, 1),
	(137, 'Ao todo, Lamec viveu setecentos e setenta e sete anos e depois morreu.', 31, b'0', 5, 1),
	(138, 'Noé tinha quinhentos anos quando gerou Sem, Cam e Jafé.', 32, b'0', 5, 1),
	(139, 'Quando o ser humano começou a procriar-se sobre o solo da terra e gerou filhas,', 1, b'0', 6, 1),
	(140, 'os filhos de Deus viram que as filhas dos humanos eram bonitas e escolheram as que lhes agradassem como mulheres para si.', 2, b'0', 6, 1),
	(141, 'E o Senhor disse: “Meu espírito não animará o ser humano para sempre. Sendo apenas carne, não viverá mais do que cento e vinte anos”.', 3, b'0', 6, 1),
	(142, '(Havia então gigantes na terra, mesmo depois que os filhos de Deus se uniram às filhas dos humanos e lhes geraram filhos. São eles os heróis renomados dos tempos antigos.)', 4, b'0', 6, 1),
	(143, 'O Senhor viu o quanto havia crescido a maldade das pessoas na terra e como todos os projetos de seus corações tendiam unicamente para o mal.', 5, b'0', 6, 1),
	(144, 'Então o Senhor arrependeu-se de ter feito o ser humano na terra e ficou com o coração magoado.', 6, b'0', 6, 1),
	(145, 'E o Senhor disse: “Vou exterminar da face da terra o ser humano que criei e, com ele, os animais, o que se move pelo chão e até as aves do céu, pois estou arrependido de os ter feito”.', 7, b'0', 6, 1),
	(146, 'Noé, porém, encontrou graça aos olhos do Senhor. Noé constrói a arca', 8, b'0', 6, 1),
	(147, 'Esta é a história de Noé: Noé era homem justo e íntegro entre os contemporâneos e sempre andava com Deus.', 9, b'0', 6, 1),
	(148, 'Gerou três filhos: Sem, Cam e Jafé.', 10, b'0', 6, 1),
	(149, 'Mas a terra se perverteu diante de Deus e encheu-se de violência.', 11, b'0', 6, 1),
	(150, 'E Deus viu que a terra estava pervertida: toda a humanidade tinha pervertido sua conduta na terra.', 12, b'0', 6, 1),
	(151, 'Então, Deus disse a Noé: “Decidi pôr fim a toda a humanidade, pois por sua causa a terra está cheia de violência. Vou exterminá-los com a terra.', 13, b'0', 6, 1),
	(152, 'Constrói para ti uma arca de madeira resinosa, divide-a em compartimentos e calafeta-a com piche por dentro e por fora.', 14, b'0', 6, 1),
	(153, 'A arca terá as seguintes dimensões: uns cento e cinqüenta metros de  comprimento, vinte e cinco de largura e quinze de altura.', 15, b'0', 6, 1),
	(154, 'No alto da arca farás, como arremate, uma clarabóia de meio metro. No lado da arca abrirás uma porta e farás na arca um primeiro, um segundo e um terceiro andar.', 16, b'0', 6, 1),
	(155, 'E eu, eu vou mandar um dilúvio sobre a terra, a fim de exterminar toda a carne com sopro de vida debaixo do céu. Tudo o que existe na terra perecerá.', 17, b'0', 6, 1),
	(156, 'Contigo, porém, estabelecerei minha aliança: entrarás na arca com teus filhos, tua mulher e as mulheres de teus filhos.', 18, b'0', 6, 1),
	(157, 'E de cada ser vivo, de tudo o que é carne, farás entrar contigo na arca dois de cada espécie, um macho e uma fêmea, para conservá-los vivos.', 19, b'0', 6, 1),
	(158, 'De cada espécie de ave, de cada espécie de animal doméstico, de cada espécie dos animais pequenos do chão virá a ti um casal, para que os conserves vivos.', 20, b'0', 6, 1),
	(159, 'Quanto a ti, recolhe de tudo o que se pode comer e armazena-o junto a ti, para servir de alimento a ti e a eles”.', 21, b'0', 6, 1),
	(160, 'E Noé executou tudo conforme Deus lhe tinha ordenado.', 22, b'0', 6, 1),
	(161, 'O Senhor disse a Noé: “Entra na arca com todos os de tua casa. Tu és o único justo que encontrei nesta geração.', 1, b'0', 7, 1),
	(162, 'De todos os animais puros toma sete casais, o macho com a fêmea, e dos animais impuros, um casal, o macho com a fêmea.', 2, b'0', 7, 1),
	(163, 'também das aves do céu levarás sete casais, o macho com a fêmea, para que suas espécies se conservem vivas sobre a face da terra.', 3, b'0', 7, 1),
	(164, 'Pois, dentro de sete dias farei chover sobre a terra durante quarenta dias e quarenta noites. Exterminarei da face da terra todos os seres vivos que fiz”.', 4, b'0', 7, 1),
	(165, 'Noé executou tudo conforme o Senhor lhe havia ordenado.', 5, b'0', 7, 1),
	(166, 'Noé tinha seiscentos anos quando as águas do dilúvio inundaram a terra.', 6, b'0', 7, 1),
	(167, 'Noé entrou na arca com os filhos, a mulher e as mulheres dos filhos, diante das águas do dilúvio.', 7, b'0', 7, 1),
	(168, 'Tanto dos animais puros como dos impuros, das aves e de tudo o que se move pelo chão,', 8, b'0', 7, 1),
	(169, 'entrou na arca com Noé sempre um casal, o macho com a fêmea, conforme Deus havia ordenado a Noé.', 9, b'0', 7, 1),
	(170, 'Passados sete dias, as águas do dilúvio inundaram a terra.', 10, b'0', 7, 1),
	(171, 'No ano seiscentos da vida de Noé, no segundo mês, no dia dezessete do mês, nesse dia rebentaram todas as fontes do abismo e se abriram as cataratas do céu.', 11, b'0', 7, 1),
	(172, 'Choveu sobre a terra durante quarenta dias e quarenta noites.', 12, b'0', 7, 1),
	(173, 'Nesse mesmo dia entraram na arca Noé e os filhos Sem, Cam e Jafé, a mulher dele e as três mulheres dos filhos.', 13, b'0', 7, 1),
	(174, 'Além deles, entraram todas as especies dos animais selvagens, dos animais domésticos, dos animais que se movem pelo chão, das aves e de todos os pássaros que voam.', 14, b'0', 7, 1),
	(175, 'Vieram para junto de Noé, na arca, dois a dois, representando todas as criaturas que têm sopro de vida.', 15, b'0', 7, 1),
	(176, 'De todas as espécies de criaturas entraram machos e fêmeas, como Deus havia ordenado. E o Senhor fechou a porta da arca atrás de Noé.', 16, b'0', 7, 1),
	(177, 'Durante quarenta dias, o dilúvio se abateu sobre a terra. As águas subiram e ergueram a arca, que se elevou acima da terra.', 17, b'0', 7, 1),
	(178, 'As águas cresceram e aumentaram muito sobre a terra, de modo que a arca  começou a flutuar na superfície das águas.', 18, b'0', 7, 1),
	(179, 'As águas cresceram tanto sobre a terra que cobriram as montanhas mais altas que há debaixo do céu.', 19, b'0', 7, 1),
	(180, 'As águas subiram uns oito metros acima das montanhas.', 20, b'0', 7, 1),
	(181, 'Pereceram todas as criaturas que se moviam na terra, aves, animais domésticos, animais selvagens e todos os animais que fervilham pelo chão, bem como todos os seres humanos.', 21, b'0', 7, 1),
	(182, 'Morreu tudo o que respirava pelo nariz e vivia em terra firme.', 22, b'0', 7, 1),
	(183, 'Assim foram exterminados todos os seres que havia na face da terra: tanto os seres humanos, como os animais grandes e pequenos e as aves do céu foram exterminados da terra. Restaram apenas Noé e os que estavam com ele na arca.', 23, b'0', 7, 1),
	(184, 'As águas dominaram sobre a terra durante cento e cinqüenta dias.', 24, b'0', 7, 1),
	(185, 'Então Deus se lembrou de Noé e de todos os animais selvagens e domésticos que estavam com ele na arca. Fez soprar um vento sobre a terra, e as águas começaram a baixar.', 1, b'0', 8, 1),
	(186, 'Fecharam-se as fontes do Abismo e as comportas do céu, e a chuva parou de cair.', 2, b'0', 8, 1),
	(187, 'Pouco a pouco as águas foram se retirando da terra. Ao término de cento e cinqüenta dias começaram a diminuir.', 3, b'0', 8, 1),
	(188, 'No dia dezessete do sétimo mês, a arca pousou sobre os montes de Ararat.', 4, b'0', 8, 1),
	(189, 'As águas continuaram diminuindo até o décimo mês. E no primeiro dia desse mês apareceram os cumes das montanhas.', 5, b'0', 8, 1),
	(190, 'Passados mais quarenta dias, Noé abriu a janela que tinha feito na arca', 6, b'0', 8, 1),
	(191, 'e soltou um corvo, que voava indo e vindo até que secassem as águas sobre a terra.', 7, b'0', 8, 1),
	(192, 'Depois soltou uma pomba para ver se as águas já se haviam retirado do solo.', 8, b'0', 8, 1),
	(193, 'Mas a pomba não achou onde pousar e voltou para junto dele na arca. É que as águas ainda cobriam toda a superfície da terra. Noé estendeu a mão para fora, apanhou a pomba e recolheu-a na arca.', 9, b'0', 8, 1),
	(194, 'Depois esperou mais sete dias e tornou a soltar a pomba.', 10, b'0', 8, 1),
	(195, 'Pela tardinha, a pomba voltou com uma folha de oliveira recém arrancada no bico. Assim Noé compreendeu que as águas se haviam retirado da terra.', 11, b'0', 8, 1),
	(196, 'Esperou outros sete dias e soltou a pomba,e ela não voltou mais.', 12, b'0', 8, 1),
	(197, 'Foi no ano seiscentos e um da vida de Noé, no primeiro mês, no dia primeiro do primeiro mês, que as águas tinham secado sobre a terra. Noé abriu o teto da arca, olhou e viu que a superfície do solo estava seca.', 13, b'0', 8, 1),
	(198, 'Foi no dia vinte e sete do segundo mês que a terra ficou enxuta.', 14, b'0', 8, 1),
	(199, 'Então Deus falou a Noé:', 15, b'0', 8, 1),
	(200, '“Sai da arca com tua mulher, teus filhos e as mulheres de teus filhos.', 16, b'0', 8, 1),
	(201, 'Traze para fora também todas as espécies de animais que estão contigo, aves, animais domésticos e animais que se movem pelo chão, para que se propaguem pela terra, sejam fecundos e se multipliquem sobre a terra”.', 17, b'0', 8, 1),
	(202, 'Saiu, pois, Noé da arca com os filhos, a mulher e as mulheres dos filhos.', 18, b'0', 8, 1),
	(203, 'Saíram também todos os animais selvagens e domésticos, todas as aves e todos os animais que se movem pelo chão, todos segundo suas espécies.', 19, b'0', 8, 1),
	(204, 'Então Noé construiu um altar para o Senhor, tomou animais e  aves de todas as espécies puras e ofereceu holocaustos sobre o altar.', 20, b'0', 8, 1),
	(205, 'O Senhor aspirou o agradável odor e disse consigo mesmo: “Nunca mais tornarei a amaldiçoar a terra por causa do gênero humano, por serem más desde a infância as inclinações do coração humano; nunca mais tornarei a castigar todos os seres vivos como acabei de fazer.', 21, b'0', 8, 1),
	(206, 'Enquanto a terra durar, plantio e colheita, frio e calor, verão e inverno, dia e noite jamais hão de cessar”.', 22, b'0', 8, 1),
	(207, 'Deus abençoou Noé e seus filhos, dizendo-lhes: “Sede fecundos, multiplicai-vos e enchei a terra.', 1, b'0', 9, 1),
	(208, 'Sereis causa de medo e de espanto para todos os animais da terra, todas as aves do céu, os bichos que se movem pelo chão e todos os peixes do mar. Eu os entrego todos em vossas mãos.', 2, b'0', 9, 1),
	(209, 'Tudo o que vive e se move vos servirá de alimento. Entrego-vos tudo, como já vos dei os vegetais.', 3, b'0', 9, 1),
	(210, 'Contudo, não deveis comer carne com vida, isto é, com o sangue.', 4, b'0', 9, 1),
	(211, 'Da mesma forma pedirei contas do vosso sangue, que é a vossa vida, a qualquer animal. E da vida do homem pedirei contas a seu irmão.', 5, b'0', 9, 1),
	(212, 'Quem derramar sangue humano, por mãos humanas terá seu sangue derramado, porque Deus fez o ser  humano à sua imagem.', 6, b'0', 9, 1),
	(213, 'Quanto a vós, sede fecundos e multiplicai-vos, povoai a terra e multiplicai- vos nela”.', 7, b'0', 9, 1),
	(214, 'Deus disse a Noé e a seus filhos:', 8, b'0', 9, 1),
	(215, '“De minha parte, vou estabelecer minha aliança  convosco e com vossa descendência,', 9, b'0', 9, 1),
	(216, 'com todos os seres vivos que estão convosco, aves, animais domésticos e selvagens, enfim, com todos os animais da terra que convosco saíram da arca.', 10, b'0', 9, 1),
	(217, 'Estabeleço convosco a minha aliança: não acontecerá novamente que toda a carne seja exterminada pelas águas de um dilúvio. Não haverá mais dilúvio para devastar a terra”.', 11, b'0', 9, 1),
	(218, 'E Deus disse: “Eis o sinal da aliança que estabeleço entre mim e vós e todos os seres vivos que estão convosco, por todas as gerações futuras.', 12, b'0', 9, 1),
	(219, 'Ponho meu arco nas nuvens, como sinal de aliança entre mim e a terra.', 13, b'0', 9, 1),
	(220, 'Quando eu cobrir de nuvens a terra, aparecerá o arco-íris nas nuvens.', 14, b'0', 9, 1),
	(221, 'Então me lembrarei de minha aliança convosco e com todas as espécies de seres vivos, e as águas não se tornarão mais um dilúvio para destruir toda carne.', 15, b'0', 9, 1),
	(222, 'Quando o arco-íris estiver nas nuvens, eu o contemplarei como recordação da aliança eterna entre Deus e todas as espécies de seres vivos sobre a terra”.', 16, b'0', 9, 1),
	(223, 'Deus disse a Noé: “Este é o sinal da aliança que estabeleço entre mim e toda a carne sobre a terra”.', 17, b'0', 9, 1),
	(224, 'Os filhos de Noé, que saíram da arca, foramSem, Cam e Jafé. Cam é o antepassado de Canaã.', 18, b'0', 9, 1),
	(225, 'Esses três eram os filhos de Noé, pelos quais se povoou toda a terra.', 19, b'0', 9, 1),
	(226, 'Noé começou a praticar a  agricultura e plantou uma vinha.', 20, b'0', 9, 1),
	(227, 'Bebeu vinho e se embriagou, ficando despido dentro da tenda.', 21, b'0', 9, 1),
	(228, 'Cam, o antepassado de Canaã, viu a nudez do pai e foi contar aos dois irmãos que estavam fora.', 22, b'0', 9, 1),
	(229, 'Sem e Jafé, porém, puseram o manto nos ombros e, caminhando de costas, cobriram a nudez do pai. Como estavam de costas, não viram a nudez do pai.', 23, b'0', 9, 1),
	(230, 'Despertando da embriaguez, Noé ficou sabendo o que fizera o filho mais novo e', 24, b'0', 9, 1),
	(231, 'disse: “Maldito seja Canaã! Que se torne o último dos escravos de seus irmãos”.', 25, b'0', 9, 1),
	(232, 'E acrescentou: “Bendito seja o Senhor Deus de Sem, e Canaã seja seu escravo.', 26, b'0', 9, 1),
	(233, 'Que Deus faça prosperar Jafé, que ele habite nas tendas de Sem, e Canaã seja seu escravo”.', 27, b'0', 9, 1),
	(234, 'Depois do dilúvio, Noé viveu trezentos e cinqüenta anos.', 28, b'0', 9, 1),
	(235, 'Quando morreu, tinha completado novecentos e cinqüenta anos de idade.', 29, b'0', 9, 1),
	(236, 'Eis os descendentes dos filhos de Noé: Sem, Cam e Jafé. Eles tiveram filhos depois do dilúvio.', 1, b'0', 10, 1),
	(237, 'Filhos de Jafé: Gomer, Magog, Madai, Javã, Tubal, Mosoc e Tiras.', 2, b'0', 10, 1),
	(238, 'Filhos de Gomer: Asquenez, Rifat e Togorma.', 3, b'0', 10, 1),
	(239, 'Filhos de Javã: Elisa e Társis, Cetim e Rodanim.', 4, b'0', 10, 1),
	(240, 'Destes se separaram as populações das ilhas, cada qual segundo seu país, língua, família e nação.', 5, b'0', 10, 1),
	(241, 'Filhos de Cam: Cuch, Mesraim, Fut e Canaã.', 6, b'0', 10, 1),
	(242, 'Filhos de Cuch: Saba, Hévila, Sabata, Regma e Sabataca. Filhos de Regma: Sabá e Dadã.', 7, b'0', 10, 1),
	(243, 'Cuch gerou Nemrod, o primeiro a se tornar valente neste mundo.', 8, b'0', 10, 1),
	(244, 'Era um caçador valente diante do Senhor. Por isso é que se diz: “Caçador valente diante do Senhor, como Nemrod”.', 9, b'0', 10, 1),
	(245, 'As capitais de seu reino foram: Babel, Arac, Acad e Calane na terra de Senaar.', 10, b'0', 10, 1),
	(246, 'Dali se originou Assur, que construiu Nínive, Reobot-Ir, Cale', 11, b'0', 10, 1),
	(247, 'e Resen, a grande cidade que fica entre Nínive e Cale.', 12, b'0', 10, 1),
	(248, 'Mesraim gerou os ludeus, os anameus, os laabeus, os neftuenses,', 13, b'0', 10, 1),
	(249, 'os fetruseus, caslueus e os caftoreus, dos quais descendem os filisteus.', 14, b'0', 10, 1),
	(250, 'Canaã gerou Sidon, o primogênito, e Het,', 15, b'0', 10, 1),
	(251, 'bem como os jebuseus, os amorreus, gergeseus,', 16, b'0', 10, 1),
	(252, 'os heveus, os araceus, os sineus,', 17, b'0', 10, 1),
	(253, 'os arádios, os samareus e os emateus. Depois de se dispersarem as famílias dos cananeus,', 18, b'0', 10, 1),
	(254, 'São esses os filhos de Cam, segundo as famílias, línguas, territórios e nações.', 19, b'0', 10, 1),
	(255, 'São esses os filhos de Cam, segundo as famílias, línguas, territórios e nações.', 20, b'0', 10, 1),
	(256, 'Sem, antepassado de todos os filhos de Héber e irmão mais velho de Jafé, também teve uma descendência.', 21, b'0', 10, 1),
	(257, 'São filhos de Sem: Elam, Assur, Arfaxad, Lud e Aram.', 22, b'0', 10, 1),
	(258, 'Filhos de Aram: Hus, Hul, Geter e Mes.', 23, b'0', 10, 1),
	(259, 'Arfaxad gerou Salé, e Salé, Héber.', 24, b'0', 10, 1),
	(260, 'Héber teve dois filhos, um dos quais se chamou Faleg, pois no seu tempo o país se dividiu. O irmão se chamava Jectã.', 25, b'0', 10, 1),
	(261, 'Jectã gerou Elmodad, Salef, Asarmot, Jaré,', 26, b'0', 10, 1),
	(262, 'Aduram, Uzal, Decla,', 27, b'0', 10, 1),
	(263, 'Ebal, Abimael, Sabá,', 28, b'0', 10, 1),
	(264, 'Ofir, Hévila e Jobab. Todos esses são filhos de Jectã', 29, b'0', 10, 1),
	(265, 'e habitavam a região desde Mesa até Sefar, a montanha oriental.', 30, b'0', 10, 1),
	(266, 'São esses os filhos de Sem, segundo as famílias, línguas, regiões e nações.', 31, b'0', 10, 1),
	(267, 'Todos esses são clãs dos filhos de  Noé, segundo as suas descendências e por nações. A partir deles se espalharam os povos pela terra, depois do dilúvio.', 32, b'0', 10, 1),
	(268, 'A terra inteira tinha uma só língua e usava as mesmas palavras.', 1, b'0', 11, 1),
	(269, 'Ao migrarem do oriente, os homens acharam uma planície na terra de Senaar, e ali se estabeleceram.', 2, b'0', 11, 1),
	(270, 'Disseram uns aos outros: “Vamos fazer tijolos e cozê-los ao fogo”. Utilizaram tijolos como pedras e betume como argamassa.', 3, b'0', 11, 1),
	(271, 'E disseram: “Vamos construir para nós uma cidade e uma torre que chegue até o céu. Assim nos faremos um nome. Do contrário, seremos dispersados por toda a superfície da terra”.', 4, b'0', 11, 1),
	(272, 'Então o Senhor desceu para ver a cidade e a torre que os homens estavam construindo.', 5, b'0', 11, 1),
	(273, 'E o Senhor disse: “Eles formam um só povo e todos falam a mesma língua. Isto é apenas o começo de seus empreendimentos. Agora, nada os impedirá de fazer o que se propuserem.', 6, b'0', 11, 1),
	(274, 'Vamos descer ali e confundir a língua deles, de modo que não se entendam uns aos outros”.', 7, b'0', 11, 1),
	(275, 'E o Senhor os dispersou daquele lugar por toda a superfície da terra, e eles pararam de construir a cidade.', 8, b'0', 11, 1),
	(276, 'Por isso a cidade recebeu o nome de Babel, \\Confusão, porque foi lá que o Senhor confundiu a linguagem de todo mundo, e de lá dispersou os seres humanos por toda a terra. De Sem a Abraão', 9, b'0', 11, 1),
	(277, 'Estes são os descendentes de Sem: Sem tinha cem anos quando gerou Arfaxad, dois anos depois do dilúvio.', 10, b'0', 11, 1),
	(278, 'Sem viveu mais quinhentos anos e gerou filhos e filhas.', 11, b'0', 11, 1),
	(279, 'Arfaxad tinha trinta e cinco anos de idade quando gerou Salé.', 12, b'0', 11, 1),
	(280, 'Viveu mais quatrocentos e três anos, e gerou filhos e filhas.', 13, b'0', 11, 1),
	(281, 'Salé tinha trinta anos de idade quando gerou Héber.', 14, b'0', 11, 1),
	(282, 'Viveu mais quatrocentos e três anos, e gerou filhos e filhas.', 15, b'0', 11, 1),
	(283, 'Héber tinha trinta e quatro anos de idade quando gerou Faleg.', 16, b'0', 11, 1),
	(284, 'Viveu mais quatrocentos e trinta anos, e gerou filhos e filhas.', 17, b'0', 11, 1),
	(285, 'Faleg tinha trinta anos quando gerou Reu.', 18, b'0', 11, 1),
	(286, 'Viveu mais duzentos e nove anos, e gerou filhos e filhas.', 19, b'0', 11, 1),
	(287, 'Reu tinha trinta e dois anos quando gerou Sarug.', 20, b'0', 11, 1),
	(288, 'Viveu mais duzentos e sete anos, e gerou filhos e filhas.', 21, b'0', 11, 1),
	(289, 'Sarug tinha trinta anos quando gerou Nacor.', 22, b'0', 11, 1),
	(290, 'Viveu mais duzentos anos, e gerou filhos e filhas.', 23, b'0', 11, 1),
	(291, 'Nacor tinha vinte e nove anos quando gerou Taré.', 24, b'0', 11, 1),
	(292, 'Viveu mais cento e dezenove anos, e gerou filhos e filhas.', 25, b'0', 11, 1),
	(293, 'Taré tinha setenta anos quando gerou Abrão, Nacor e Arã.', 26, b'0', 11, 1),
	(294, 'O Senhor disse a Abrão: “Sai de tua terra, do meio de teus parentes, da casa de teu pai, e vai para a terra que eu vou te mostrar.', 1, b'0', 12, 1),
	(295, 'Farei de ti uma grande nação e te abençoarei: engrandecerei o teu nome, de modo que ele se torne uma bênção.', 2, b'0', 12, 1),
	(296, 'Abençoarei os que te abençoarem e amaldiçoarei os que te amaldiçoarem. Em ti serão abençoadas todas as famílias da terra”.', 3, b'0', 12, 1),
	(297, 'Abrão partiu, como o Senhor lhe havia dito, e Ló foi com ele. Abrão tinha setenta e cinco anos ao partir de Harã.', 4, b'0', 12, 1),
	(298, 'Ele levou consigo sua mulher Sarai, o sobrinho Ló e todos os bens que possuíam, além dos escravos que haviam adquirido em Harã. Assim partiram rumo à terra de Canaã, e ali chegaram.', 5, b'0', 12, 1),
	(299, 'Abrão atravessou o país até o santuário de Siquém, até o carvalho de Moré. Os cananeus viviam então nessa terra.', 6, b'0', 12, 1),
	(300, 'O Senhor apareceu a Abrão e lhe disse: “Darei esta terra à tua descendência”. Abrão ergueu ali um altar ao Senhor, que lhe tinha aparecido.', 7, b'0', 12, 1),
	(301, 'De lá, deslocou-se em direção ao monte que fica a oriente de Betel, e ali armou as tendas, tendo Betel a ocidente e Hai a oriente. Também ali ergueu um altar ao Senhor e invocou o nome do Senhor.', 8, b'0', 12, 1),
	(302, 'Depois, de acampamento em acampamento, Abrão foi até o deserto do Negueb. Abrão e Sarai no Egito', 9, b'0', 12, 1),
	(303, 'Houve, porém, uma fome no país. Abrão desceu ao Egito para morar ali por algum tempo, porque a fome assolava a terra.', 10, b'0', 12, 1),
	(304, 'Perto de entrar no Egito, disse a Sarai, sua mulher: “Eu sei que és uma mulher bonita.', 11, b'0', 12, 1),
	(305, 'Quando te virem, os egípcios vão dizer: ‘Esta é a mulher dele’, e me matarão, conservando-te viva.', 12, b'0', 12, 1),
	(306, 'Dize, por favor, que és minha irmã, para que me tratem bem por tua causa e, graças a ti, eu salve minha vida”.', 13, b'0', 12, 1),
	(307, 'Quando Abrão entrou no Egito, os egípcios viram que sua mulher era muito bonita.', 14, b'0', 12, 1),
	(308, 'Ao vê-la, os ministros do faraó a elogiaram muito diante dele, de modo que a mulher foi levada ao palácio do faraó.', 15, b'0', 12, 1),
	(309, 'Quanto a Abrão, foi muito bem tratado por causa dela, ganhando ovelhas, bois e jumentos, escravos e escravas, mulas e camelos.', 16, b'0', 12, 1),
	(310, 'O Senhor, porém, castigou com grandes pragas o faraó e sua corte por causa de Sarai, mulher de Abrão.', 17, b'0', 12, 1),
	(311, 'O faraó mandou chamar Abrão e lhe disse: “Por que me fizeste isso? Por que não me contaste que ela era tua mulher?', 18, b'0', 12, 1),
	(312, 'Por que disseste: ‘É minha irmã’, levando-me a tomá-la por esposa? E agora, aqui tens tua mulher. Toma-a e vai embora”.', 19, b'0', 12, 1),
	(313, 'O faraó deu ordens a seus homens que o despachassem com a mulher e tudo o que possuía.', 20, b'0', 12, 1),
	(314, 'Abrão subiu do Egito ao deserto do Negueb, com a mulher, com todos os bens e em companhia de Ló.', 1, b'0', 13, 1),
	(315, 'Abrão era muito rico em rebanhos, prata e ouro.', 2, b'0', 13, 1),
	(316, 'Do Negueb voltou para Betel, de parada em parada, até o lugar onde tinha acampado antes, entre Betel e Hai.', 3, b'0', 13, 1),
	(317, 'Chegando ao lugar onde antes tinha erguido um altar, Abrão invocou o nome do Senhor.', 4, b'0', 13, 1),
	(318, 'Ló, que acompanhava Abrão, também tinha ovelhas, gado e tendas.', 5, b'0', 13, 1),
	(319, 'A região já não bastava para os dois; o tamanho de seus rebanhos não permitia mais que morassem no mesmo lugar.', 6, b'0', 13, 1),
	(320, 'Surgiram discórdias entre os pastores que cuidavam da criação de Abrão e os pastores de Ló. Naquele tempo, os cananeus e os fereseus ainda viviam nessa terra.', 7, b'0', 13, 1),
	(321, 'Abrão disse a Ló: “Não deve haver discórdia entre nós nem entre nossos pastores, pois somos irmãos.', 8, b'0', 13, 1),
	(322, 'Estás vendo toda esta terra diante de ti? Pois bem, peço-te, separa-te de mim. Se fores para a esquerda, eu irei para a direita; se fores para a direita, eu irei para a esquerda”.', 9, b'0', 13, 1),
	(323, 'Levantando os olhos, Ló viu que toda a região em torno do Jordão, até a altura de Segor, era por toda a parte irrigada; era como um jardim do Senhor, como o Egito. (Isso era antes que o Senhor destruísse Sodoma e Gomorra.)', 10, b'0', 13, 1),
	(324, 'Ló escolheu, então, para si a região em torno do rio Jordão e dirigiu-se para oriente. Assim os dois se separaram.', 11, b'0', 13, 1),
	(325, 'Abrão permaneceu na terra de Canaã, enquanto Ló se estabeleceu nas cidades próximas do Jordão, armando suas tendas até Sodoma.', 12, b'0', 13, 1),
	(326, 'Ora, os habitantes de Sodoma eram perversos e pecavam gravemente contra o Senhor.', 13, b'0', 13, 1),
	(327, 'No tempo de seu reinado, Amrafel, rei de Senaar, Arioc, rei de Elasar, Codorlaomor, rei de Elam, e Tadal, rei de Goim,', 1, b'0', 14, 1),
	(328, 'declararam guerra contra Bara, rei de Sodoma, Bersa, rei de Gomorra, Senaab, rei de Adama, Semeber, rei de Seboim e contra o rei de Bela (que é Segor).', 2, b'0', 14, 1),
	(329, 'Estes últimos se haviam concentrado no vale de Sidim (que agora é o mar Morto).', 3, b'0', 14, 1),
	(330, 'Durante doze anos haviam servido a Codorlaomor, mas no décimo terceiro ano se rebelaram.', 4, b'0', 14, 1),
	(331, 'No décimo quarto ano veio Codorlaomor com os reis aliados e derrotou os refaítas em Astarot Carnaim, os zuzitas em Ham, os emitas na planície de Cariataim', 5, b'0', 14, 1),
	(332, 'e os hurritas nos montes de Seir até El-Farã, junto ao deserto.', 6, b'0', 14, 1),
	(333, 'Voltando, vieram a En-Mispat (que é Cades) e devastaram todo o território dos amalecitas e o dos amorreus, que  moravam em Asasontamar.', 7, b'0', 14, 1),
	(334, 'Saíram-lhes ao encontro os reis de Sodoma, Gomorra, Adama, Seboim e Bela (que é Segor). Puseram-se em ordem de batalha, no vale de Sidim,', 8, b'0', 14, 1),
	(335, 'contra Codorlaomor, rei de Elam, Tadal, rei de Goim, Amrafel, rei de Senaar e Arioc, rei de Elasar. Eram quatro reis contra cinco.', 9, b'0', 14, 1),
	(336, 'Havia no vale de Sidim muitos poços de betume. Postos em fuga, os reis de Sodoma e Gomorra caíram neles, enquanto os sobreviventes fugiram para a montanha.', 10, b'0', 14, 1),
	(337, 'Os vencedores saquearam todos os bens e provisões de Sodoma e Gomorra e se retiraram.', 11, b'0', 14, 1),
	(338, 'Levaram também consigo Ló, sobrinho de Abrão, que morava em Sodoma, com todos os seus bens.', 12, b'0', 14, 1),
	(339, 'Um dos fugitivos foi informar a Abrão, o hebreu, que morava junto ao carvalho do amorreu Mambré, irmão de Escol e Aner, aliados de Abrão.', 13, b'0', 14, 1),
	(340, 'Quando Abrão soube que seu parente fora seqüestrado, mobilizou trezentos e dezoito escravos nascidos em sua casa e perseguiu os reis até Dã.', 14, b'0', 14, 1),
	(341, 'Dividiu a tropa e caiu sobre eles de noite, ele com os seus escravos, derrotando-os e perseguindo-os até Hoba, ao norte de Damasco.', 15, b'0', 14, 1),
	(342, 'Recuperou todos os bens, seu parente Ló com todos os bens, as mulheres e sua gente.', 16, b'0', 14, 1),
	(343, 'Quando Abraão voltava, depois da vitória contra Codorlaomor e os reis aliados, saiu-lhe ao encontro o rei de Sodoma no vale de Save (que é o vale do Rei).', 17, b'0', 14, 1),
	(344, 'Melquisedec, rei de Salém, trouxe pão e vinho e, como sacerdote de Deus Altíssimo,', 18, b'0', 14, 1),
	(345, 'abençoou Abrão, dizendo: “Bendito seja Abrão pelo Deus Altíssimo, Criador do céu e da terra.', 19, b'0', 14, 1),
	(346, 'Bendito seja o Deus Altíssimo, que entregou teus inimigos em tuas mãos”. E Abrão entregou-lhe o dízimo de tudo.', 20, b'0', 14, 1),
	(347, 'O rei de Sodoma disse a Abrão: “Entrega- me as pessoas e fica com os bens”.', 21, b'0', 14, 1),
	(348, 'Abrão, porém, respondeu ao rei de Sodoma: “Levanto minha mão para o Senhor, o Deus Altíssimo, Criador do céu e da terra, e juro:', 22, b'0', 14, 1),
	(349, 'nem um fio, nem uma correia de sandália, nem coisa alguma tomarei do que é teu, para que não digas: ‘Enriqueci Abrão’.', 23, b'0', 14, 1),
	(350, 'nem um fio, nem uma correia de sandália, nem coisa alguma tomarei do que é teu, para que não digas: ‘Enriqueci Abrão’.', 24, b'0', 14, 1),
	(351, 'Depois desses acontecimentos, o Senhor falou a Abrão numa visão, dizendo: “Não temas, Abrão! Eu sou teu escudo protetor; tua recompensa será muito grande”.', 1, b'0', 15, 1),
	(352, 'Abrão respondeu: “Senhor Deus, que me haverás de dar? Eu me vou sem filhos, e o herdeiro de minha casa é Eliezer de Damasco”.', 2, b'0', 15, 1),
	(353, 'E acrescentou: “Como não me deste descendência, um escravo nascido em minha casa será meu herdeiro”.', 3, b'0', 15, 1),
	(354, 'Então veio-lhe a palavra do Senhor: “Não será esse o teu herdeiro; um dos teus descendentes será o herdeiro”.', 4, b'0', 15, 1),
	(355, 'E, conduzindo-o para fora, disse-lhe: “Olha para o céu e conta as  estrelas, se fores capaz!” E acrescentou: “Assim será tua descendência”.', 5, b'0', 15, 1),
	(356, 'Abrão teve fé no Senhor, que levou isso em conta de justiça.', 6, b'0', 15, 1),
	(357, 'E disse-lhe: “Eu sou o Senhor que te fez sair de Ur dos Caldeus, para te dar esta terra em posse”.', 7, b'0', 15, 1),
	(358, 'Abrão lhe perguntou: “Senhor Deus, como poderei saber que eu vou possuí-la?”', 8, b'0', 15, 1),
	(359, 'o Senhor lhe disse: “Traze-me uma novilha de três anos, uma cabra de três anos e um carneiro de três anos, além de uma rola e uma pombinha”.', 9, b'0', 15, 1),
	(360, 'Abrão trouxe tudo e cortou os animais pelo meio, menos as aves, dispondo as respectivas partes uma na frente da outra.', 10, b'0', 15, 1),
	(361, 'Aves de rapina se precipitavam sobre os cadáveres, mas Abrão as afugentava.', 11, b'0', 15, 1),
	(362, 'Quando o sol já ia descendo, um sono profundo caiu sobre Abrão, que foi tomado de grande e misterioso terror.', 12, b'0', 15, 1),
	(363, 'E o Senhor disse a Abrão: “Fica sabendo que tua descendência viverá como estrangeiros numa terra que não lhes pertence. Serão reduzidos à escravidão e oprimidos durante quatrocentos anos.', 13, b'0', 15, 1),
	(364, 'Mas eu farei o julgamento da nação que os escravizará, e depois sairão dali com grandes riquezas.', 14, b'0', 15, 1),
	(365, 'Quanto a ti, irás reunir-te em paz com teus pais e serás sepultado depois de uma velhice feliz.', 15, b'0', 15, 1),
	(366, 'Na quarta geração, eles voltarão para cá, pois a culpa dos amorreus ainda não se completou”.', 16, b'0', 15, 1),
	(367, 'Quando o sol se pôs e a escuridão chegou, apareceu um braseiro fumegante e uma tocha de fogo, que passaram por entre as partes dos animais esquartejados.', 17, b'0', 15, 1),
	(368, 'aquele dia, o Senhor fez aliança com Abrão, dizendo: “A teus descendentes darei esta terra, desde o rio do Egito até o grande rio, o Eufrates:', 18, b'0', 15, 1),
	(369, 'terra dos quenitas, dos quenezitas, dos cadmonitas,', 19, b'0', 15, 1),
	(370, 'dos heteus, dos fereseus, dos refaítas,', 20, b'0', 15, 1),
	(371, 'dos amorreus, dos cananeus, dos gergeseus e dos jebuseus”.', 21, b'0', 15, 1),
	(372, 'Sarai, mulher de Abrão, não lhe havia dado filhos. Mas ela tinha uma escrava egípcia chamada Agar.', 1, b'0', 16, 1),
	(373, 'Sarai disse a Abrão: “Já que o Senhor me fez estéril, une-te à minha escrava, para ver se, por meio dela, eu possa ter filhos”. Abrão atendeu ao pedido de Sarai.', 2, b'0', 16, 1),
	(374, '(Isso foi quando Abrão habitava na terra de Canaã fazia dez anos.) Sarai, esposa de Abrão, tomou a escrava egípcia, Agar, e deu-a como mulher a Abrão, seu marido.', 3, b'0', 16, 1),
	(375, 'Ele uniu-se a Agar e ela concebeu. Percebendo-se grávida, começou a olhar com desprezo para a sua Senhora.', 4, b'0', 16, 1),
	(376, 'Sarai disse a Abrão: “Tu és responsável pela injúria que estou sofrendo. Fui eu mesma que pus minha escrava em teus braços, mas ela, assim que ficou grávida, começou a desprezar-me. O Senhor seja juiz entre mim e ti”.', 5, b'0', 16, 1),
	(377, 'Abrão disse para Sarai: “Olha, a escrava é tua. Faze dela o que bem entenderes”. Então Sarai a maltratou tanto que ela fugiu.', 6, b'0', 16, 1),
	(378, 'Um anjo do Senhor encontrou-a junto à fonte do deserto, no caminho de Sur,', 7, b'0', 16, 1),
	(379, 'e disse-lhe: “Agar, escrava de Sarai, de onde vens e para onde vais?” Ela respondeu: “Estou fugindo de Sarai, minha Senhora”.', 8, b'0', 16, 1),
	(380, 'E o anjo do Senhor lhe disse: “Volta para tua Senhora e põe-te sob as suas ordens”.', 9, b'0', 16, 1),
	(381, 'E o anjo do Senhor acrescentou: “Multiplicarei a tua descendência de tal forma que ninguém a poderá contar”.', 10, b'0', 16, 1),
	(382, 'Por fim o anjo do Senhor disse: “Olha, estás grávida, darás à luz um filho e o chamarás Ismael, porque  na tua aflição o Senhor te escutou.', 11, b'0', 16, 1),
	(383, 'Ele será semelhante a um jumento selvagem, sua mão estará contra todos, e a mão de todos contra ele. Ele habitará separado de todos os seus irmãos”.', 12, b'0', 16, 1),
	(384, 'Ela invocou, então, o nome do Senhor que lhe havia falado: “Tu, o Deus que olha para mim”, pois ela disse: “Aqui cheguei a ver Aquele que olha para mim”.', 13, b'0', 16, 1),
	(385, 'Por isso aquele poço se chamou poço de Laai-Roí (isto é, “d’Aquele que vive e olha para mim”). Fica entre Cades e Barad.', 14, b'0', 16, 1),
	(386, 'Agar deu a Abrão um filho. Abrão pôs o nome de Ismael ao filho que Agar lhe deu.', 15, b'0', 16, 1),
	(387, 'Abrão tinha oitenta e seis anos quando Agar deu à luz Ismael.', 16, b'0', 16, 1),
	(388, 'Abrão tinha noventa e nove anos, quando o Senhor lhe apareceu e lhe disse: “Eu sou o Deus Poderoso. Anda na minha presença e sê íntegro.', 1, b'0', 17, 1),
	(389, 'Quero estabelecer contigo minha aliança e multiplicar sobremaneira a tua descendência”.', 2, b'0', 17, 1),
	(390, 'Abrão prostrou-se com o rosto em terra, e Deus lhe disse:', 3, b'0', 17, 1),
	(391, '“De minha parte, esta é a minha aliança contigo: tu serás pai de uma multidão de nações.', 4, b'0', 17, 1),
	(392, 'Já não te chamarás Abrão: Abraão será teu nome, porque farei de ti o pai de uma multidão de nações.', 5, b'0', 17, 1),
	(393, 'Eu te tornarei extremamente fecundo. De ti farei nações e terás reis como descendentes.', 6, b'0', 17, 1),
	(394, 'Estabeleço minha aliança entre mim e ti e teus descendentes para sempre, uma aliança eterna, para que eu seja Deus para ti e para teus descendentes.', 7, b'0', 17, 1),
	(395, 'A terra em que vives como estrangeiro, toda a terra de Canaã, eu a darei como propriedade perpétua a ti e a teus descendentes. Eu serei o Deus deles”.', 8, b'0', 17, 1),
	(396, 'Deus disse a Abraão: “De tua parte, guardarás a minha aliança, tu e tua descendência, para sempre.', 9, b'0', 17, 1),
	(397, 'Esta é a minha aliança que devereis observar, aliança entre mim e vós e tua descendência futura: todo varão entre vós deverá ser circuncidado.', 10, b'0', 17, 1),
	(398, 'Circuncidareis a carne do prepúcio: esse será o sinal da aliança entre mim e vós.', 11, b'0', 17, 1),
	(399, 'No oitavo dia do nascimento serão circuncidados todos os meninos, de cada geração, mesmo os filhos dos escravos nascidos em casa ou comprados de algum estrangeiro, e que não fazem parte de tua descendência.', 12, b'0', 17, 1),
	(400, 'Seja circuncidado tanto o escravo nascido em casa como o comprado a dinheiro. Assim trareis em vossa carne o sinal de minha aliança para sempre.', 13, b'0', 17, 1),
	(401, 'O incircunciso, porém, aquele que não circuncidar a carne de seu prepúcio, seja eliminado do povo, porque violou minha aliança”.', 14, b'0', 17, 1),
	(402, 'Deus disse ainda a Abraão: “Quanto à tua mulher, Sarai, já não a chamarás Sarai, mas Sara, Princesa.', 15, b'0', 17, 1),
	(403, 'Eu a abençoarei e também dela te darei um filho. Eu a abençoarei, e ela será mãe de nações; dela nascerão reis de povos”.', 16, b'0', 17, 1),
	(404, 'Abraão prostrou-se com o rosto em terra e começou a rir, dizendo consigo mesmo: “Será que um homem de cem anos vai ter um filho e que, aos noventa anos, Sara vai dar à luz?”', 17, b'0', 17, 1),
	(405, 'E, dirigindo-se a Deus, disse: “Quem dera que ao menos Ismael pudesse viver  em tua presença”.', 18, b'0', 17, 1),
	(406, 'Mas Deus respondeu: “Na verdade é Sara, tua mulher, que te dará um filho, a quem chamarás Isaac. Com ele estabelecerei minha aliança, uma aliança perpétua para sua descendência.', 19, b'0', 17, 1),
	(407, 'E também a respeito de Ismael atendo a teu pedido: eu o abençoarei e o tornarei fecundo e extremamente numeroso. Será pai de doze chefes, e dele farei uma grande nação.', 20, b'0', 17, 1),
	(408, 'Quanto à minha aliança, porém, eu a estabelecerei com Isaac, o filho que Sara te dará no ano que vem, por este tempo”.', 21, b'0', 17, 1),
	(409, 'Tendo acabado de falar com Abraão, Deus subiu e o deixou.', 22, b'0', 17, 1),
	(410, 'Abraão tomou o filho Ismael, bem como todos os escravos nascidos em sua casa ou comprados a dinheiro, todos os varões de sua casa, e circuncidou-lhes a carne do prepúcio naquele mesmo dia, como Deus lhe tinha ordenado.', 23, b'0', 17, 1),
	(411, 'Abraão tinha noventa e nove anos quando circuncidou a carne de seu prepúcio.', 24, b'0', 17, 1),
	(412, 'Seu filho Ismael tinha treze anos quando foi circuncidado. 26 Naquele mesmo dia foram circuncidados Abraão e seu filho Ismael,', 25, b'0', 17, 1),
	(413, 'Naquele mesmo dia foram circuncidados Abraão e seu filho Ismael,', 26, b'0', 17, 1),
	(414, 'juntamente com todos os homens de sua casa, tanto os que nela nasceram como os comprados de estrangeiro.', 27, b'0', 17, 1),
	(415, 'Depois o Senhor apareceu a Abraão junto ao carvalho de Mambré, quando ele estava sentado à entrada da tenda, no maior calor do dia.', 1, b'0', 18, 1),
	(416, 'Levantando os olhos, Abraão viu, perto dele, três homens de pé. Assim que os viu, saiu correndo ao seu encontro, prostrou-se por terra', 2, b'0', 18, 1),
	(417, 'e disse: “Meu Senhor, se mereci teu favor, peço-te, não prossigas viagem sem parar junto a mim, teu servo.', 3, b'0', 18, 1),
	(418, 'Mandarei trazer um pouco de água para lavar vossos pés e descansareis debaixo da árvore.', 4, b'0', 18, 1),
	(419, 'Farei servir um pouco de pão para refazerdes as forças, antes de continuar a viagem. Pois foi para isso mesmo que passastes junto a vosso servo”. Eles responderam: “Faze como disseste”.', 5, b'0', 18, 1),
	(420, 'Abraão entrou logo na tenda onde estava Sara e lhe disse: “Toma depressa três medidas da mais fina farinha, amassa uns pães e assa-os”.', 6, b'0', 18, 1),
	(421, 'Depois, Abraão correu até o rebanho, pegou um bezerro bem bonito e o entregou a um criado para que o preparasse sem demora.', 7, b'0', 18, 1),
	(422, 'a seguir foi buscar coalhada, leite e o bezerro assado e serviu tudo para eles. Enquanto comiam, Abraão ficou de pé, junto deles, debaixo da árvore.', 8, b'0', 18, 1),
	(423, 'Eles lhe perguntaram: “Onde está Sara, tua mulher?” – “Está na tenda”, respondeu ele.', 9, b'0', 18, 1),
	(424, 'Um deles disse: “No ano que vem, por este tempo, voltarei a ti, e Sara, tua mulher, já terá um filho”. Sara ouviu isso na entrada da tenda, atrás dele.', 10, b'0', 18, 1),
	(425, 'Ora, Abraão e Sara já eram velhos, muito avançados em idade, e ela já não tinha as regras das mulheres.', 11, b'0', 18, 1),
	(426, 'Por isso, Sara se pôs a rir em seu íntimo, dizendo: “Acabada como estou, terei ainda tal prazer, sendo meu marido já velho?”', 12, b'0', 18, 1),
	(427, 'E o Senhor disse a Abraão: “Por que Sara riu? Pois ela disse: ‘Acaso ainda terei um filho, sendo já velha?’', 13, b'0', 18, 1),
	(428, 'Existe alguma coisa impossível  para o Senhor? No ano que vem, por este tempo voltarei e Sara já terá um filho”.', 14, b'0', 18, 1),
	(429, 'Sara negou que tivesse rido: “Não ri”, disse ela, pois estava com medo. Mas ele insistiu: “Sim, tu riste”.', 15, b'0', 18, 1),
	(430, 'Os homens levantaram-se e voltaram os olhos em direção de Sodoma. Abraão os acompanhava para deles se despedir.', 16, b'0', 18, 1),
	(431, 'O Senhor disse consigo: “Acaso poderei ocultar a Abraão o que vou fazer?', 17, b'0', 18, 1),
	(432, 'Pois Abraão virá a ser uma nação grande e forte, e nele serão abençoadas todas as nações da terra.', 18, b'0', 18, 1),
	(433, 'De fato, eu o escolhi para que ensine seus filhos e sua casa a guardarem os caminhos do Senhor, praticando a justiça e o direito, a fim de que o Senhor cumpra a respeito de Abraão o que lhe prometeu”.', 19, b'0', 18, 1),
	(434, 'Então o Senhor disse: “O clamor contra Sodoma e Gomorra cresceu, e agravou-se muito o seu pecado.', 20, b'0', 18, 1),
	(435, 'Vou descer para verificar se as suas obras correspondem ou não ao clamor que chegou até mim”.', 21, b'0', 18, 1),
	(436, 'Partindo dali, os homens se dirigiram a Sodoma, enquanto Abraão ficou ali na presença do Senhor.', 22, b'0', 18, 1),
	(437, 'Então, aproximando-se, Abraão disse: “Vais realmente exterminar o justo com o ímpio?', 23, b'0', 18, 1),
	(438, 'Se houvesse cinqüenta justos na cidade, acaso os exterminarias? Não perdoarias o lugar por causa dos cinqüenta justos que ali vivem?', 24, b'0', 18, 1),
	(439, 'Longe de ti proceder assim, fazendo morrer o justo com o ímpio, como se o justo fosse igual ao ímpio! Longe de ti! O juiz de toda a terra não faria justiça?”', 25, b'0', 18, 1),
	(440, 'O Senhor respondeu: “Se eu encontrar em Sodoma cinqüenta justos, perdoarei por causa deles a cidade inteira”.', 26, b'0', 18, 1),
	(441, 'Abraão prosseguiu e disse: “Sou bem atrevido em falar a meu Senhor, eu que sou pó e cinza.', 27, b'0', 18, 1),
	(442, 'Se dos cinqüenta justos faltarem cinco, destruirás por causa dos cinco a cidade inteira?” O Senhor respondeu-lhe: “Não a destruirei se achar ali quarenta e cinco justos”.', 28, b'0', 18, 1),
	(443, 'Insistiu ainda Abraão e disse: “E se forem só quarenta?” Ele respondeu: “Por causa dos quarenta, não a destruirei”.', 29, b'0', 18, 1),
	(444, 'Abraão tornou a insistir: “Não se irrite o meu Senhor, se ainda falo. E se não houver mais do que trinta justos?” Ele respondeu: “Também não o farei se encontrar somente trinta”.', 30, b'0', 18, 1),
	(445, 'Tornou Abraão a insistir: “Já que me atrevi a falar a meu Senhor: e se houver apenas vinte justos?” Ele respondeu: “Não a destruirei, por causa dos vinte”.', 31, b'0', 18, 1),
	(446, 'E Abraão disse: “Que meu Senhor não se irrite, se falar só mais uma vez: e se houver apenas dez?” E ele respondeu: “Por causa dos dez, não a destruirei”.', 32, b'0', 18, 1),
	(447, 'Tendo acabado de falar a Abraão, o Senhor partiu, e Abraão voltou para sua tenda.', 33, b'0', 18, 1),
	(448, 'De tarde, os dois anjos chegaram a Sodoma. Ló estava sentado à porta da cidade. Ao vê-los, Ló se levantou, saiu-lhes ao encontro, prostrou-se com o rosto em terra', 1, b'0', 19, 1),
	(449, 'e disse: “Meus Senhores, rogo-vos que venhais à casa de vosso servo para lavardes os pés e pernoitardes. Amanhã cedo, ao despertar, seguireis vosso caminho”. Mas eles responderam: “Não, nós vamos passar a noite na praça”.', 2, b'0', 19, 1),
	(450, 'Ló insistiu muito com eles, de modo que foram com ele para casa, onde lhes preparou um jantar e alguns  pães, e eles comeram.', 3, b'0', 19, 1),
	(451, 'Ainda não foram dormir, quando os homens da cidade, os habitantes de Sodoma, cercaram a casa; moços e velhos, vieram todos sem exceção.', 4, b'0', 19, 1),
	(452, 'Chamaram Ló e lhe disseram: “Onde estão os homens que vieram à tua casa esta noite? Traze-os cá até nós, para termos relações com eles”.', 5, b'0', 19, 1),
	(453, 'Ló saiu à porta, fechou-a atrás de si', 6, b'0', 19, 1),
	(454, 'e lhes disse: “Por favor, meus irmãos, não façais semelhante maldade.', 7, b'0', 19, 1),
	(455, 'Vede, tenho duas filhas ainda virgens. Vou trazê-las para fora. Podeis fazer com elas o que bem entenderdes; mas nada façais a estes homens, pois vieram acolher-se sob o meu teto”.', 8, b'0', 19, 1),
	(456, 'Eles lhe disseram: “Fora daqui! Este indivíduo veio como imigrante e pretende ser juiz? Pois bem. Vamos te fazer algo pior do que a eles”. Avançaram violentamente sobre Ló e já estavam para arrombar a porta.', 9, b'0', 19, 1),
	(457, 'Mas os hóspedes intervieram, puxaram Ló para dentro de casa e fecharam a porta.', 10, b'0', 19, 1),
	(458, 'Feriram de cegueira os homens que estavam fora, desde o menor até o maior, de modo que não podiam mais encontrar a porta.', 11, b'0', 19, 1),
	(459, 'Os dois homens disseram a Ló: “Se tens aqui ainda algum parente, genro, filho ou filha, tudo o que tens na cidade, tira-o daqui.', 12, b'0', 19, 1),
	(460, 'Vamos destruir este lugar, pois grande é o clamor contra ele diante do Senhor. Ele nos enviou para destruir a cidade.', 13, b'0', 19, 1),
	(461, 'Ló foi então falar com os genros que estavam para casar-se com suas filhas e lhes disse: “Levantai-vos e saí deste lugar, porque o Senhor vai destruir a cidade”. Mas os genros julgaram que estivesse brincando.', 14, b'0', 19, 1),
	(462, 'Ao raiar da aurora, os anjos insistiram com Ló, dizendo: “Levanta-te, toma tua mulher e as duas filhas que tens, para não morreres também tu por causa do castigo da cidade”.', 15, b'0', 19, 1),
	(463, 'Como ele hesitasse, os homens tomaram-no pela mão, a ele, à mulher e às duas filhas – pois o Senhor tinha compaixão dele –, fizeram-nos sair e deixaram-nos fora da cidade.', 16, b'0', 19, 1),
	(464, 'Uma vez fora, disseram: “Trata de salvar tua vida. Não olhes para trás, nem pares em parte alguma desta região, mas foge para a montanha, se não quiseres morrer”.', 17, b'0', 19, 1),
	(465, 'Ló respondeu: “Não, meu Senhor, eu te peço!', 18, b'0', 19, 1),
	(466, 'O teu servo encontrou teu favor, e foi grande tua bondade comigo, conservando-me a vida. Mas receio não poder salvar-me na montanha, antes que a calamidade me atinja e eu morra.v', 19, b'0', 19, 1),
	(467, 'Eis aqui perto uma cidade onde poderei refugiar-me. É um povoado. Permite que me salve ali. É bem pequena, mas salvaria minha vida”.', 20, b'0', 19, 1),
	(468, 'E ele lhe disse: “Pois bem, concedo-te também este favor: não destruirei a cidade de que falas.', 21, b'0', 19, 1),
	(469, 'Refugia-te lá depressa, pois nada posso fazer enquanto não tiveres entrado na cidade”. Por isso foi dado àquela cidade o nome de Segor, Pequena.', 22, b'0', 19, 1),
	(470, 'O sol estava nascendo quando Ló entrou em Segor.', 23, b'0', 19, 1),
	(471, 'O Senhor fez então chover do céu enxofre e fogo sobre Sodoma e Gomorra.', 24, b'0', 19, 1),
	(472, 'Destruiu as cidades e toda a região, junto com os habitantes das cidades e até a vegetação do solo.', 25, b'0', 19, 1),
	(473, 'Ora, a mulher de Ló olhou para trás e tornou-se uma estátua de sal.', 26, b'0', 19, 1),
	(474, 'Abraão levantou-se bem cedo e foi até o lugar onde antes tinha estado com o Senhor.', 27, b'0', 19, 1),
	(475, 'Deixando pairar seu olhar na direção de Sodoma e Gomorra e da região toda, viu levantar-se do chão uma densa fumaça, como a fumaça de uma fornalha.', 28, b'0', 19, 1),
	(476, 'Mas Deus, ao destruir as cidades da região, lembrou-se de Abraão e salvou Ló da catástrofe que arrasou as cidades onde Ló havia morado. As filhas de Ló. Origem de Moab e Amon', 29, b'0', 19, 1),
	(477, 'Ló subiu de Segor e foi morar nas montanhas com as duas filhas, pois tinha medo de ficar em Segor. Instalou-se numa gruta com as duas filhas.', 30, b'0', 19, 1),
	(478, 'A mais velha disse à mais nova: “Nosso pai já está velho e aqui não há homens  com quem possamos casar-nos, como faz todo mundo.', 31, b'0', 19, 1),
	(479, 'Vamos embriagar nosso pai com vinho e dormir com ele, para ter filhos dele”.', 32, b'0', 19, 1),
	(480, 'Embriagaram o pai, naquela noite, e a mais velha foi dormir com ele sem que ele nada percebesse, nem quando ela se deitou nem quando se levantou.', 33, b'0', 19, 1),
	(481, 'No dia seguinte a mais velha disse à mais nova: “Ontem à noite dormi com o pai. Vamos embriagá-lo também esta noite, e tu vais dormir com ele para gerar descendência de nosso pai”.', 34, b'0', 19, 1),
	(482, 'Também naquela noite embriagaram o pai, e a mais nova dormiu com ele. Ele, porém, nada percebeu, nem quando ela se deitou, nem quando se levantou.', 35, b'0', 19, 1),
	(483, 'Assim as duas filhas de Ló ficaram grávidas do próprio pai.', 36, b'0', 19, 1),
	(484, 'A mais velha deu à luz um filho a quem chamou Moab, que é o antepassado dos atuais moabitas.', 37, b'0', 19, 1),
	(485, 'Também a mais nova deu à luz um filho a quem chamou Ben-Ami, que é o antepassado dos atuais amonitas.', 38, b'0', 19, 1),
	(487, 'Abraão partiu dali para a região do deserto do Negueb e habitou entre Cades e Sur, vivendo como migrante em Gerara.', 1, b'0', 20, 1),
	(488, 'Abraão dizia de Sara, sua mulher: “Ela é minha irmã”. Então Abimelec, rei de Gerara, mandou que lhe trouxessem Sara.', 2, b'0', 20, 1),
	(489, 'Mas, durante a noite, Deus apareceu a Abimelec num sonho e lhe disse: “Vais morrer por causa da mulher que tomaste, pois ela tem marido”.', 3, b'0', 20, 1),
	(490, 'Abimelec, que não se havia aproximado dela, respondeu: “Meu Senhor, matarias mesmo gente inocente?', 4, b'0', 20, 1),
	(491, 'Acaso não foi ele que me disse: ‘Ela é minha irmã’? E não foi ela que me disse: ‘Ele é meu irmão’? Agi com consciência reta e mãos inocentes”.', 5, b'0', 20, 1),
	(492, 'E Deus lhe disse no sonho: “Bem sei que fizeste isto de boa fé. Por isso te impedi de pecares contra mim e não consenti que a tocasses.', 6, b'0', 20, 1),
	(493, 'Portanto devolve a mulher a seu marido, pois sendo ele um profeta, rogará por ti e viverás. Mas se não a devolveres, fica sabendo que deverás morrer, tu com todos os teus”.', 7, b'0', 20, 1),
	(494, 'De manhã, ao despertar, Abimelec chamou todos os seus servidores e contou tudo o que acontecera, e os homens ficaram com muito medo.', 8, b'0', 20, 1),
	(495, 'Depois chamou Abraão e lhe disse: “Que foi que nos fizeste? E que fiz de errado contra ti para atraíres sobre mim e meu reino um tão grande pecado? Fizeste comigo o que não se deve fazer”.', 9, b'0', 20, 1),
	(496, 'E Abimelec perguntou a Abraão: “O que pretendias ao fazer isso?”', 10, b'0', 20, 1),
	(497, 'Abraão lhe respondeu: “Eu pensei comigo: ‘Certamente não há temor de Deus neste lugar e vão matar-me por causa de minha mulher’.', 11, b'0', 20, 1),
	(498, 'Além do mais, ela é realmente minha irmã, filha de meu pai mas não de minha mãe, e se tornou minha mulher.', 12, b'0', 20, 1),
	(499, 'Desde que Deus me fez emigrar da casa de meu pai, eu pedi a ela: ‘Faze-me o favor de dizer em todos os lugares aonde chegarmos que sou teu irmão’”.', 13, b'0', 20, 1),
	(500, 'Abimelec tomou então ovelhas e bois, escravos e escravas e os deu a Abraão. Devolveu a Abraão a sua mulher Sara', 14, b'0', 20, 1),
	(501, 'e lhe disse: “Tens minha terra à tua disposição; mora onde bem entenderes”.', 15, b'0', 20, 1),
	(502, 'E para Sara disse: “Olha, dei a teu irmão mil moedas de prata. Sirvam-te elas como reparação moral diante de toda gente, e assim estarás justificada”.', 16, b'0', 20, 1),
	(503, 'Abraão intercedeu por Abimelec, e Deus curou Abimelec, sua mulher e suas servas,  para poderem ter filhos.', 17, b'0', 20, 1),
	(504, '(Pois o Senhor tinha tornado estéreis todas as mulheres na casa de Abimelec, por causa de Sara, mulher de Abraão.)', 18, b'0', 20, 1),
	(505, 'O Senhor deu atenção a Sara, como havia prometido, e cumpriu o que lhe dissera.', 1, b'0', 21, 1),
	(506, 'Sara concebeu e deu a Abraão um filho na velhice, no tempo que Deus lhe havia predito.', 2, b'0', 21, 1),
	(507, 'Abraão deu o nome de Isaac ao filho que lhe nascera de Sara.', 3, b'0', 21, 1),
	(508, 'Abraão circuncidou o filho Isaac no oitavo dia, como Deus lhe havia ordenado.', 4, b'0', 21, 1),
	(509, 'Abraão tinha cem anos quando lhe nasceu o filho Isaac.', 5, b'0', 21, 1),
	(510, 'E Sara disse: “Deus me fez sorrir, e todos os que souberem vão sorrir comigo”.', 6, b'0', 21, 1),
	(511, 'E acrescentou: “Quem teria dito a Abraão que Sara haveria de amamentar filhos? Pois eu lhe dei um filho na velhice”.', 7, b'0', 21, 1),
	(512, 'Entretanto, o menino cresceu e foi desmamado. Abraão fez um grande banquete no dia em que Isaac foi desmamado.', 8, b'0', 21, 1),
	(513, 'Mas Sara viu o filho que a egípcia Agar dera a Abraão brincando com seu filho Isaac.', 9, b'0', 21, 1),
	(514, 'E disse a Abraão: “Manda embora essa escrava e seu filho, pois o filho de uma escrava não pode ser herdeiro com o meu filho Isaac”.', 10, b'0', 21, 1),
	(515, 'Abraão ficou muito desgostoso com isso, por se tratar de um filho seu.', 11, b'0', 21, 1),
	(516, 'Mas Deus lhe disse: “Não te aflijas a propósito do menino e da escrava. Atende a tudo o que Sara te pedir, pois é por Isaac que terás uma descendência que levará teu nome.', 12, b'0', 21, 1),
	(517, 'Mas também do filho da escrava farei uma nação, por ser descendência tua”.', 13, b'0', 21, 1),
	(518, 'Abraão levantou-se de manhã, tomou pão e um odre de água, que deu a Agar e lhe pôs aos ombros. Depois entregou-lhe o menino e despediu-a. Ela foi-se embora e andou vagueando pelo deserto de Bersabéia.', 14, b'0', 21, 1),
	(519, 'Quando acabou a água do odre, largou o menino debaixo de um arbusto', 15, b'0', 21, 1),
	(520, 'e foi sentar-se em frente dele, à distância de um tiro de arco. Pois dizia consigo: “Não quero ver o menino morrer”. Assim ficou sentada em frente do menino e chorava em alta voz.', 16, b'0', 21, 1),
	(521, 'Deus ouviu o choro do menino e, de lá dos céus, o anjo de Deus chamou Agar, dizendo: “Que tens, Agar? Não tenhas medo, pois Deus ouviu o choro do menino do lugar onde está.', 17, b'0', 21, 1),
	(522, 'Levanta-te, toma o menino e segura-o pela mão, porque farei dele uma grande nação”.', 18, b'0', 21, 1),
	(523, 'Deus abriu os olhos de Agar, e ela viu um poço d’água. Encheu então o odre de água e deu  de beber ao menino.', 19, b'0', 21, 1),
	(524, 'Deus estava com o menino, que cresceu e ficou morando no deserto, tornando- se um jovem arqueiro.', 20, b'0', 21, 1),
	(525, 'Morou no deserto de Farã, e sua mãe escolheu para ele uma mulher egípcia.', 21, b'0', 21, 1),
	(526, 'Por aquele tempo, Abimelec e Ficol, chefe do exército, vieram dizer a Abraão: “Deus está contigo em tudo o que fazes.', 22, b'0', 21, 1),
	(527, 'Portanto, jura-me por Deus, aqui mesmo, que não me enganarás, nem a meus filhos, nem a meus descendentes, e que terás comigo e com a terra na qual estás andando a mesma  lealdade que eu tive contigo”.', 23, b'0', 21, 1),
	(528, 'E Abraão disse: “Eu juro”.', 24, b'0', 21, 1),
	(529, '25 Ora, Abraão queixou-se a Abimelec por causa de um poço de água de que se haviam apoderado os servos de Abimelec.', 25, b'0', 21, 1),
	(530, '“Não sei quem fez isso”, respondeu Abimelec. “Como não me informaste de nada, só hoje fiquei sabendo”.', 26, b'0', 21, 1),
	(531, 'Abraão tomou, então, ovelhas e bois e os deu a Abimelec, e assim firmaram aliança entre si.', 27, b'0', 21, 1),
	(532, 'Abraão separou sete ovelhinhas do rebanho,', 28, b'0', 21, 1),
	(533, 'e Abimelec perguntou-lhe: “Para que servem essas sete ovelhinhas que separaste?”', 29, b'0', 21, 1),
	(534, 'Abraão respondeu: “Para que as recebas de minha mão e elas me sirvam de prova de que eu cavei este poço”.', 30, b'0', 21, 1),
	(535, 'Por isso o lugar foi chamado Bersabéia, porque ali os dois fizeram juramento.', 31, b'0', 21, 1),
	(536, 'Tendo feito aliança em Bersabéia, Abimelec e Ficol, chefe do exército, partiram de volta à terra dos filisteus.', 32, b'0', 21, 1),
	(537, 'Abraão plantou em Bersabéia um tamarindeiro e ali invocou o nome do Senhor, o Deus Eterno.', 33, b'0', 21, 1),
	(538, 'Abraão residiu muito tempo na terra dos filisteus.', 34, b'0', 21, 1),
	(539, 'Depois desses acontecimentos, Deus pôs Abraão à prova. Chamando-o, disse: “Abraão!” E ele respondeu: “Aqui estou”.', 1, b'0', 22, 1),
	(540, 'E Deus disse: “Toma teu filho único, Isaac, a quem tanto amas, dirige-te à terra de Moriá e oferece-o ali em holocausto sobre o monte que eu te indicar”.', 2, b'0', 22, 1),
	(541, 'Abraão levantou-se bem cedo, encilhou o jumento, tomou consigo dois criados e o seu filho Isaac. Depois de ter rachado lenha para o holocausto, pôs-se a caminho para o lugar que Deus lhe havia ordenado.', 3, b'0', 22, 1),
	(542, 'No terceiro dia, Abraão levantou os olhos e viu de longe o lugar.', 4, b'0', 22, 1),
	(543, 'Disse então aos criados: “Esperai aqui com o jumento, enquanto eu e o menino vamos até lá. Depois de adorarmos a Deus, voltaremos a vós”.', 5, b'0', 22, 1),
	(544, 'Abraão tomou a lenha para o holocausto e a pôs às costas do seu filho Isaac, enquanto ele levava o fogo e a faca. Os dois continuaram caminhando juntos.', 6, b'0', 22, 1),
	(545, 'Isaac falou para seu pai Abraão e disse: “Pai!” – “O que queres, meu filho?” respondeu ele. O menino disse: “Temos o fogo e a lenha, mas onde está o cordeiro para o holocausto?”', 7, b'0', 22, 1),
	(546, 'Abraão respondeu: “Deus providenciará o cordeiro para o holocausto, meu filho”. Os dois continuaram caminhando juntos.', 8, b'0', 22, 1),
	(547, 'Quando chegaram ao lugar indicado por Deus, Abraão ergueu ali o altar, colocou a lenha em cima, amarrou o filho e o pôs sobre a lenha do altar.', 9, b'0', 22, 1),
	(548, 'Depois estendeu a mão e tomou a faca a fim de matar o filho para o sacrifício.', 10, b'0', 22, 1),
	(549, 'Mas o anjo do Senhor gritou-lhe do céu: “Abraão! Abraão!” Ele respondeu: “Aqui estou!”', 11, b'0', 22, 1),
	(550, 'E o anjo disse: “Não estendas a mão contra o menino e não lhe faças mal algum. Agora sei que temes a Deus, pois não me recusaste teu único filho”.', 12, b'0', 22, 1),
	(551, 'Abraão ergueu os olhos e viu um carneiro preso pelos chifres num espinheiro. Pegou o carneiro e ofereceu-o em holocausto no lugar do seu filho.', 13, b'0', 22, 1),
	(552, 'Abraão passou a chamar aquele lugar “O Senhor providenciará”. Hoje se diz: “No monte em que o Senhor aparece”.', 14, b'0', 22, 1),
	(553, 'O anjo do Senhor chamou Abraão pela segunda vez, do céu', 15, b'0', 22, 1),
	(554, 'e lhe falou: Juro por mim mesmo – oráculo do Senhor – já que agiste deste modo e não me recusaste teu único filho,', 16, b'0', 22, 1),
	(555, 'eu te abençoarei e tornarei tua descendência tão numerosa como as estrelas do céu e como as areias da praia  do mar. Teus descendentes conquistarão as cidades dos inimigos.', 17, b'0', 22, 1),
	(556, 'Por tua descendência serão abençoadas todas as nações da terra, porque me obedeceste”.', 18, b'0', 22, 1),
	(557, 'Abraão retornou até aos criados e, juntos, puseram-se a caminho de Bersabéia, onde Abraão passou a residir.', 19, b'0', 22, 1),
	(558, 'Depois desses acontecimentos, Abraão recebeu uma notícia nestes termos: “Também Melca deu filhos a Nacor, teu irmão.', 20, b'0', 22, 1),
	(559, 'Hus é o primogênito e os irmãos são Buz, Camuel, pai dos arameus, 22 Cased, Azau, Feldas,Jedlaf e Batuel”.', 21, b'0', 22, 1),
	(560, 'Batuel foi o pai de Rebeca. São esses os oito filhos que Melca deu a Nacor, irmão de Abraão.', 22, b'0', 22, 1),
	(561, 'Também sua concubina, de nome Roma, deu à luz Tabé, Gaam, Taás e Maaca.', 23, b'0', 22, 1),
	(562, 'Sara viveu cento e vinte e sete anos', 1, b'0', 23, 1),
	(563, 'e morreu em Cariat Arbe, que é Hebron, na terra de Canaã. Abraão veio fazerluto por Sara e chorá-la.', 2, b'0', 23, 1),
	(564, 'Depois levantou-se de junto da falecida e assim falou aos heteus:', 3, b'0', 23, 1),
	(565, '“Sou estrangeiro e hóspede no vosso meio. Cedei-me em propriedade entre vós um lugar de sepultura onde possa sepultar minha esposa que morreu”.', 4, b'0', 23, 1),
	(566, 'Os heteus responderam a Abraão:', 5, b'0', 23, 1),
	(567, '“Por favor, escuta-nos, Senhor! Tu, que és um chefe poderoso entre nós, sepulta a falecida no melhor dos nossos sepulcros. Ninguém de nós te negará uma sepultura para tua falecida”.', 6, b'0', 23, 1),
	(568, 'Abraão levantou-se, prostrou-se diante dos donos daquela terra, os heteus,', 7, b'0', 23, 1),
	(569, 'e disse-lhes: “Se deveras quereis que sepulte minha falecida esposa, atendei-me e pedi por mim junto a Efron filho de Seor,', 8, b'0', 23, 1),
	(570, 'para que me ceda a gruta de Macpela, que lhe pertence e que está nos fundos de seu terreno. Que a venda a mim pelo seu preço, como propriedade funerária em vosso meio”.', 9, b'0', 23, 1),
	(571, 'Efron estava sentado entre os heteus. Efron, o heteu, respondeu a Abraão em presença dos heteus e de todos os ue vieram até a porta da cidade:', 10, b'0', 23, 1),
	(572, '“Por favor, escuta-me, Senhor! Eu te dou de presente o campo, com a gruta que nele se encontra. Dou- o na presença de meus compatriotas. Sepulta a tua falecida”.', 11, b'0', 23, 1),
	(573, 'Abraão tornou a prostrar-se diante dos  donos daquela terra', 12, b'0', 23, 1),
	(574, 'e assim falou a Efron, para que todos ouvissem: “Faze o favor de escutar-me: eu te pagarei o preço do terreno.Aceita-o para que possa sepultar ali minha falecida”.', 13, b'0', 23, 1),
	(575, 'Efron respondeu a Abraão:', 14, b'0', 23, 1),
	(576, '“Escuta-me, Senhor! O que é para mim e para ti um terreno no valor de quatrocentos siclos (quatro quilos) de prata, para sepultar tua falecida?”', 15, b'0', 23, 1),
	(577, 'Abraão concordou com Efron e pesou diante dos heteus a prata que este havia pedido: quatrocentos siclos de prata, segundo seu peso no mercado.', 16, b'0', 23, 1),
	(578, 'E assim o campo de Efron em Macpela, em frente de Mambré, tanto o terreno como a gruta que se encontra nele e todas as árvores dentro dos limites do terreno,', 17, b'0', 23, 1),
	(579, 'tornaram-se propriedade de Abraão, na presença dos heteus e dos que vieram até a porta da cidade.', 18, b'0', 23, 1),
	(580, 'Depois, Abraão sepultou sua mulher Sara na gruta do campo de Macpela, em frente de Mambré, que é Hebron,  na terra de Canaã.', 19, b'0', 23, 1),
	(581, 'Assim o terreno com a gruta passaram dos heteus para Abraão, como propriedade funerária.', 20, b'0', 23, 1),
	(582, 'Abraão já era velho, avançado em anos, e o Senhor o havia abençoado em tudo.', 1, b'0', 24, 1),
	(583, 'Abraão disse ao mais antigo dos criados da casa, administrador de todos os seus bens: “Põe a mão debaixo de minha coxa', 2, b'0', 24, 1),
	(584, 'e jura-me pelo Senhor, Deus do céu e da terra, que não escolherás para meu filho uma mulher entre as filhas dos cananeus, no meio dos quais eu moro.', 3, b'0', 24, 1),
	(585, 'Pelo contrário, irás à minha terra natal buscar entre os meus parentes uma mulher para meu filho Isaac”.', 4, b'0', 24, 1),
	(586, 'E o criado lhe disse: “E se a mulher não quiser vir comigo para cá, deverei levar teu filho para a terra donde saíste?”', 5, b'0', 24, 1),
	(587, 'Abraão respondeu: “Guarda-te de levar meu filho de volta para lá.', 6, b'0', 24, 1),
	(588, 'O Senhor, Deus do céu, tirou-me da casa de meu pai e de minha terra natal e me jurou: ‘À tua descendência darei esta terra’. Ele mesmo enviará seu anjo diante de ti e tu vais trazer de lá uma mulher para meu filho.', 7, b'0', 24, 1),
	(589, 'Ora, se a mulher não quiser vir contigo, ficarás livre deste juramento. Mas de maneira alguma levarás meu filho de volta para lá”.', 8, b'0', 24, 1),
	(590, 'Então o criado pôs a mão sob a coxa de seu Senhor Abraão e prestou-lhe juramento nos termos propostos.', 9, b'0', 24, 1),
	(591, 'O criado tomou dez camelos de seu Senhor e se pôs a caminho, levando consigo tudo o que seu Senhor tinha de melhor, e dirigiu-se à Mesopotâmia, à cidade de Nacor.', 10, b'0', 24, 1),
	(592, 'Fez descansar os camelos fora da cidade, junto a um poço d’água, já de tarde, à hora em que as mulheres costumam ir apanhar água.', 11, b'0', 24, 1),
	(593, 'E disse: “Senhor, Deus de meu Senhor Abraão, que o dia de hoje me seja favorável. Mostra-te benigno com meu Senhor Abraão.', 12, b'0', 24, 1),
	(594, 'Vou ficar junto à fonte, enquanto as moças da cidade vêm buscar água.', 13, b'0', 24, 1),
	(595, 'Para eu saber que te mostras benigno com meu Senhor, vamos combinar o seguinte: se eu disser a determinada moça: ‘Inclina o cântaro, por favor, para eu beber’ e ela responder: ‘Bebe, que vou dar de beber também aos camelos’, será esta que destinas a teu servo Isaac”.', 14, b'0', 24, 1),
	(596, 'Ora, ainda não tinha acabado de falar, quando chegou Rebeca filha de Batuel, filho de Melca, a mulher de Nacor, irmão de Abraão. Ela trazia o cântaro no ombro.', 15, b'0', 24, 1),
	(597, 'A jovem era muito bela, virgem, e nenhum homem a tinha conhecido. Desceu à fonte, encheu o cântaro e tornou a subir.', 16, b'0', 24, 1),
	(598, 'O criado lhe correu ao encontro e disse: “Por favor, dá-me de beber um pouco de água do teu cântaro”.', 17, b'0', 24, 1),
	(599, '“Bebe, meu Senhor”, respondeu ela, e logo abaixou o cântaro, apoiando-o sobre a mão, para dar-lhe de', 18, b'0', 24, 1),
	(600, 'Depois de lhe ter dado de beber, ela disse: “Vou tirar água também para os camelos, a fim de beberem à vontade”.', 19, b'0', 24, 1),
	(601, 'Esvaziou depressa o cântaro no bebedouro, correu de novo ao poço para apanhar mais e tirou água para todos os camelos.', 20, b'0', 24, 1),
	(602, 'O homem a observava em silêncio e se perguntava se o Senhor tinha tornado bem sucedida sua viagem ou não.', 21, b'0', 24, 1),
	(603, 'Quando os camelos acabaram de beber, o homem pegou para ela um anel de cinco gramas de ouro, dois braceletes de cem gramas de ouro', 22, b'0', 24, 1),
	(604, 'e lhe perguntou: “De quem és filha? Dize-me, por favor: não haveria em casa de teu  pai um lugar para eu pernoitar?”', 23, b'0', 24, 1),
	(605, 'Ela respondeu: “Sou filha de Batuel, o filho que Melca deu a Nacor”.', 24, b'0', 24, 1),
	(606, 'E acrescentou: “Em nossa casa há palha em abundância e lugar para pernoitar”.', 25, b'0', 24, 1),
	(607, 'Então o homem ajoelhou-se e adorou o Senhor,', 26, b'0', 24, 1),
	(608, 'dizendo: “Bendito seja o Senhor, Deus de meu Senhor Abraão, que não negou sua amizade e fidelidade a meu Senhor, conduzindo-me à casa do irmão de meu Senhor”.', 27, b'0', 24, 1),
	(609, 'A jovem correu para casa e contou à mãe o que acontecera.', 28, b'0', 24, 1),
	(610, 'Rebeca tinha um irmão, de nome Labão. Ele saiu correndo até à fonte em busca do homem,', 29, b'0', 24, 1),
	(611, 'pois tinha visto o anel e os braceletes na mão da irmã e tinha ouvido quando ela dizia: “Assim me falou o homem”. Encontrou o homem com os camelos, parado junto à fonte', 30, b'0', 24, 1),
	(612, 'e disse-lhe: “Vem, abençoado do Senhor! Por que continuas parado aí fora? Já preparei a casa e um lugar para os camelos”.', 31, b'0', 24, 1),
	(613, 'Enquanto o homem entrou em casa, Labão descarregou os camelos e deulhes palha e forragem; e ao homem, bem como a seus companheiros, deu-lhes água para lavarem os pés.', 32, b'0', 24, 1),
	(614, 'Depois serviram-lhe comida, mas o homem disse: “Não comerei enquanto não falar de meu assunto”. Labão respondeu: “Fala, pois”.', 33, b'0', 24, 1),
	(615, 'Então ele falou: “Sou um criado de Abraão.', 34, b'0', 24, 1),
	(616, 'O Senhor tem abençoado muito meu Senhor, e ele se tornou rico. Deu-lhe ovelhas e bois, prata e ouro, escravos e escravas, camelos e jumentos.', 35, b'0', 24, 1),
	(617, 'Sara, a mulher de meu Senhor, depois de velha, deu-lhe ainda um filho, que recebeu todos os seus bens.', 36, b'0', 24, 1),
	(618, 'O meu Senhor fez-me jurar e ordenou: ‘Não escolhas para o meu filho uma mulher entre as filhas dos cananeus, em cuja terra eu moro.', 37, b'0', 24, 1),
	(619, 'Ao contrário, vai à casa de meu pai e dos meus parentes, para trazer uma mulher para meu filho’.', 38, b'0', 24, 1),
	(620, 'Eu disse a meu Senhor: ‘E se a mulher não quiser vir comigo?’', 39, b'0', 24, 1),
	(621, 'Ele me respondeu: ‘O Senhor, em cuja presença eu ando, enviará contigo seu anjo e dará feliz êxito à viagem: tu escolherás dentre meus parentes e da casa de meu pai uma mulher para meu filho.', 40, b'0', 24, 1),
	(622, 'Ficarás livre do juramento quando fores até os meus parentes; se eles te negarem a mulher, estarás livre do juramento’.', 41, b'0', 24, 1),
	(623, 'o chegar hoje à fonte, eu disse: ‘Senhor, Deus de meu Senhor Abraão, peço-te que eu tenha êxito na viagem que estou fazendo.', 42, b'0', 24, 1),
	(624, 'Vou colocar-me junto à fonte de água. A jovem que vier buscar água e a quem eu disser: Dá-me de beber, por favor, um pouco de água de teu cântaro,', 43, b'0', 24, 1),
	(625, 'e que responder: Bebe, não somente tu, mas tirarei água também para os camelos, essa deverá ser a mulher que o Senhor destinou para o filho do meu Senhor’.', 44, b'0', 24, 1),
	(626, 'Não havia ainda acabado de dizê-lo para mim mesmo, quando vi Rebeca vindo com o cântaro ao ombro. Ela desceu à fonte e tirou água. Então eu lhe falei: ‘Dá-me de beber, por favor’.', 45, b'0', 24, 1),
	(627, 'Ela logo baixou o cântaro de cima do ombro e disse: ‘Bebe, que eu vou dar de beber aos camelos’. Eu bebi e ela deu de beber também aos camelos.', 46, b'0', 24, 1),
	(628, 'Perguntei-lhe: ‘De quem és filha?’ Ela respondeu: ‘Sou filha de Batuel filho de Nacor, nascido de Melca’. Então pus-lhe o anel no nariz e os braceletes nas mãos,', 47, b'0', 24, 1),
	(629, 'ajoelhei-me para adorar o Senhor, bendizendo o Senhor, Deus de meu Senhor Abraão, que me tinha guiado pelo caminho certo a fim de escolher a filha de seu irmão para esposa do seu filho.', 48, b'0', 24, 1),
	(630, 'Portanto, dizei-me se estais dispostos ou não a demonstrar amizade e fidelidade a meu Senhor, a fim de que eu saiba o que fazer”.', 49, b'0', 24, 1),
	(631, 'Labão e Batuel responderam: “Isso vem do Senhor; nós não podemos dizer-te nada que não seja de sua vontade.', 50, b'0', 24, 1),
	(632, 'Aí tens Rebeca, leva-a contigo, para que seja a esposa do filho de teu amo, conforme a  palavra do Senhor”.', 51, b'0', 24, 1),
	(633, 'Quando o criado de Abraão ouviu estas palavras, prostrou-se em terra diante do Senhor.', 52, b'0', 24, 1),
	(634, 'Tirou da bagagem os objetos de prata e ouro e os vestidos e os deu a Rebeca. Ofereceu também presentes ao irmão e à mãe.', 53, b'0', 24, 1),
	(635, 'Então com os companheiros se pôs a comer e beber e foi dormir. Pela manhã, ao levantar, disse o criado: “Deixai-me voltar para junto de meu Senhor”.', 54, b'0', 24, 1),
	(636, 'O irmão e a mãe de Rebeca disseram: “Fique a jovem conosco ainda uns dez dias, depois partirá”.', 55, b'0', 24, 1),
	(637, 'Ele respondeu: “Não retardeis minha volta, já que o Senhor deu feliz êxito à minha viagem. Deixai-me partir para que volte a meu Senhor”.', 56, b'0', 24, 1),
	(638, 'Disseram-lhe: “Vamos chamar a jovem e perguntar sua opinião”.', 57, b'0', 24, 1),
	(639, 'chamaram Rebeca e lhe perguntaram: “Queres ir com este homem?” E ela respondeu: “Quero”.', 58, b'0', 24, 1),
	(640, 'Deixaram, pois, partir sua irmã Rebeca, juntamente com sua ama de leite, o criado de Abraão e seus homens.', 59, b'0', 24, 1),
	(641, 'Abençoaram Rebeca, dizendo:“Tu, irmã nossa, multiplica-te aos milhares e os teus descendentes conquistem as cidades inimigas”.', 60, b'0', 24, 1),
	(642, 'Rebeca levantou-se com suas criadas, montaram nos camelos e acompanharam o homem. E assim o criado levou Rebeca e partiu.', 61, b'0', 24, 1),
	(643, 'Isaac andava perto do poço de Laai-Roí; ele morava, então, na região do Negueb.', 62, b'0', 24, 1),
	(644, 'Assim, ao cair da tarde, saiu para passear pelo campo e, erguendo os olhos, viu camelos chegando.', 63, b'0', 24, 1),
	(645, 'Rebeca também, erguendo os olhos, viu Isaac. Ela desceu do camelo', 64, b'0', 24, 1),
	(646, 'e perguntou ao criado: “Quem é aquele homem que vem caminhando pelo campo ao nosso encontro?” O criado respondeu: “É o meu Senhor”. Ela puxou o véu e se cobriu.', 65, b'0', 24, 1),
	(647, 'Então o criado contou a Isaac tudo o que havia feito.', 66, b'0', 24, 1),
	(648, 'Então o criado contou a Isaac tudo o que havia feito.', 67, b'0', 24, 1),
	(649, 'Abraão tomou outra mulher, de nome Cetura.', 1, b'0', 25, 1),
	(650, 'Dela nasceram Zanrã, Jecsã, Madã, Madiã, Jesboc e Sué.', 2, b'0', 25, 1),
	(651, 'Jecsã gerou Sabá e Dadã. Filhos de Dadã são os assuritas, os latusitas e os loomitas.', 3, b'0', 25, 1),
	(652, 'Os filhos de Madiã foram Efa, Ofer, Henoc, Abida e Eldaá. Todos esses são filhos de Cetura.', 4, b'0', 25, 1),
	(653, 'Abraão, porém, deu todos os seus bens a Isaac.', 5, b'0', 25, 1),
	(654, 'Aos filhos das concubinas fez doações, mas ainda em vida afastou-os do filho Isaac, mandando-os rumo às terras do oriente.', 6, b'0', 25, 1),
	(655, 'Estes são os anos de vida de Abraão: viveu cento e setenta e cinco anos', 7, b'0', 25, 1),
	(656, 'e expirou. Morreu numa feliz velhice, idoso e cumulado de anos, e foi reunir-se a seus antepassados.', 8, b'0', 25, 1),
	(657, 'Os filhos Isaac e Ismael sepultaram-no na gruta de Macpela, no campo do heteu Efron, filho de Seor, em frente de Mambré.', 9, b'0', 25, 1),
	(658, 'Neste campo, comprado por Abraão aos heteus, foram sepultados Abraão e a mulher Sara.', 10, b'0', 25, 1),
	(659, 'Depois da morte de Abraão, Deus abençoou o filho Isaac, que ficou morando junto ao poço de Laai-Roí.', 11, b'0', 25, 1),
	(660, 'Estes são os descendentes de Ismael, o filho que Agar, a escrava egípcia de Sara, dera a Abraão.', 12, b'0', 25, 1),
	(661, 'Estes são os nomes dos filhos de Ismael, em ordem de nascimento. O primogênito de Ismael foi Nabaiot, depois Cedar, Adbeel, Mabsam,', 13, b'0', 25, 1),
	(662, 'Masma, Duma, Massa,', 14, b'0', 25, 1),
	(663, 'Hadad, Tema, Jetur, Nafis e Cedma.', 15, b'0', 25, 1),
	(664, 'São esses os filhos de Ismael e os seus nomes, de acordo com as aldeias e acampamentos: foram doze chefes de doze tribos.', 16, b'0', 25, 1),
	(665, 'Ismael viveu cento e trinta e sete anos e expirou. Morreu e foi reunir-se a seus antepassados.', 17, b'0', 25, 1),
	(666, 'Seus filhos habitavam desde Hévila até Sur, em frente ao Egito na direçãode Assur. Assim, Ismael estabeleceu-se em frente de todos os seus irmãos.', 18, b'0', 25, 1),
	(667, 'Eis a história dos descendentes de Isaac, filho de Abraão. Abraão gerou Isaac. 20 Isaac tinha quarenta anos quando se casou com Rebeca, filha do arameu Batuel e irmã do arameu Labão. Ela veio de Padã-Aram.', 19, b'0', 25, 1),
	(668, 'Isaac tinha quarenta anos quando se casou com Rebeca, filha do arameu Batuel e irmã do arameu Labão. Ela veio de Padã-Aram.', 20, b'0', 25, 1),
	(669, 'Isaac suplicou ao Senhor por sua mulher, que era estéril. Foi atendido pelo Senhor, e Rebeca concebeu.', 21, b'0', 25, 1),
	(670, 'Mas os meninos chocavam-se no ventre. Ela disse: “Se é assim, o que adianta viver?” E foi consultar o Senhor,', 22, b'0', 25, 1),
	(671, 'que lhe respondeu: “Duas nações trazes no ventre, em tuas entranhas dois povos se dividirão. Um povo será mais forte que o outro, e o mais velho servirá ao mais novo”.', 23, b'0', 25, 1),
	(672, 'Quando chegou o tempo de dar à luz, ela tinha gêmeos no ventre.', 24, b'0', 25, 1),
	(673, 'primeiro saiu todo ruivo, peludo como um manto de pele, e foi chamado Esaú.', 25, b'0', 25, 1),
	(674, 'Depois saiu o irmão, segurando com a mão o calcanhar de Esaú, e foi chamado Jacó. Isaac tinha sessenta anos quando eles nasceram.', 26, b'1', 25, 1),
	(675, 'Quando os meninos cresceram, Esaú tornou-se um hábil caçador e homem rude, ao passo que Jacó era pacífico e morava em tendas.', 27, b'0', 25, 1),
	(676, 'Isaac gostava mais de Esaú porque comia da caça, mas Rebeca preferia Jacó.', 28, b'0', 25, 1),
	(677, 'Certo dia, Jacó preparou uma sopa de lentilhas. Esaú chegou do campo, muito cansado', 29, b'0', 25, 1),
	(678, 'e disse a Jacó: “Dá-me de comer desse negócio vermelho, pois estou exausto”. Foi por isso que Esaú recebeu o nome de Edom.', 30, b'0', 25, 1),
	(679, 'Jacó respondeu-lhe: “Vende-me agora mesmo o teu direito de primogênito”.', 31, b'0', 25, 1),
	(680, 'Esaú ponderou: “Estou morrendo de fome, e de que me serve a primogenitura?”', 32, b'0', 25, 1),
	(681, 'Jacó insistiu: “Jura-me agora mesmo!” E Esaú jurou e vendeu o direito de primogênito a Jacó.', 33, b'0', 25, 1),
	(682, 'Então Jacó deu-lhe pão com a sopa de lentilhas. Esaú comeu e bebeu, levantou-se e foi embora. Desprezou assim a sua primogenitura.', 34, b'0', 25, 1),
	(683, 'Ocorreu novamente uma fome no país, depois daquela primeira no tempo de Abraão. Isaac foi até Gerara, junto a Abimelec, rei dos filisteus,', 1, b'0', 26, 1),
	(684, 'pois o Senhor lhe aparecera e tinha dito: “Não desças ao Egito. Vai morar na terra que eu te indico.', 2, b'0', 26, 1),
	(685, 'Nesta terra fica migrando, e eu estarei contigo e te abençoarei. Pois a ti e à tua descendência darei todas estas terras, cumprindo o juramento que fiz a teu pai Abraão.', 3, b'0', 26, 1),
	(686, 'Multiplicarei tua descendência como as estrelas do céu e lhe darei todas estas terras. Por tua descendência serão abençoadas todas as nações da terra.', 4, b'0', 26, 1),
	(687, 'Isso, em consideração a Abraão, que obedeceu à minha voz e observou meu mandamento, os meus preceitos, as minhas prescrições e leis”.', 5, b'0', 26, 1),
	(688, 'Assim Isaac foi morar em Gerara.', 6, b'0', 26, 1),
	(689, 'Quando os homens do lugar lhe perguntavam por sua mulher, ele dizia: “É minha irmã”. Temia dizer que era sua mulher, para que os homens do lugar não o matassem por causa de Rebeca, pois era muito bonita.', 7, b'0', 26, 1),
	(690, 'Como se prolongasse sua permanência em Gerara, certo dia, Abimelec, rei dos filisteus, olhava pela janela e viu Isaac acariciando Rebeca, sua mulher.', 8, b'0', 26, 1),
	(691, 'Abimelec mandou chamar Isaac e lhe disse: “Não há dúvida que é tua mulher. Por que então dizes: ‘É minha irmã’?” Isaac respondeu: “Pensei comigo: vou ser morto por causa dela”.', 9, b'0', 26, 1),
	(692, 'Abimelec respondeu: “Por que nos fizeste isso? Faltou pouco para que alguém de nossa gente dormisse com tua mulher, e assim tu terias atraído sobre nós uma culpa”.', 10, b'0', 26, 1),
	(693, 'Então Abimelec decretou para todo o povo: “Aquele que tocar neste homem, ou em sua mulher, morrerá”.', 11, b'0', 26, 1),
	(694, 'Isaac fez naquela terra sua semeadura e colheu naquele ano cem vezes o que semeou, pois o Senhor o abençoou.', 12, b'0', 26, 1),
	(695, 'Foi enriquecendo sempre mais, até tornar-se um homem muito rico.', 13, b'0', 26, 1),
	(696, 'Possuía rebanhos de ovelhas e bois e numerosa criadagem, de modo que os filisteus ficaram com inveja dele.', 14, b'0', 26, 1),
	(697, 'Os filisteus entupiram todos os poços abertos pelos criados do pai Abraão, enchendo-os de terra.', 15, b'0', 26, 1),
	(698, 'E Abimelec disse a Isaac: “Vai-te embora daqui, porque chegaste a ser muito mais poderoso do que nós”.', 16, b'0', 26, 1),
	(699, 'Isaac partiu e acampou junto à torrente de Gerara, onde se estabeleceu.', 17, b'0', 26, 1),
	(700, 'Reabriu os poços cavados no tempo do pai Abraão e entupidos pelos filisteus depois da morte de Abraão, dando-lhes os mesmos nomes que seu pai lhes havia dado.', 18, b'0', 26, 1),
	(701, 'Os criados de Isaac cavaram um poço junto à torrente e descobriram um veio de água.', 19, b'0', 26, 1),
	(702, 'Mas os pastores de Gerara começaram a discutir com os de Isaac, afirmando que a água era deles. O poço recebeu o nome de Esec (Desafio), porque causa da contenda que suscitou.', 20, b'0', 26, 1),
	(703, 'Escavaram outro poço, que também causou discussão, e recebeu o nome de Sitna (Inimizade).', 21, b'0', 26, 1),
	(704, 'Indo mais longe, cavou mais um poço, pelo qual já não houve rixas, e pôs-lhe o nome de Reobot (Espaço), dizendo: “Agora o Senhor já nos deu espaço, e podemos prosperar nesta terra”.', 22, b'0', 26, 1),
	(705, 'Dali subiu para Bersabéia.', 23, b'0', 26, 1),
	(706, 'Naquela noite apareceu-lhe o Senhor e disse: “Eu sou o Deus de teu pai Abraão; nada temas, pois estou contigo. Eu te abençoarei e multiplicarei tua descendência por causa de meu servo Abraão”', 24, b'0', 26, 1),
	(707, 'Ergueu ali um altar, invocou o nome do Senhor e armou o acampamento. Os criados de Isaac puseram-se a cavar ali um poço.', 25, b'0', 26, 1),
	(708, 'bimelec veio visitá-lo de Gerara com seu amigo Ocozat e com Ficol, general do exército.', 26, b'0', 26, 1),
	(709, '“Por que viestes a mim, vós que me odiais e me expulsastes do vosso meio?”', 27, b'0', 26, 1),
	(710, 'Eles disseram: “Porque vimos claramente que o Senhor está contigo e achamos que deveria haver um juramento entre nós. Queremos fazer aliança contigo.', 28, b'0', 26, 1),
	(711, 'Jura que  nunca nos farás mal algum, como nós nunca te atacamos mas te fizemos somente o bem, deixando-te partir em paz. Pois tu és o abençoado pelo Senhor”.', 29, b'0', 26, 1),
	(712, 'Isaac preparou-lhes um banquete, e eles comeram e beberam.', 30, b'0', 26, 1),
	(713, 'Na manhã seguinte levantaram-se e fizeram um juramento mútuo. Isaac os despediu, e eles partiram em paz.', 31, b'0', 26, 1),
	(714, 'Naquele mesmo dia vieram os servos de Isaac informá-lo acerca do poço que estavam cavando e disseram: “Achamos água”.', 32, b'0', 26, 1),
	(715, 'Isaac chamou o poço de Siba. Por isso a cidade se chama Bersabéia até hoje.', 33, b'0', 26, 1),
	(716, '34 Quando Esaú completou quarenta anos, casou-se com Judite, filha do heteu Beeri, e com Basemat, filha do heteu Elon.', 34, b'0', 26, 1),
	(717, 'Elas causaram muitos aborrecimentos a Isaac e Rebeca.', 35, b'0', 26, 1),
	(718, 'Quando Isaac ficou velho, seus olhos se enfraqueceram e já não podia ver. Chamou, então, o filho mais velho Esaú: “Meu filho!” Este respondeu: “Aqui estou!”', 1, b'0', 27, 1),
	(719, 'Quando Isaac ficou velho, seus olhos se enfraqueceram e já não podia ver. Chamou, então, o filho mais velho Esaú: “Meu filho!” Este respondeu: “Aqui estou!”', 2, b'0', 27, 1),
	(720, 'Pega tuas armas, flechas e arco e sai para o campo. Se apanhares alguma caça,', 3, b'0', 27, 1),
	(721, 'prepara-me um saboroso assado, como sabes que eu gosto, e traze-o para que eu o coma e te dê a bênção antes de morrer”.', 4, b'0', 27, 1),
	(722, 'Rebeca escutava o que Isaac dizia a seu filho Esaú. Esaú saiu para o campo à procura de caça para o pai.', 5, b'0', 27, 1),
	(723, 'Rebeca disse a seu filho Jacó: “Olha, ouvi teu pai falar com teu irmão Esaú e dizer-lhe:', 6, b'0', 27, 1),
	(724, '‘Traze-me uma caça e prepara-me um saboroso assado para que eu o coma e te abençoe diante do Senhor, antes de minha morte’.', 7, b'0', 27, 1),
	(725, 'Agora, meu filho, escuta bem o que te mando:', 8, b'0', 27, 1),
	(726, 'vai até ao rebanho e traze-me dois cabritos gordos. Com eles farei para teu pai um assado saboroso como ele gosta.', 9, b'0', 27, 1),
	(727, 'Depois, leva-o a teu pai para que ele coma e te dê a bênção antes da sua morte”.', 10, b'0', 27, 1),
	(728, 'Jacó respondeu a Rebeca, sua mãe: “Mas meu irmão Esaú é um homem peludo, enquanto minha pele é lisa!', 11, b'0', 27, 1),
	(729, 'Se o pai me tocar, vai me considerar um impostor, e atrairei sobre mim a maldição em vez da bênção”.13[[[', 12, b'0', 27, 1),
	(730, 'Se o pai me tocar, vai me considerar um impostor, e atrairei sobre mim a maldição em vez da bênção”.', 13, b'0', 27, 1),
	(731, 'A mãe lhe disse: “Caia sobre mim tua maldição, meu filho, mas obedece-me. Vai pegar os cabritos para mim”.', 14, b'0', 27, 1),
	(732, 'Ele foi pegar os cabritos para a mãe, e ela preparou um assado saboroso como o pai gostava.', 15, b'0', 27, 1),
	(733, 'Com as peles dos cabritos cobriu-lhe as mãos e a parte lisa do pescoço.', 16, b'0', 27, 1),
	(734, 'Pôs nas mãos do filho Jacó o assado e o pão que havia preparado.', 17, b'0', 27, 1),
	(735, '18 Este os levou ao pai e disse: “Meu pai!” Isaac respondeu: “Estou ouvindo! Quem és tu, meu filho?”', 18, b'0', 27, 1),
	(736, 'E Jacó respondeu ao pai: “Eu sou Esaú, teu filho primogênito. Fiz como me ordenaste. Levanta-te, senta-te e come de minha caça, para me abençoares”.', 19, b'0', 27, 1),
	(737, 'Isaac disse ao filho: “Como conseguiste achar a caça tão depressa, meu filho?” Ele respondeu: “O Senhor teu Deus me deu sorte”.', 20, b'0', 27, 1),
	(738, 'Isaac disse a  Jacó: “Vem cá, meu filho, para que eu te apalpe e veja se és ou não meu filho Esaú”.', 21, b'0', 27, 1),
	(739, 'Jacó achegou- se ao pai Isaac, que o apalpou e disse: “A voz é a voz de Jacó, mas as mãos são as de Esaú”.', 22, b'0', 27, 1),
	(740, 'E não  o reconheceu, pois as mãos estavam peludas como as do irmão Esaú. Então decidiu abençoá-lo.', 23, b'0', 27, 1),
	(741, 'Perguntou-lhe ainda: “Tu és, de fato, meu filho Esaú?” Ele respondeu: “Sou”.', 24, b'0', 27, 1),
	(742, 'Isaac continuou: “Meu filho, serve-me da tua caça para eu comer e te abençoar”. Jacó o serviu e ele comeu. Trouxe-lhe também vinho e ele bebeu.', 25, b'0', 27, 1),
	(743, 'Disse-lhe então seu pai Isaac: “Aproxima-te, meu filho, e beija-me”.', 26, b'0', 27, 1),
	(744, 'Jacó se aproximou e o beijou.', 27, b'0', 27, 1),
	(745, 'Que Deus te conceda o orvalho do céu e a fertilidade da terra, trigo e vinho em abundância.', 28, b'0', 27, 1),
	(746, 'Que os povos te sirvam e as nações se prostrem diante de ti; sê o Senhor de teus irmãos, e diante de ti inclinem-se os filhos de tua mãe. Maldito seja quem te amaldiçoar e bendito, quem te abençoar”.', 29, b'0', 27, 1),
	(747, '30 Apenas Isaac tinha acabado de abençoar Jacó, que logo saíra da presença do pai, quando seu irmão Esaú voltou da caça.', 30, b'0', 27, 1),
	(748, 'Também ele preparou um assado saboroso,levou-o ao pai e disse: “Que meu pai se levante e coma da caça de seu filho para abençoá-lo”.', 31, b'0', 27, 1),
	(749, 'Isaac, seu pai, perguntou-lhe: “Quem és tu?” E ele respondeu: “Sou teu filho primogênito Esaú”.', 32, b'0', 27, 1),
	(750, 'Isaac ficou profundamente perturbado e disse: “E quem, então, foi caçar e me trouxe a caça? Eu comi de tudo isso antes que viesses. Eu o abençoei, e abençoado ficará”.', 33, b'0', 27, 1),
	(751, 'Ao ouvir as palavras do pai, Esaú pôs-se a gritar e chorar amargamente e lhe disse: “Abençoa-me também a mim, meu pai”.', 34, b'0', 27, 1),
	(752, 'Mas Isaac respondeu: “Teu irmão veio com disfarce e usurpou tua bênção”.', 35, b'0', 27, 1),
	(753, 'Esaú lhe disse: “É com razão que se chama Jacó, pois com esta já são duas vezes que levou vantagem sobre mim; primeiro tirou-me a primogenitura e agora usurpou a minha bênção”. E acrescentou: “Não reservaste nenhuma bênção para mim?”', 36, b'0', 27, 1),
	(754, 'Respondeu Isaac e disse a Esaú: “Olha, eu fiz de Jacó o teu Senhor, e todos os parentes o servirão. Eu lhe garanti o trigo e o vinho. Que poderia eu fazer por ti, meu filho?”', 37, b'0', 27, 1),
	(755, 'E Esaú disse ao pai: “Não tens mais do que uma bênção, meu pai? Abençoa-me também a mim, meu pai”. E chorou em voz alta.', 38, b'0', 27, 1),
	(756, 'Então Isaac o atendeu e disse: “Longe da terra fértil será a tua morada e sem o orvalho que desce do céu.', 39, b'0', 27, 1),
	(757, 'Viverás da tua espada e servirás a teu irmão; mas logo que te soltares, sacudirás o jugo de teu pescoço”.', 40, b'0', 27, 1),
	(758, 'Esaú começou a nutrir ódio contra Jacó por causa da bênção que o pai lhe dera, e dizia a si mesmo: “Estão perto os dias de luto por meu pai. Depois vou matar meu irmão Jacó”.', 41, b'0', 27, 1),
	(759, 'Rebeca soube o que seu filho mais velho Esaú havia dito e mandou chamar Jacó, o filho mais novo. Disse-lhe: “Olha, teu irmão Esaú planeja uma vingança mortal contra ti.', 42, b'0', 27, 1),
	(760, 'Escuta o que vou te dizer, meu filho: foge para Harã, para junto de meu irmão Labão.', 43, b'0', 27, 1),
	(761, 'Fica alguns anos com ele, até que passe a fúria de teu irmão,', 44, b'0', 27, 1),
	(762, 'a sua ira se amaine e ele se esqueça do que lhe fizeste. Depois mandarei buscar-te. Por que haveria eu de perder os dois num só dia?”', 45, b'0', 27, 1),
	(763, 'Rebeca queixou-se junto a Isaac: “Essas moças hetéias estão aborrecendo minha vida. Se Jacó se casar com uma dessas hetéias da região, que me adianta viver?”', 46, b'0', 27, 1),
	(764, 'Isaac chamou Jacó, deu-lhe a bênção e ordenou: “Não cases com nenhuma das moças de Canaã.', 1, b'0', 28, 1),
	(765, 'Vai a Padã-Aram, à casa de Batuel, teu avô materno. Casa-te lá com uma das filhas de Labão, irmão de tua mãe.', 2, b'0', 28, 1),
	(766, 'Que o Deus Poderoso te abençoe, te faça fecundo e te multiplique, para te tornares uma comunidade de povos.', 3, b'0', 28, 1),
	(767, 'Conceda-te a bênção de Abraão, a ti e à tua descendência, a fim de possuíres a terra em que agora vives como migrante, e que Deus deu a Abraão”.', 4, b'0', 28, 1),
	(768, 'Isaac se despediu de Jacó, e este partiu para Padã-Aram, para junto de Labão, filho do arameu Batuel, irmão de Rebeca, mãe de Jacó e de Esaú.', 5, b'0', 28, 1),
	(769, 'Esaú viu que Isaac tinha abençoado Jacó, mandando-o a Padã-Aram para escolher ali uma mulher, e que, ao lhe dar a bênção, havia dito: “Não te cases com uma cananéia”.', 6, b'0', 28, 1),
	(770, 'E Jacó, obedecendo aos pais, tinha ido a Padã-Aram.', 7, b'0', 28, 1),
	(771, 'Esaú percebeu então que o pai não gostava das moças de Canaã.', 8, b'0', 28, 1),
	(772, 'Foi para junto de Ismael, filho de Abraão, e tomou para mulher Maelet filha de Ismael e irmã de Nabaiot, além das mulheres que já tinha.', 9, b'0', 28, 1),
	(773, 'Jacó saiu de Bersabéia e dirigiu-se a Harã.', 10, b'0', 28, 1),
	(774, 'Chegou a um lugar onde resolveu passar a noite, pois o sol já se havia posto. Serviu-se de uma das pedras do lugar como travesseiro e dormiu ali.', 11, b'0', 28, 1),
	(775, 'Em sonho, viu uma escada apoiada no chão e com a outra ponta tocando o céu. Por ela subiam e desciam os anjos de Deus.', 12, b'0', 28, 1),
	(776, 'No alto da escada estava o Senhor, que lhe dizia: “Eu sou o Senhor, Deus de teu pai Abraão, o Deus de Isaac. A ti e à tua descendência darei a terra em que estás dormindo.', 13, b'0', 28, 1),
	(777, 'Tua descendência será como a poeira da terra. Tu te expandirás para o ocidente e para o oriente, para o norte e para o sul. Em ti e em tua descendência serão abençoadas todas as famílias da terra.', 14, b'0', 28, 1),
	(778, 'Estou contigo e te guardarei aonde quer que vás, e te reconduzirei a esta terra. Nunca te abandonarei até cumprir o que te prometi”.', 15, b'0', 28, 1),
	(779, 'Ao despertar, Jacó disse: “Sem dúvida o Senhor está neste lugar, e eu não sabia”.', 16, b'0', 28, 1),
	(780, 'Cheio de pavor, acrescentou: “Como é terrível este lugar! Isto aqui só pode ser a casa de Deus e a porta do céu”.', 17, b'0', 28, 1),
	(781, 'Jacó levantou-se bem cedo, tomou a pedra que lhe servira de travesseiro, colocou-a de pé para servir de coluna sagrada e derramou óleo sobre ela.', 18, b'0', 28, 1),
	(782, 'Ele chamou aquele lugar Betel, \\Casa de Deus. Anteriormente, a cidade chamava-se Luza.', 19, b'0', 28, 1),
	(783, 'Jacó fez, então, este voto: “Se Deus estiver comigo e me proteger nesta viagem, se ele me der pão para comer e roupa para vestir,', 20, b'0', 28, 1),
	(784, 'se eu voltar são e salvo para a casa de meu pai, então o Senhor será meu Deus.', 21, b'0', 28, 1),
	(785, 'Esta pedra que ergui como coluna sagrada será transformada em casa de Deus, e eu te darei o dízimo de tudo o que me deres”.', 22, b'0', 28, 1),
	(786, 'Jacó continuou a viagem e chegou às terras do oriente.', 1, b'0', 29, 1),
	(787, 'Viu no campo um poço junto ao qual descansavam três rebanhos, pois era desse poço que os rebanhos bebiam água. Havia uma grande  pedra na boca do poço.', 2, b'0', 29, 1),
	(788, 'Só quando todos os rebanhos estavam reunidos é que rolavam a pedra da boca do poço e davam de beber às ovelhas. Depois recolocavam a pedra em seu devido lugar.', 3, b'0', 29, 1),
	(789, 'Jacó perguntou-lhes: “De onde sois, irmãos?” – “Somos de Harã”, responderam.', 4, b'0', 29, 1),
	(790, 'E Jacó continuou: “Conheceis Labão filho de Nacor?” – “Conhecemos”, responderam.', 5, b'0', 29, 1),
	(791, '“Ele está bem?”, perguntou Jacó. E disseram: “Sim, está bem. Olha, aí vem sua filha Raquel com as ovelhas”.', 6, b'0', 29, 1),
	(792, 'E ele lhes disse: “Ainda é cedo para reunir os rebanhos. Por que não dais de beber às ovelhas e não as levais de novo a pastar?”', 7, b'0', 29, 1),
	(793, 'Eles responderam: “Não podemos fazê-lo enquanto não se reunirem todos os rebanhos. Só então removeremos a pedra da boca do poço e daremos de beber às ovelhas”.', 8, b'0', 29, 1),
	(794, 'Jacó ainda conversava com eles quando chegou Raquel com o rebanho do pai, pois era pastora.', 9, b'0', 29, 1),
	(795, 'Ao ver Raquel filha da Labão, irmão de sua mãe, e as ovelhas de Labão, irmão de sua mãe, Jacó aproximou-se, removeu a pedra de cima do poço e deu de beber às ovelhas de Labão, irmão de sua mãe.', 10, b'0', 29, 1),
	(796, 'Em seguida beijou Raquel e chorou em voz alta.', 11, b'0', 29, 1),
	(797, 'Contou a Raquel que era sobrinho de seu pai e filho de Rebeca, e ela foi correndo contar ao pai.', 12, b'0', 29, 1),
	(798, 'Logo que Labão soube das notícias de Jacó, filho de sua irmã, correu ao encontro dele, abraçouo, beijou-o e o levou para casa. Jacó contou a Labão o que havia acontecido e', 13, b'0', 29, 1),
	(799, 'Labão lhe disse: “Realmente tu és meu osso e minha carne”. E Jacó ficou com Labão durante um mês.', 14, b'0', 29, 1),
	(800, 'Depois Labão disse a Jacó: “Será que me vais servir de graça por seres meu sobrinho? Dize-me qual deve ser o salário”.', 15, b'0', 29, 1),
	(801, 'Ora, Labão tinha duas filhas. A mais velha se chamava Lia e a mais nova Raquel.', 16, b'0', 29, 1),
	(802, 'Lia tinha um olhar apagado, mas Raquel era bonita de corpo e de rosto.', 17, b'0', 29, 1),
	(803, 'Jacó ficou enamorado de Raquel e disse a Labão: “Eu te servirei sete anos por Raquel, tua filha mais nova”.', 18, b'0', 29, 1),
	(804, 'Labão respondeu: “É melhor confiá-la a ti do que entregá-la a um estranho. Fica comigo”.', 19, b'0', 29, 1),
	(805, 'Jacó serviu por Raquel sete anos, que lhe pareceram dias, tanto era o amor por ela.', 20, b'0', 29, 1),
	(806, 'Jacó disse a Labão: “Dá-me minha mulher, pois completou-se o tempo e quero viver com ela”.', 21, b'0', 29, 1),
	(807, 'Labão reuniu todos os homens do lugar e deu um banquete.', 22, b'0', 29, 1),
	(808, 'Chegada a noite, porém, tomou a filha Lia e levou-a a Jacó, que dormiu com ela.', 23, b'0', 29, 1),
	(809, '(Labão dera à filha Lia a escrava Zelfa, para lhe servir de criada.)', 24, b'0', 29, 1),
	(810, 'Ao amanhecer, Jacó viu que era Lia e disse a Labão: “Por que fizeste isso comigo? Não te servi por Raquel? Por que me enganaste?”', 25, b'0', 29, 1),
	(811, 'E Labão respondeu: “Não é costume em nosso lugar dar a filha mais nova antes da mais velha.', 26, b'0', 29, 1),
	(812, 'Termina esta semana de festa e depois te será dada também a outra pelo serviço que me prestarás durante outros sete anos”.', 27, b'0', 29, 1),
	(813, 'Assim o fez Jacó. Completada a semana, Labão deu-lhe por mulher sua filha Raquel,', 28, b'0', 29, 1),
	(814, 'e com ela a escrava Bala para servi-la como criada.', 29, b'0', 29, 1),
	(815, 'Jacó se uniu também a Raquel e amou Raquel mais do que Lia. Por ela serviu mais sete anos. Deus dá filhos a Jacó', 30, b'0', 29, 1),
	(816, 'Quando o Senhor viu que Lia era desprezada, tornou-a fecunda, ao passo que Raquel permaneceu estéril.', 31, b'0', 29, 1),
	(817, 'Lia concebeu e deu à luz um filho, a quem chamou Rúben, pois dizia: “Agora que o Senhor olhou para minha aflição, meu marido me amará”.', 32, b'0', 29, 1),
	(818, 'Concebeu de novo e deu à luz outro  filho, dizendo: “O Senhor ouviu que eu era desprezada e deu-me mais este”. E deu-lhe o nome de Simeão.', 33, b'0', 29, 1),
	(819, 'Concebeu outra vez e deu à luz um filho e disse: “Desta vez meu marido se apegará a mim, pois lhe dei três filhos”. Por isso o chamou Levi.', 34, b'0', 29, 1),
	(820, 'Concebeu novamente e deu à luz um filho, dizendo: “Agora sim posso louvar o Senhor”. Por isso o chamou Judá. E parou de ter filhos.', 35, b'0', 29, 1),
	(821, 'Vendo que não conseguia dar filhos a Jacó, Raquel ficou com ciúmes da irmã e disse a Jacó: “Dá-me filhos, senão eu morro!”', 1, b'0', 30, 1),
	(822, 'Jacó irritou-se com Raquel e lhe disse: “Por acaso estou no lugar de Deus que te fez estéril?”', 2, b'0', 30, 1),
	(823, 'Ela respondeu: “Aí tens minha escrava Bala. Une-te a ela para dar à luz sobre os meus joelhos. Assim terei filhos também eu por meio dela”.', 3, b'0', 30, 1),
	(824, 'Deu-lhe, pois, a escrava por mulher e Jacó se uniu a ela.', 4, b'0', 30, 1),
	(825, 'Bala concebeu e deu a Jacó um filho.', 5, b'0', 30, 1),
	(826, 'Raquel disse: “Deus me fez justiça, atendeu meu pedido e deu-me um filho”. Por isso, chamou-o Dã.', 6, b'0', 30, 1),
	(827, 'Bala, escrava de Raquel, concebeu outra vez e deu um segundo filho a Jacó.', 7, b'0', 30, 1),
	(828, 'E Raquel disse: “Batalhas sobre-humanas travei com minha irmã e a venci”. Por isso o chamou Neftali.', 8, b'0', 30, 1),
	(829, 'Percebendo que tinha parado de ter filhos, Lia tomou a escrava Zelfa e a deu a Jacó por mulher.', 9, b'0', 30, 1),
	(830, 'Zelfa, escrava de Lia, deu a Jacó um filho.', 10, b'0', 30, 1),
	(831, 'E Lia disse: “Que sorte!” e chamou-o Gad.', 11, b'0', 30, 1),
	(832, 'Zelfa, escrava de Lia, deu um segundo filho a Jacó.', 12, b'0', 30, 1),
	(833, 'Lia disse: “Para felicidade minha, pois as mulheres me felicitarão”; e chamou-o Aser.', 13, b'0', 30, 1),
	(834, 'Certo dia, na época da colheita do trigo, Rúben saiu e achou no campo umas mandrágoras. Ele as trouxe para Lia, sua mãe. Raquel disse a Lia: “Dá-me por favor algumas mandrágoras de teu filho”.', 14, b'0', 30, 1),
	(835, 'Lia respondeu: “Ainda te parece pouco tirar-me o marido, para quereres tirar-me também as mandrágoras que meu filho me deu?” – “Pois bem”, disse Raquel, “que Jacó durma esta noite contigo em troca das mandrágoras de teu filho”.q', 15, b'0', 30, 1),
	(836, 'Quando Jacó voltou do campo pela tarde, Lia saiu-lhe ao encontro e disse: “Dorme comigo, pois comprei este direito em troca de algumas mandrágoras de meu filho”. E Jacó dormiu aquela noite com ela.', 16, b'0', 30, 1),
	(837, 'Deus atendeu Lia, que concebeu e deu a Jacó o quinto filho.', 17, b'0', 30, 1),
	(838, 'Lia disse: “Deus me recompensou por ter dado minha escrava a meu marido”. E deu ao filho o nome de Issacar.', 18, b'0', 30, 1),
	(839, 'Lia concebeu de novo e deu a Jacó o sexto filho', 19, b'0', 30, 1),
	(840, 'e disse: “Deus me fez um belo presente. Agora meu marido me honrará, pois dei-lhe seis filhos”. E chamou-o Zabulon.', 20, b'0', 30, 1),
	(841, 'Depois deu à luz uma filha, que ela chamou Dina.', 21, b'0', 30, 1),
	(842, 'Então Deus se lembrou de Raquel. Deus a atendeu, tornando-a fecunda.', 22, b'0', 30, 1),
	(843, 'Ela concebeu e deu à luz um filho e disse: “Deus retirou a minha desonra”.', 23, b'0', 30, 1),
	(844, 'Ela lhe deu o nome de José, pois disse: “Que o Senhor me dê mais um filho”.', 24, b'0', 30, 1),
	(845, 'Quando Raquel deu à luz José, Jacó disse a Labão: “Deixa-me ir para meu lugar, para minha terra.', 25, b'0', 30, 1),
	(846, 'Dá-me as mulheres, pelas quais te servi, e os meus filhos, pois vou partir. Bem sabes o quanto trabalhei para ti”.', 26, b'0', 30, 1),
	(847, 'Labão respondeu: “Sem dúvida fui favorecido com a tua presença: fiquei sabendo, por adivinhação, que o Senhor me abençoou por causa de ti.', 27, b'0', 30, 1),
	(848, 'Fixa o teu salário, e eu te  pagarei”.', 28, b'0', 30, 1),
	(849, 'Jacó respondeu: “Sabes muito bem como te servi e como os rebanhos se desenvolveram sob os meus cuidados.', 29, b'0', 30, 1),
	(850, 'Era pouco o que possuías antes de minha chegada. Mas tudo aumentou consideravelmente, e o Senhor te abençoou por minha causa. Agora já é tempo de eu fazer algo também para minha família”.', 30, b'0', 30, 1),
	(851, 'Labão lhe disse: “Dize-me o que te devo dar”. “Não tens de me dar nada – respondeu Jacó – senão fazer o que vou dizer-te. Voltarei a apascentar e guardar teu rebanho.', 31, b'0', 30, 1),
	(852, 'Hoje vou passar por toda a criação e separar todo animal escuro entre os cordeiros e todo animal malhado ou listrado entre as cabras. Eles serão meu salário.', 32, b'0', 30, 1),
	(853, 'A minha honestidade ficará comprovada quando chegar o dia de receber o salário: todo animal meu que não for malhado ou listrado entre as cabras, ou escuro entre os cordeiros, seja considerado roubo”.', 33, b'0', 30, 1),
	(854, 'E Labão respondeu: “Pois bem, seja como dizes”.', 34, b'0', 30, 1),
	(855, 'Naquele mesmo dia Labão separou todos os bodes com malhas ou listras, todas as cabras malhadas ou com manchas brancas e os cordeiros de tonalidade escura e os entregou a seus filhos.', 35, b'0', 30, 1),
	(856, 'Colocou-os à distância de uns três dias de onde estava Jacó, o qual continuou a apascentar o resto do rebanho de Labão.', 36, b'0', 30, 1),
	(857, 'Jacó colheu varas verdes de álamo, de amendoeira e de plátano. Fez nelas algumas incisões e as descascou, deixando o branco das varas a descoberto.', 37, b'0', 30, 1),
	(858, 'Colocou depois as varas assim descascadas nos bebedouros, no lugar onde os animais iam beber e onde se acasalavam.', 38, b'0', 30, 1),
	(859, 'Assim, as fêmeas que eram cobertas diante das varas davam crias listradas, raiadas ou malhadas.', 39, b'0', 30, 1),
	(860, 'Jacó separava então esses cordeiros e dirigia as ovelhas para o que havia de listrado e escuro no rebanho de Labão. Assim constituiu um rebanho separado, que ele não deixava misturar-se com as ovelhas de Labão.', 40, b'0', 30, 1),
	(861, 'E sempre que as fêmeas vigorosas entravam em cio, Jacó punha as varas à vista, nos bebedouros,para que se acasalassem diante das varas.', 41, b'0', 30, 1),
	(862, 'Diante das fracas, porém, não as punha, e assim as crias fracas eram de Labão e as fortes de Jacó.', 42, b'0', 30, 1),
	(863, 'Deste modo Jacó tornou-se muito rico, dono de numerosos rebanhos, de escravos e escravas, de camelos e jumentos.', 43, b'0', 30, 1),
	(864, 'Jacó ouviu os filhos de Labão dizerem: “Jacó tomou tudo o que era de nosso pai e com isso construiu toda esta riqueza”.', 1, b'0', 31, 1),
	(865, 'Notou também pelo rosto de Labão que este já não o tratava com os mesmos sentimentos de antes.', 2, b'0', 31, 1),
	(866, 'E o Senhor disse a Jacó: “Volta para a terra de teu pai, para tua terra natal, que eu estarei contigo”.', 3, b'0', 31, 1),
	(867, 'Então Jacó mandou chamar Raquel e Lia para que fossem ao campo onde estava com o rebanho', 4, b'0', 31, 1),
	(868, 'e disse: “Eu noto no semblante de vosso pai que ele já não me trata com os mesmos sentimentos de antes. Mas o Deus de meu pai está comigo.', 5, b'0', 31, 1),
	(869, 'Vós mesmas sabeis que servi vosso pai com todas as minhas forças', 6, b'0', 31, 1),
	(870, 'e que ele me explorou, mudando dez vezes meu salário. Mas Deus não permitiu que ele me prejudicasse.', 7, b'0', 31, 1),
	(871, 'Quando ele dizia: ‘Teu salário serão os animais malhados’, todos os animais davam à luz malhados; e quando dizia: ‘Os animais listrados serão teu salário’, todas as ovelhas davam à luz listrados.', 8, b'0', 31, 1),
	(872, 'Deus tirou assim o rebanho de vosso pai e o deu a mim.', 9, b'0', 31, 1),
	(873, 'Pois no  tempo em que as ovelhas estão em cio, vi em sonhos que os carneiros, que cobriam as ovelhas, eram listrados, malhados ou pintados.', 10, b'0', 31, 1),
	(874, 'E o anjo de Deus me chamou no sonho: ‘Jacó’! E eu lhe respondi: ‘Eis-me aqui’.', 11, b'0', 31, 1),
	(875, 'E ele disse: Levanta os olhos e vê: todos os carneiros que cobrem as ovelhas são listrados, malhados ou pintados, porque vi tudo o que Labão está fazendo contigo.', 12, b'0', 31, 1),
	(876, 'Eu sou o Deus que te apareceu em Betel, onde ungiste a coluna sagrada e me fizeste o voto. Levanta-te, sai desta terra e volta para tua terra natal’”.', 13, b'0', 31, 1),
	(877, 'Raquel e Lia responderam: “Não temos direito a um dote ou herança na casa de nosso pai?', 14, b'0', 31, 1),
	(878, 'Acaso não nos trata como estrangeiras? Ele vendeu-nos e devorou o nosso dinheiro!', 15, b'0', 31, 1),
	(879, 'Acaso não nos trata como estrangeiras? Ele vendeu-nos e devorou o nosso dinheiro!', 16, b'0', 31, 1),
	(880, 'Jacó levantou-se e fez montar as mulheres e os filhos nos camelos.', 17, b'0', 31, 1),
	(881, 'Foi levando consigo todo o gado e tudo o que havia adquirido em Padã-Aram, rumo à casa de seu pai Isaac, para a terra de Canaã.', 18, b'0', 31, 1),
	(882, 'Como Labão tinha ido à tosquia das ovelhas, Raquel roubou as estatuetas dos ídolos de seu pai.', 19, b'0', 31, 1),
	(883, 'Assim Jacó iludiu Labão, o arameu, fugindo sem que ele o soubesse.', 20, b'0', 31, 1),
	(884, 'Fugiu levando tudo o que tinha, atravessou o rio Eufrates e dirigiu-se ao monte Galaad.', 21, b'0', 31, 1),
	(885, 'Três dias depois informaram a Labão que Jacó tinha fugido.', 22, b'0', 31, 1),
	(886, 'Levou, então, consigo sua gente e o perseguiu durante sete dias até alcançá-lo no monte Galaad.', 23, b'0', 31, 1),
	(887, 'De noite Deus apareceu em sonho a Labão, o arameu, e lhe disse: “Cuida-te de não fazer qualquer ameaça a Jacó”.', 24, b'0', 31, 1),
	(888, 'Quando Labão alcançou Jacó, este havia armado sua tenda no monte, e Labão fez o mesmo com sua gente no monte Galaad.', 25, b'0', 31, 1),
	(889, 'Labão disse a Jacó: “Que foi que fizeste? Enganaste-me e levaste contigo minhas filhas, como se fossem prisioneiras de guerra!', 26, b'0', 31, 1),
	(890, 'Por que fugiste secretamente e me enganaste, em vez de me avisares para te fazer uma despedida com festa, cantos, tímpanos e cítaras?', 27, b'0', 31, 1),
	(891, 'Nem sequer me deixaste beijar minhas filhas e meus netos. Agiste estupidamente.', 28, b'0', 31, 1),
	(892, 'Teria poder para vos fazer mal, mas o Deus de teu pai falou-me, na noite passada, dizendo: ‘Cuida-te de não fazer qualquer ameaça a Jacó’.', 29, b'0', 31, 1),
	(893, 'E se foi por sentires saudade da casa de teu pai que decidiste ir embora, por que então roubaste meus deuses?”', 30, b'0', 31, 1),
	(894, 'Jacó respondeu a Labão: “Eu receava que talvez me tirasses tuas filhas.', 31, b'0', 31, 1),
	(895, 'Ora, quanto aos deuses, morra aquele com quem os encontrares. Na presença de nossa gente, busca tudo o que seja teu e leva-o”. Jacó não sabia que Raquel os tinha roubado.', 32, b'0', 31, 1),
	(896, 'Labão entrou para examinar as tendas de Jacó, de Lia e das duas servas, mas não achou nada. Enquanto saía da tenda de Lia e entrava na de Raquel,', 33, b'0', 31, 1),
	(897, 'Raquel pegou os ídolos, escondeu-os nos arreios do camelo e sentou-se em cima. Labão revirou toda a tenda, sem achar nada.', 34, b'0', 31, 1),
	(898, 'Raquel disse ao pai: “Não te irrites, meu Senhor, por não poder levantar-me em tua presença, uma vez que estou menstruada”. Assim, por mais que procurasse em toda parte, Labão não pôde achar os ídolos.', 35, b'0', 31, 1),
	(899, 'Jacó irritou-se e discutiu com Labão, dizendo-lhe: “Qual é o meu crime? Que pecado cometi, para assim me perseguires?', 36, b'0', 31, 1),
	(900, 'Depois de revirares todas as minhas coisas, que achaste de teu? Apresenta-o aqui diante de minha gente e da tua, para que eles julguem entre nós dois.', 37, b'0', 31, 1),
	(901, 'Nesses vinte anos que passei em tua casa, tuas ovelhas e tuas  cabras não abortaram, nem comi os cordeiros de teus rebanhos.', 38, b'0', 31, 1),
	(902, 'Nem te apresentava os animais estraçalhados: a perda corria por minha conta. Reclamavas de mim o que me roubavam de dia e o que me roubavam de noite.', 39, b'0', 31, 1),
	(903, 'De dia me consumia o calor, de noite, o frio, e o sono me fugia dos olhos.', 40, b'0', 31, 1),
	(904, 'Assim passei vinte anos em tua casa. Catorze anos te servi por tuas filhas, seis por teu gado, e dez vezes mudaste o salário.', 41, b'0', 31, 1),
	(905, 'Se o Deus de meu pai, o Deus de Abraão e o Deus Terrível de Isaac, não estivesse comigo, agora me terias despachado de mãos vazias. Deus viu a minha aflição, o trabalho de minhas mãos, e deu a sentença na noite passada”.', 42, b'0', 31, 1),
	(906, 'Labão respondeu e disse a Jacó: “Estas filhas são minhas, estas crianças são meus filhos, estes rebanhos são meus rebanhos e tudo quanto vês é meu. Que poderia eu fazer hoje por estas minhas filhas e pelos filhos que elas deram à luz?', 43, b'0', 31, 1),
	(907, 'Vamos, portanto, fazer uma aliança nós dois para que sirva de garantia entre mim e ti”.', 44, b'0', 31, 1),
	(908, 'Tomou Jacó então uma pedra e a ergueu como coluna sagrada.', 45, b'0', 31, 1),
	(909, 'Depois deu ordem à sua gente para apanhar pedras e reuni-las num monte, junto ao qual comeram.', 46, b'0', 31, 1),
	(910, 'Labão lhe deu o nome de Jegar-Saaduta, Monte do Testemunho, ao passo que Jacó o chamou de Galed.', 47, b'0', 31, 1),
	(911, 'Labão disse: “Hoje este monte é um testemunho entre mim e ti”. Por isso chamaram-no Galed,', 48, b'0', 31, 1),
	(912, 'e também Masfa, Espreita, pois tinha dito: “O Senhor vigie a nós dois quando nos separarmos um do outro.', 49, b'0', 31, 1),
	(913, 'Se maltratares minhas filhas ou tomares outras mulheres além delas, mesmo que não haja ninguém que presencie nossa conversa, Deus será testemunha entre nós”.', 50, b'0', 31, 1),
	(914, 'E Labão disse ainda a Jacó: “Veja este monte e esta coluna sagrada que levantei entre mim e ti.', 51, b'0', 31, 1),
	(915, 'Este monte e esta coluna sagrada são testemunhas de que não os ultrapassarei com intenção hostil, nem tu os ultrapassarás para me fazeres mal.', 52, b'0', 31, 1),
	(916, 'O Deus de Abraão e o Deus de Nacor julguem entre nós”. Jacó jurou pelo Deus Terrível de Isaac, seu pai.', 53, b'0', 31, 1),
	(917, 'Depois ofereceu sacrifícios no monte e convidou sua gente para comer. Comeram e passaram a noite no monte.', 54, b'0', 31, 1),
	(918, 'Labão levantou-se cedo, beijou os netos e as filhas e os abençoou. Depois foi embora, de volta para seu lugar.', 1, b'0', 32, 1),
	(919, 'Jacó prosseguiu a viagem e encontrou-se com alguns anjos de Deus.', 2, b'0', 32, 1),
	(920, 'Ao vê-los, Jacó disse: “Este é o acampamento de Deus”. Por isso deu ao lugar o nome de Maanaim,', 3, b'0', 32, 1),
	(921, 'Jacó mandou adiante de si mensageiros a Esaú, seu irmão, na terra de Seir, nos campos de Edom.', 4, b'0', 32, 1),
	(922, 'Deu-lhes estas instruções: “Assim direis a meu Senhor Esaú: Assim fala teu servo Jacó: ‘Estive com Labão e morei com ele até agora.', 5, b'0', 32, 1),
	(923, 'Tenho bois, jumentos, ovelhas, escravos e escravas. Quero dar a notícia a meu Senhor, para alcançar seu favor’”.', 6, b'0', 32, 1),
	(924, 'Os mensageiros voltaram e disseram a Jacó: “Estivemos com teu irmão Esaú, e ele vem ao teu encontro com quatrocentos homens”.', 7, b'0', 32, 1),
	(925, 'Jacó ficou com muito medo e angustiado; dividiu em dois acampamentos sua gente, as ovelhas, o gado e os camelos.', 8, b'0', 32, 1),
	(926, 'Ele pensou assim: “Se  Esaú atacar um acampamento e o destroçar, talvez o outro fique a salvo”.', 9, b'0', 32, 1),
	(927, 'E Jacó orou: “Deus de meu pai Abraão, Deus de meu pai Isaac, Senhor que me disseste: ‘Volta para tua terra natal e serei bom para contigo’.', 10, b'0', 32, 1),
	(928, 'Não mereço tantos favores e toda essa fidelidade com que trataste teu servo. De fato passei este rio Jordão trazendo apenas o bastão e volto agora com dois acampamentos.', 11, b'0', 32, 1),
	(929, 'Livra-me das mãos de meu irmão Esaú, pois tenho medo que ele venha a exterminar-me, as mães com os filhos.', 12, b'0', 32, 1),
	(930, 'Foste tu que me garantiste: ‘Eu serei bom para contigo e tornarei a tua descendência como as areias do mar, tão numerosas que não se podem contar’”.', 13, b'0', 32, 1),
	(931, 'Jacó passou ali a noite. Depois escolheu, do que tinha, presentes para o irmão Esaú:', 14, b'0', 32, 1),
	(932, 'duzentas cabras e vinte bodes; duzentas ovelhas e vinte carneiros;', 15, b'0', 32, 1),
	(933, 'trinta camelas com as crias; quarenta vacas e dez touros; vinte jumentas e dez jumentos.', 16, b'0', 32, 1),
	(934, 'Confiou cada rebanho a um servo separadamente e lhes disse: “Ide à minha frente, deixando um espaço entre os rebanhos”.', 17, b'0', 32, 1),
	(935, 'Ao primeiro deu esta ordem: “Se meu irmão Esaú te encontrar e te perguntar: ‘De quem és, aonde vais e de quem é o que levas aí?’,', 18, b'0', 32, 1),
	(936, 'tu lhe responderás: ‘De teu servo Jacó: é um presente que ele envia a meu Senhor Esaú; ele vem também atrás de nós’”.', 19, b'0', 32, 1),
	(937, 'A mesma ordem deu ao segundo, ao terceiro e a todos quantos levavam o gado, dizendo-lhes: “Assim devereis falar a Esaú quando o encontrardes.', 20, b'0', 32, 1),
	(938, 'E direis: ‘Olha, teu servo Jacó vem atrás de nós’”. Pois Jacó dizia consigo: “Vou aplacá-lo com os presentes que me precedem e depois o verei pessoalmente. Talvez assim ele me receba bem”.', 21, b'0', 32, 1),
	(939, 'Os presentes passaram adiante e ele ficou ali naquela noite no acampamento.', 22, b'0', 32, 1),
	(940, 'Levantou-se, ainda de noite, tomou suas duas mulheres, as duas escravas e os onze filhos e passou o vau do Jaboc.', 23, b'0', 32, 1),
	(941, 'Jacó ajudou todos a passar a torrente e fez atravessar tudo o que tinha.', 24, b'0', 32, 1),
	(942, 'Quando depois ficou sozinho, um homem se pôs a lutar com ele até o raiar da aurora.', 25, b'0', 32, 1),
	(943, 'Vendo que não podia vencê-lo, atingiu a coxa de Jacó, de modo que o tendão se deslocou enquanto lutava com ele.', 26, b'0', 32, 1),
	(944, 'O homem disse a Jacó: “Larga-me, pois já surge a aurora”. Mas Jacó respondeu: “Não te largarei, se não me abençoares”.', 27, b'0', 32, 1),
	(945, 'E o homem lhe perguntou: “Qual é o teu nome?” – “Jacó”, respondeu.', 28, b'0', 32, 1),
	(946, 'E ele lhe disse: “Doravante não te chamarás Jacó, mas Israel, porque lutaste com Deus e com homens, e venceste”.', 29, b'0', 32, 1),
	(947, 'E Jacó lhe pediu: “Dize-me, por favor, teu nome”. Mas ele respondeu: “Para que perguntas por meu nome?” E ali mesmo o abençoou.', 30, b'0', 32, 1),
	(948, 'Jacó deu àquele lugar o nome de Fanuel, pois disse: “Vi Deus face a face e minha vida foi poupada”.', 31, b'0', 32, 1),
	(949, 'O sol surgia quando ele atravessava Fanuel; e ia mancando por causa da coxa.', 32, b'0', 32, 1),
	(950, 'Por isso os israelitas não comem até hoje o nervo da articulação da coxa, pois Jacó foi ferido nesse nervo.', 33, b'0', 32, 1),
	(951, 'Jacó ergueu os olhos e viu Esaú que vinha com quatrocentos homens. Então repartiu os filhos entre Lia, Raquel e as duas escravas,', 1, b'0', 33, 1),
	(952, 'pondo na frente estas duas com os filhos, depois Lia com os seus e por último Raquel com José.', 2, b'0', 33, 1),
	(953, 'Ele mesmo se pôs na frente de todos e se prostrou sete vezes em terra antes de se aproximar do irmão.', 3, b'0', 33, 1),
	(954, 'Esaú correu ao seu encontro, abraçou-o, lançou-se-lhe ao pescoço e o beijou. E ambos puseram-se a chorar.', 4, b'0', 33, 1),
	(955, 'Depois, levantando os olhos, Esaú viu as mulheres e as crianças; e perguntou: “Quem são estes que trazes contigo?” Jacó respondeu: “São os filhos com que Deus presenteou teu servo”.', 5, b'0', 33, 1),
	(956, 'Aproximaram-se as escravas com os filhos e se prostraram.', 6, b'0', 33, 1),
	(957, 'Aproximou-se também Lia com os seus e se prostraram. Depois acercaram-se José e Raquel e se prostraram.', 7, b'0', 33, 1),
	(958, 'Esaú lhe perguntou: “O que pretendes com todos esses rebanhos que vim encontrando?” Ele disse: “Conseguir o favor de meu Senhor”.', 8, b'0', 33, 1),
	(959, 'Esaú respondeu: “Já tenho bastante, meu irmão. Fica com o que é teu”.', 9, b'0', 33, 1),
	(960, '“Oh, não!”, respondeu Jacó. “Se alcancei teu favor, então aceita de minha mão o presente, pois vim à tua presença como se vem à presença de Deus, e tu me acolheste favoravelmente.', 10, b'0', 33, 1),
	(961, 'Aceita o presente que te mandei levar, pois Deus me ajudou, e não me falta nada”. Tanto insistiu que Esaú aceitou.', 11, b'0', 33, 1),
	(962, 'Este lhe disse: “Vamos andando. Eu te farei escolta”.', 12, b'0', 33, 1),
	(963, 'Mas Jacó lhe respondeu: “O meu Senhor sabe muito bem que há aqui crianças franzinas e que trago ovelhas e vacas com crias. Bastaria um dia de marcha forçada e todo o rebanho morreria.', 13, b'0', 33, 1),
	(964, 'Passe meu Senhor na frente de seu servo, e eu seguirei lentamente, ao passo dos rebanhos que vão na frente e ao passo das crianças, até alcançar meu Senhor em Seir”.', 14, b'0', 33, 1),
	(965, 'Esaú disse: “Deixarei contigo uma parte da gente que trago”. Mas Jacó respondeu: “E para quê, se alcancei o favor de meu Senhor?”', 15, b'0', 33, 1),
	(966, 'Assim Esaú voltou a Seir por seu caminho naquele mesmo dia.', 16, b'0', 33, 1),
	(967, 'Jacó partiu para Sucot e ali fez para si uma casa e abrigos para o  rebanho. Por isso chamou-se aquele lugar Sucot, as Tendas. Jacó em Siquém', 17, b'0', 33, 1),
	(968, 'De volta de Padã- Aram, Jacó chegou são e salvo à cidade de Siquém, na terra de Canaã, e acampou em frente à cidade.', 18, b'0', 33, 1),
	(969, 'Por cem moedas de prata ele comprou aos filhos de Hemor, pai de Siquém, o terreno onde armou as tendas. 20 Levantou ali um altar que chamou “El, Deus de Israel”.', 19, b'0', 33, 1),
	(970, 'Dina, a filha que Lia deu a Jacó, saiu um dia para visitar as moças daquela terra.', 1, b'0', 34, 1),
	(971, 'Siquém, filho de Hemor, o heveu, chefe daquela terra, viu-a, agarrou-a e deitou-se com ela, violentando-a.', 2, b'0', 34, 1),
	(972, 'Sentiu-se então apaixonado por Dina, a filha de Jacó, e, enamorado como estava, falou-lhe de modo amigo.', 3, b'0', 34, 1),
	(973, 'Siquém disse a seu pai Hemor: “Dá-me essa jovem em casamento”.', 4, b'0', 34, 1),
	(974, 'Jacó soube que Siquém tinha desonrado sua filha Dina. Mas, como seus filhos estavam no campo com o rebanho, calou-se até à volta deles.', 5, b'0', 34, 1),
	(975, 'Hemor, pai de Siquém, veio falar com Jacó.', 6, b'0', 34, 1),
	(976, 'Ao voltarem do campo, os filhos de Jacó ouviram a notícia e encheram-se de indignação e furor, pois Siquém havia cometido uma infâmia em Israel por ter dormido com a filha de Jacó, coisa que não se devia fazer.', 7, b'0', 34, 1),
	(977, 'Hemor lhes falou, dizendo:  “Meu filho Siquém está apaixonado por vossa filha. Peço-vos que lhe seja dada em casamento.', 8, b'0', 34, 1),
	(978, 'Assim nos tornaremos parentes; poderíeis dar-nos vossas filhas e tomar para vós as nossas', 9, b'0', 34, 1),
	(979, 'e habitar conosco. A terra estará à vossa disposição: habitai-a, percorrei-a e adquiri nela propriedades”.', 10, b'0', 34, 1),
	(980, 'Siquém disse ao pai e aos irmãos de Dina: “Encontre eu favor a vossos olhos, e vos darei o que me pedirdes.', 11, b'0', 34, 1),
	(981, 'Podeis aumentar o dote e as dádivas que devo dar. Tudo o que me pedirdes vo-lo darei, mas dai-me a moça em casamento”.', 12, b'0', 34, 1),
	(982, 'Ora, por causa do estupro de sua irmã Dina, os filhos de Jacó deram a Siquém e a seu pai Hemor uma resposta enganosa;', 13, b'0', 34, 1),
	(983, 'disseram-lhes: “Não podemos fazer uma coisa dessas, dar nossa irmã a um incircunciso. Seria para nós uma afronta.', 14, b'0', 34, 1),
	(984, 'Daremos nosso consentimento só com a condição de que vos torneis como nós, circuncidando todos os vossos homens.', 15, b'0', 34, 1),
	(985, 'Então poderemos dar nossas filhas e tomar as vossas, habitar juntos e formar um só povo.', 16, b'0', 34, 1),
	(986, 'Mas se não consentirdes em vos circuncidar, levaremos nossa filha conosco”.', 17, b'0', 34, 1),
	(987, 'Estas palavras agradaram a Hemor e a Siquém filho de Hemor.', 18, b'0', 34, 1),
	(988, 'O jovem não tardou em cumprir a exigência, tão enamorado estava da filha de Jacó, e por ser o mais respeitado da família do pai.', 19, b'0', 34, 1),
	(989, '20 Hemor e Siquém foram até às portas da cidade e falaram com os concidadãos:', 20, b'0', 34, 1),
	(990, '“Essa gente está em paz conosco. Que se estabeleçam no país e o percorram livremente. Sem dúvida a terra é bastante espaçosa. Tomaremos as suas filhas para mulheres e lhes daremos as nossas.', 21, b'0', 34, 1),
	(991, 'Mas eles só consentem em morar conosco e formar um só povo sob a condição de todos os homens se circuncidarem, assim como eles são circuncidados.', 22, b'0', 34, 1),
	(992, 'Os rebanhos, os bens e todos os animais domésticos serão assim nossos. Basta lhes darmos o consentimento e eles habitarão conosco”.', 23, b'0', 34, 1),
	(993, 'Todos os que freqüentavam a assembléia da cidade atenderam a Hemor e Siquém, e todos foram circuncidados.', 24, b'0', 34, 1),
	(994, 'No terceiro dia, quando ainda sofriam as dores, os dois filhos de Jacó, Simeão e Levi, que eram irmãos de Dina, penetraram tranqüilamente na cidade de espada em punho e mataram todos os homens.', 25, b'0', 34, 1),
	(995, 'Passaram a fio de espada Hemor e Siquém, tiraram Dina da casa de Siquém e saíram.', 26, b'0', 34, 1),
	(996, 'Então os outros filhos de Jacó lançaram-se sobre os cadáveres e saquearam a cidade por terem desonrado a irmã.', 27, b'0', 34, 1),
	(997, 'Levaram consigo as ovelhas, os bois, os jumentos, tudo o que havia na cidade e nos campos.', 28, b'0', 34, 1),
	(998, 'Levaram cativas todas as crianças e mulheres e pilharam todas as riquezas, tudo o que havia nas casas.', 29, b'0', 34, 1),
	(999, 'Jacó disse a Simeão e a Levi: “Tornastes minha vida difícil, fazendo-me odiado dos cananeus e dos fereseus, que habitam esta terra. Tenho poucos homens. Quando se unirem contra mim para atacar, acabarão comigo e com minha família”.', 30, b'0', 34, 1),
	(1000, 'Eles responderam: “Acaso nossa irmã devia ser tratada como uma prostituta?”', 31, b'0', 34, 1),
	(1001, 'Deus disse a Jacó: “Levanta-te, sobe a Betel para morar ali. Ergue ali um altar ao Deus que te apareceu quando estavas fugindo de teu irmão Esaú”.', 1, b'0', 35, 1),
	(1002, 'Jacó disseà sua família e a todos os que estavam com ele: “Eliminai todos os deuses estranhos que houver entre vós. Purificai-vos, trocai  vossas roupas.', 2, b'0', 35, 1),
	(1003, 'Vamos subir a Betel e erguer ali um altar ao Deus que me ouviu no dia de minha angústia e esteve comigo durante a viagem que fiz”.', 3, b'0', 35, 1),
	(1004, 'Eles entregaram a Jacó todos os deuses estranhos que tinham em seu poder e os brincos que levavam nas orelhas, e Jacó enterrou tudo debaixo do carvalho que fica perto da cidade de Siquém.', 4, b'0', 35, 1),
	(1005, 'Quando partiram, Deus espalhou o terror sobre as cidades da redondeza, de modo que não se atreveram a perseguir os filhos de Jacó.', 5, b'0', 35, 1),
	(1006, 'Assim, com toda sua gente, Jacó chegou a Luza (que é Betel), na terra de Canaã.', 6, b'0', 35, 1),
	(1007, 'Ergueu ali um altar e deu ao lugar o nome de “Deus de Betel”, porque ali Deus lhe aparecera quando estava fugindo de seu irmão.', 7, b'0', 35, 1),
	(1008, 'Por aquele tempo morreu Débora, a ama de Rebeca, e foi enterrada ao sopé de Betel, à sombra de um carvalho que foi chamado Carvalho do Pranto.', 8, b'0', 35, 1),
	(1009, 'Depois que Jacó voltou de Padã-Aram, Deus apareceu-lhe de novo e o abençoou,', 9, b'0', 35, 1),
	(1010, 'dizendo: “Teu nome é Jacó, mas já não serás chamado Jacó; teu nome será Israel”. E deu-lhe o nome de Israel.', 10, b'0', 35, 1),
	(1011, 'E Deus lhe falou:“ Eu sou o Deus Poderoso: sê fecundo e multiplica- te. De ti sairá uma nação, uma comunidade de nações, e de tuas entranhas sairão reis.', 11, b'0', 35, 1),
	(1012, 'A terra que dei a Abraão e a Isaac darei a ti e a tua descendência”.', 12, b'0', 35, 1),
	(1013, 'Deus se retirou de junto dele, do lugar onde lhe tinha falado.', 13, b'0', 35, 1),
	(1014, 'Então Jacó levantou uma coluna sagrada no lugar onde Deus lhe havia falado, uma coluna de pedra sobre a qual fez uma libação e derramou óleo.', 14, b'0', 35, 1),
	(1015, 'Ao lugar onde Deus tinha falado com ele deu o nome de Betel. Nasce Benjamim, morrem Raquel e Isaac', 15, b'0', 35, 1),
	(1016, 'Em seguida partiram de Betel. Faltava pouco para chegarem a Éfrata, quando Raquel deu à luz, num parto muito difícil.', 16, b'0', 35, 1),
	(1017, 'Entre as angústias do parto disse-lhe a parteira: “Coragem, que este também é um menino!”', 17, b'0', 35, 1),
	(1018, 'Estando prestes a morrer, já agonizante, ela deu-lhe o nome de Benoni, filho da dor, mas o pai o chamou Benjamim, filho da mão direita.', 18, b'0', 35, 1),
	(1019, 'Raquel morreu e foi sepultada no caminho de Éfrata (que é Belém).', 19, b'0', 35, 1),
	(1020, 'Jacó levantou sobre a tumba de Raquel uma coluna sagrada. É a coluna sagrada da tumba de Raquel, que existe até hoje.', 20, b'0', 35, 1),
	(1021, 'Israel partiu e armou as tendas para além de Migdal-Eder, Torre do Rebanho.', 21, b'0', 35, 1),
	(1022, 'Foi quando Israel morava nessa região, que Rúben dormiu com Bala, a concubina de seu pai, e Israel ficou sabendo. Os filhos de Jacó eram doze:', 22, b'0', 35, 1),
	(1023, 'Filhos de Lia: Rúben, o primogênito de Jacó, Simeão, Levi, Judá, Issacar e Zabulon.', 23, b'0', 35, 1),
	(1024, 'Os filhos de Raquel: José e Benjamim.', 24, b'0', 35, 1),
	(1025, 'Filhos de Bala, a escrava de Raquel: Dã e Neftali.', 25, b'0', 35, 1),
	(1026, 'Filhos de Zelfa, a escrava de Lia: Gad e Aser. São esses os filhos de Jacó que nasceram em Padã-Aram.', 26, b'0', 35, 1),
	(1027, 'Jacó foi para junto de seu pai Isaac, para Mambré, em Cariat-Arbe (que é Hebron), onde haviam morado Abraão e Isaac.', 27, b'0', 35, 1),
	(1028, 'Isaac viveu cento e oitenta anos', 28, b'0', 35, 1),
	(1029, 'e faleceu. Morreu e foi reunir-se aos antepassados, velho e com muitos anos. Os filhos Esaú e Jacó o sepultaram.', 29, b'0', 35, 1),
	(1030, 'Estes são os descendentes de Esaú (que é Edom).', 1, b'0', 36, 1),
	(1031, 'Esaú casou-se com mulheres cananéias: Ada, filha de Elon o heteu, e Oolibama, filha de Ana, filha de Sebeon, o heveu.', 2, b'0', 36, 1),
	(1032, 'Além dessas, tomou  Basemat, filha de Ismael, irmã de Nabaiot.', 3, b'0', 36, 1),
	(1033, 'Ada deu um filho a Esaú, Elifaz, e Basemat lhe deu Rauel.', 4, b'0', 36, 1),
	(1034, 'Oolibama lhe deu Jeús, Jalam e Coré. São esses os filhos de Esaú que nasceram na terra de Canaã.', 5, b'0', 36, 1),
	(1035, 'Esaú levou as mulheres, os filhos, as filhas e todas as pessoas de sua casa, o gado, todos os animais e os bens que havia adquirido na terra de Canaã, e foi para Seir, longe do irmão Jacó.', 6, b'0', 36, 1),
	(1036, 'Como tivessem muitos bens, não podiam habitar juntos,e a terra em que estavam migrando não bastava para os rebanhos.', 7, b'0', 36, 1),
	(1037, 'Assim, Esaú estabeleceu-se na montanha de Seir. (Esaú é Edom.)', 8, b'0', 36, 1),
	(1038, 'Estes são os descendentes de Esaú, antepassado dos edomitas, na montanha de Seir.', 9, b'0', 36, 1),
	(1039, 'Eis os nomes dos filhos de Esaú: Elifaz filho de Ada, mulher de Esaú, e Rauel filho de Basemat, mulher de Esaú.', 10, b'0', 36, 1),
	(1040, 'Os filhos de Elifaz foram: Temã, Omar, Sefo, Gatam e Cenez.', 11, b'0', 36, 1),
	(1041, 'Tamna foi concubina de Elifaz filhode Esaú, e lhe deu Amalec. Eles são netos de Ada, mulher de Esaú.', 12, b'0', 36, 1),
	(1042, 'Filhos de Rauel: Naat, Zara, Sama e Meza. Eles são netos de Basemat, mulher de Esaú.', 13, b'0', 36, 1),
	(1043, 'Os filhos de Oolibama, mulher de Esaú, filha de Ana filho de Sebeon, foram Jeús, Jalam e Coré.', 14, b'0', 36, 1),
	(1044, 'Estes são os chefes de tribo dos filhos de Esaú. Filhos de Elifaz, primogênito de Esaú: o chefe Temã, o chefe Omar, o chefe Sefo, o chefe Cenez,', 15, b'0', 36, 1),
	(1045, 'chefe Coré, o chefe Gatam, o chefe Amalec. São esses os chefes de Elifaz na terra de Edom; são netos de Ada.', 16, b'0', 36, 1),
	(1046, 'Filhos de Rauel filho de Esaú: o chefe Naat, o chefe Zara, o chefe Sama e o chefe Meza. São esses os chefes de Rauel na terra de Edom; são netos de Basemat, mulher de Esaú.', 17, b'0', 36, 1),
	(1047, 'Filhos de Oolibama, mulher de Esaú: o chefe Jeús, o chefe Jalam e o chefe Coré. São esses os chefes de Oolibama, filha de Aná e mulher de Esaú.', 18, b'0', 36, 1),
	(1048, 'Todos esses são descendentes de Esaú e chefes de sua gente,isto é, de Edom.', 19, b'0', 36, 1),
	(1049, 'Estes são os filhos de Seir, o hurrita, que habitava o país: Lotã, Sobal, Sebeon, Ana,', 20, b'0', 36, 1),
	(1050, 'Dison, Eser, Disã; eles são os chefes dos hurritas, descendentes de Seir, na terra de Edom.', 21, b'0', 36, 1),
	(1051, 'Os filhos de Lotã são Hori e Hemã, e a irmã de Lotã é Tamna.', 22, b'0', 36, 1),
	(1052, 'Estes são os filhos de Sobal: Alvã, Manaat, Ebal, Sefo e Onam.', 23, b'0', 36, 1),
	(1053, 'Estes são os filhos de Sebeon: Aia e Ana. Foi este Aná que achou no deserto os mananciais de água quente enquanto apascentava os jumentos de seu pai Sebeon.', 24, b'0', 36, 1),
	(1054, 'Estes são os filhos de Ana: Dison e Oolibama filha de Ana.', 25, b'0', 36, 1),
	(1055, 'Estes são os filhos de Dison: Hamdã, Esebã, Jetrã e Carã.', 26, b'0', 36, 1),
	(1056, 'Estes são os filhos de Eser: Balaã, Zavã e Acã.', 27, b'0', 36, 1),
	(1057, 'Estes são os filhos de Disã: Hus e Aran.', 28, b'0', 36, 1),
	(1058, 'São esses os chefes dos hurritas: o chefe Lotã, o chefe Sobal, o chefe Sebeon, o chefe Ana,', 29, b'0', 36, 1),
	(1059, 'O chefe Dison, o chefe Eser, o chefe Disã. Todos esses são chefes dos hurritas, segundo as tribos, na terra de Seir.', 30, b'0', 36, 1),
	(1060, 'Estes são os reis que reinaram na terra de Edom antes que os israelitas tivessem um rei.', 31, b'0', 36, 1),
	(1061, 'Em Edom reinou Bela, filho de Beor, e o nome de sua cidade era Danaba.', 32, b'0', 36, 1),
	(1062, 'Bela morreu, e Jobab, filho de Zara, de Bosra, sucedeu-lhe no trono.', 33, b'0', 36, 1),
	(1063, 'Morreu Jobab e sucedeu-lhe no trono Husam, da terra dos temanitas.', 34, b'0', 36, 1),
	(1064, 'Morreu Husam e sucedeu-lhe no trono Adad filho de Badad, que derrotou Madiã no campo de Moab; o nome de sua cidade era Avit.', 35, b'0', 36, 1),
	(1065, 'Morreu Adad e sucedeulhe Semla, de Masreca.', 36, b'0', 36, 1),
	(1066, 'Morreu Semla e sucedeu-lhe Saul, de Reobot, perto do rio.', 37, b'0', 36, 1),
	(1067, 'Morreu Saul e sucedeu-lhe Baalanã, filho de Acobor.', 38, b'0', 36, 1),
	(1068, 'Morreu Baalanã, filho de Acobor, e sucedeu-lhe no trono Adad; o nome de sua cidade era Fau, e sua mulher se chamava Meetabel filha de Matred, a filha de Mezaab.', 39, b'0', 36, 1),
	(1069, 'Estes são os nomes dos chefes de Esaú segundo suas famílias, seus territórios e nomes: o chefe Tamna, o chefe  Alva, o chefe Jetet,', 40, b'0', 36, 1),
	(1070, 'o chefe Oolibama, o chefe Ela, o chefe Finon,', 41, b'0', 36, 1),
	(1071, 'o chefe Cenez, o chefe Temã, o chefe Mabsar,', 42, b'0', 36, 1),
	(1072, 'o chefe Magdiel, o chefe Iram. São esses os chefes de Edom segundo as regiões que ocupam na terra que lhes pertence. Esse, pois, é Esaú, o antepassado de Edom.', 43, b'0', 36, 1),
	(1073, 'Jacó foi morar na terra de Canaã, onde seu pai tinha vivido como migrante.', 1, b'0', 37, 1),
	(1074, 'Segue aqui a história dos descendentes de Jacó. Quando tinha dezessete anos, José apascentava as ovelhas com os irmãos, como ajudante dos filhos de Bala e Zelfa, mulheres de seu pai. E José falou ao pai da péssima fama deles.', 2, b'0', 37, 1),
	(1075, 'Ora, Israel amava mais a José do que a todos os outros filhos, porque lhe tinha nascido na velhice; e por isso mandou fazer para ele uma túnica de mangas compridas.', 3, b'0', 37, 1),
	(1076, 'Os irmãos, percebendo que o pai o amava mais do que a todos eles, odiavam-no e já não podiam falar-lhe pacificamente.', 4, b'0', 37, 1),
	(1077, 'Ora, José teve um sonho e contou-o aos irmãos, que o ficaram odiando ainda mais.', 5, b'0', 37, 1),
	(1078, 'Disse-lhes ele: “Escutai o sonho que tive:', 6, b'0', 37, 1),
	(1079, 'Estávamos no campo atando feixes de trigo.De repente o meu feixe se levantou e ficou de pé, enquanto os vossos o cercaram e se prostraram diante do meu”.', 7, b'0', 37, 1),
	(1080, 'Os irmãos lhe disseram: “Será que irás mesmo reinar sobre nós e dominar-nos?” E odiavam-no mais ainda por causa de seus sonhos e de suas palavras.', 8, b'0', 37, 1),
	(1081, 'José teve ainda outro sonho, que contou aos irmãos. “Tive outro sonho”, disse, “e vi que o sol, a lua e onze estrelas se inclinavam diante de mim”.', 9, b'0', 37, 1),
	(1082, 'Quando contou o sonho ao pai e aos irmãos, o pai o repreendeu, dizendo:“ Que sonho é esse que sonhaste? Acaso vamos prostrar-nos por terra diante de ti, eu, tua mãe e teus irmãos?”', 10, b'0', 37, 1),
	(1083, 'Os irmãos o invejavam, mas o pai guardou o assunto. José vendido pelos irmãos.', 11, b'0', 37, 1),
	(1084, 'Ora, como os irmãos de José tinham ido apascentar os rebanhos do pai em Siquém,', 12, b'0', 37, 1),
	(1085, 'Israel disse a José: “Teus irmãos devem estar com os rebanhos em Siquém. Vem! Vou enviar-te a eles”. Ele respondeu-lhe: “Aqui estou”.', 13, b'0', 37, 1),
	(1086, 'Disse-lhe Israel: “Vai ver se teus irmãos e os rebanhos estão passando bem e traze-me notícias”. Assim o enviou do vale de Hebron, e José chegou a Siquém.', 14, b'0', 37, 1),
	(1087, 'Um homem o encontrou vagando pelo campo e perguntou: “Que procuras?”', 15, b'0', 37, 1),
	(1088, 'Ele respondeu: “Estou procurando meus irmãos. Dize-me, por favor, onde estão apascentando”.', 16, b'0', 37, 1),
	(1089, 'O homem respondeu: “Eles foram embora daqui, pois os ouvi dizer:‘ Vamos para Dotain’”. José foi à procura dos irmãos e encontrou-os em Dotain.', 17, b'0', 37, 1),
	(1090, 'Eles, porém, tendo-o o visto de longe, antes que se aproximasse, tramaram a sua morte.', 18, b'0', 37, 1),
	(1091, 'Disseram uns aos outros: “Aí vem o sonhador!', 19, b'0', 37, 1),
	(1092, 'Vamos matá-lo e lançá-lo numa cisterna. Depois diremos que um animal feroz o devorou. Assim veremos de que lhe servem os sonhos”.', 20, b'0', 37, 1),
	(1093, 'Rúben, porém, ouvindo isto, tentou livrá-lo de suas mãos e disse: “Não lhe tiremos a vida!”', 21, b'0', 37, 1),
	(1094, 'E acrescentou: “Não derrameis sangue. Lançai-o naquela cisterna no deserto, mas não levanteis a mão contra ele”. Dizia isso porque queria livrá-lo das  mãos deles e devolvê-lo ao pai.', 22, b'0', 37, 1),
	(1095, 'Assim que José se aproximou dos irmãos, estes o despojaram da túnica, a túnica de mangas compridas que trazia,', 23, b'0', 37, 1),
	(1096, 'agarraram-no e o lançaram numa cisterna que estava sem água.', 24, b'0', 37, 1),
	(1097, 'Depois sentaram-se para comer. Levantando os olhos, avistaram uma caravana de ismaelitas, que se aproximava, proveniente de Galaad. Os camelos iam carregados de especiarias, bálsamo e resina, que transportavam para o Egito.', 25, b'0', 37, 1),
	(1098, 'E Judá disse aos irmãos: “Que proveito teríamos em matar nosso irmão e ocultar o crime?', 26, b'0', 37, 1),
	(1099, 'É melhor vendê-lo a esses ismaelitas. Não levantemos contra ele nossa mão, pois ele é nosso irmão, nossa carne”. E os irmãos concordaram.', 27, b'0', 37, 1),
	(1100, 'Ao passarem os comerciantes madianitas, os irmãos tiraram José da cisterna e por vinte moedas de prata o venderam aos ismaelitas, que o levaram para o Egito.', 28, b'0', 37, 1),
	(1101, 'Quando Rúben voltou à cisterna e não encontrou José, rasgou as vestes de dor.', 29, b'0', 37, 1),
	(1102, 'Voltando para junto dos irmãos disse: “O menino sumiu! E eu, para onde irei agora?”', 30, b'0', 37, 1),
	(1103, 'Então os irmãos tomaram a túnica de mangas compridas de José, mataram um cabrito e, embebendo-a de sangue,', 31, b'0', 37, 1),
	(1104, 'mandaram levar a túnica para o pai, dizendo: “Encontramos isso. Examina para ver se é ou não a túnica de teu filho”.', 32, b'0', 37, 1),
	(1105, 'Jacó reconheceu-a e disse: “É a túnica de meu filho. Um animal feroz devorou José, estraçalhou-o por inteiro”.', 33, b'0', 37, 1),
	(1106, 'Jacó rasgou as vestes de dor,  vestiu-se de luto e chorou a morte do filho por muitos dias.', 34, b'0', 37, 1),
	(1107, 'Todos os filhos e filhas vinham consolá- lo, mas ele recusava qualquer consolo, dizendo: “Em prantos descerei até meu filho no reino dos  mortos”. Assim o chorava o pai.', 35, b'0', 37, 1),
	(1108, 'Entretanto, os madianitas venderam José no Egito a Putifar, ministro do faraó e chefe da guarda.', 36, b'0', 37, 1),
	(1109, 'Por esse tempo, Judá desceu da montanha e, afastando-se dos irmãos, foi parar junto a um homem de Odolam, de nome Hira.', 1, b'0', 38, 1),
	(1110, 'Ao ver ali a filha de um cananeu chamado Sué, casou-se e viveu com ela.', 2, b'0', 38, 1),
	(1111, 'Ela concebeu e deu à luz um filho a quem chamou Her.', 3, b'0', 38, 1),
	(1112, 'Concebeu de novo e deu à luz outro filho, a quem chamou Onã.', 4, b'0', 38, 1),
	(1113, 'Tornou a conceber e deu à luz outro filho, a quem chamou Sela. Quando lhe nasceu este último, achava-se em Casib.', 5, b'0', 38, 1),
	(1114, 'Judá escolheu para Her, o primogênito, uma mulher chamada Tamar.', 6, b'0', 38, 1),
	(1115, 'Mas Her, primogênito de Judá, desagradou ao Senhor, e o Senhor o fez morrer.', 7, b'0', 38, 1),
	(1116, 'Então Judá disse a Onã: “Unete à mulher de teu irmão para cumprir a obrigação de cunhado e assegurar uma descendência para teu irmão”.', 8, b'0', 38, 1),
	(1117, 'Mas Onã sabia que o filho não seria seu; por isso, quando se juntava com a mulher do irmão, derramava o sêmen na terra, para não dar descendência ao irmão.', 9, b'0', 38, 1),
	(1118, 'proceder de Onã desagradou ao Senhor, que o fez morrer também.', 10, b'0', 38, 1),
	(1119, 'Judá disse, então, à sua nora Tamar: “Fica como viúva em casa de teu pai até que meu filho Sela se torne adulto”. Dizia isso pensando: “Não vá também ele morrer como os irmãos”. E assim, Tamar foi morar na casa do pai.', 11, b'0', 38, 1),
	(1120, 'Tempos depois morreu a filha de Sué, mulher de Judá. Passado o luto, Judá subiu com o amigo Hira de Odolam, para a tosquiadas ovelhas em Tamna.', 12, b'0', 38, 1),
	(1121, 'Comunicaram-no a Tamar, dizendo-lhe: “Olha,  teu sogro foi a Tamna para tosquiar as ovelhas”.', 13, b'0', 38, 1),
	(1122, 'Ela trocou suas vestes de viúva, cobriu-se com um véu e, assim disfarçada, sentou-se à entrada de Enaim, no caminho de Tamna, pois percebeu que Sela, apesar de já adulto, não lhe tinha sido dado por marido', 14, b'0', 38, 1),
	(1123, 'Judá a viu e a tomou por uma prostituta, pois estava com o rosto coberto.', 15, b'0', 38, 1),
	(1124, 'Dirigiu-se até ela e disse-lhe: “Deixa-me ir contigo”, pois não percebeu que era a nora. Ela perguntou: “Que me darás para te unires comigo?”', 16, b'0', 38, 1),
	(1125, 'Ele respondeu: “Vou mandar-te um cabrito do rebanho”. Ela lhe disse: “Só se me deres alguma coisa como garantia de que o mandarás”.', 17, b'0', 38, 1),
	(1126, '“Que garantia queres que te dê?” perguntou ele. Ela respondeu: “O teu sinete, o cordão e o bastão que trazes na mão”. Ele os deu e uniu-se com ela, que ficou grávida.', 18, b'0', 38, 1),
	(1127, 'Depois ela se levantou e foi embora, tirouo véu e retomou as vestes de viúva.', 19, b'0', 38, 1),
	(1128, 'Mais tarde, Judá mandou o cabrito por intermédio do amigo, o odolamita, para que retirasse a garantia das mãos da mulher. Mas ele não a encontrou.', 20, b'0', 38, 1),
	(1129, 'Perguntou aos homens do lugar: “Onde está a prostituta que ficava esperando à beira do caminho em Enaim?” Mas eles responderam: “Nunca houve prostituta nesse lugar”.', 21, b'0', 38, 1),
	(1130, 'Hira voltou e disse a Judá: “Não a encontrei, e os homens do lugar me disseram que ali nunca houve prostituta alguma”.', 22, b'0', 38, 1),
	(1131, 'Judá respondeu: “Pois que fique por isso, para não cairmos no ridículo, eu mandando o cabrito prometido e tu não encontrando a prostituta”.', 23, b'0', 38, 1),
	(1132, 'Passados uns três meses, comunicaram a Judá: “Tua nora Tamar prostituiu-se e, em conseqüência, está grávida”. Judá respondeu: “Trazei-a para fora para que seja queimada”.', 24, b'0', 38, 1),
	(1133, 'Quando a levavam para fora, Tamar mandou dizer ao sogro: “O homem a quem pertencem estas coisas deixou-me grávida. Verifica de quem são o sinete, o cordão e o bastão”.', 25, b'0', 38, 1),
	(1134, 'Judá os reconheceu e disse: “Ela foi mais honesta do que eu. É que não lhe dei meu filho Sela para marido”. E não tornou a conhecê-la.', 26, b'0', 38, 1),
	(1135, 'Quando chegou a hora do parto, viram que ela teria gêmeos.', 27, b'0', 38, 1),
	(1136, 'Durante o parto, um deles pôs uma das mãos para fora, e a parteira pegou-a e atou-lhe um fio vermelho, dizendo: “Este foi o primeiro a sair”.', 28, b'0', 38, 1),
	(1137, 'Mas ele recolheu a mão, e saiu o irmão: “Que brecha abriste para ti!” disse ela, e lhe deu o nome de Farés.', 29, b'0', 38, 1),
	(1138, 'Depois saiu o irmão, com o fio atado à mão, e ela o chamou Zara.', 30, b'0', 38, 1),
	(1139, 'José foi levado para o Egito. Putifar, um egípcio, ministro do faraó e chefe da guarda do palácio, comprou-o dos ismaelitas que o tinham levado para lá.', 1, b'0', 39, 1),
	(1140, 'Mas o Senhor estava com José, e ele se tornou um homem bem sucedido, morando na casa de seu Senhor egípcio.', 2, b'0', 39, 1),
	(1141, 'O patrão notou que o Senhor estava com ele e fazia prosperar tudo o que empreendia.', 3, b'0', 39, 1),
	(1142, 'José conquistou as boas graças do patrão e ficou a seu serviço. O patrão o nomeou administrador de sua casa, confiando-lhe todos os seus bens.', 4, b'0', 39, 1),
	(1143, 'E a partir desse momento, o Senhor abençoou, em atenção a José, a casa do egípcio e derramou sua bênção sobre tudo o que possuía em casa e no campo.', 5, b'0', 39, 1),
	(1144, 'O patrão entregou tudo nas mãos de José e não se preocupava com coisa alguma, a não ser com o que comia. Ora, José tinha um belo porte e era  bonito de rosto.', 6, b'0', 39, 1),
	(1145, 'Aconteceu, depois, que a mulher de seu patrão pôs nele os olhos e lhe disse: “Dorme comigo”.', 7, b'0', 39, 1),
	(1146, 'Ele recusou, dizendo à mulher de seu patrão: “Em verdade, meu Senhor não me pede contas do que há na casa, confiando-me todos os bens.', 8, b'0', 39, 1),
	(1147, 'Ele próprio não é mais importante do que eu nesta casa. Nada me proibiu senão a ti, por seres sua mulher. Como poderia eu fazer tamanha maldade pecando contra Deus!”', 9, b'0', 39, 1),
	(1148, 'E embora ela insistisse diariamente com José, ele se recusou a dormir ou ficar com ela.', 10, b'0', 39, 1),
	(1149, 'Certo dia, José entrou na casa para seus afazeres, e nenhum dos domésticos estava em casa.', 11, b'0', 39, 1),
	(1150, 'A mulher agarrou-o pelo manto e disse: “Dorme comigo”. Mas ele largou-lhe nas mãos o manto e fugiu correndo para fora.', 12, b'0', 39, 1),
	(1151, 'Vendo que lhe tinha deixado nas mãos o manto e escapado para fora,', 13, b'0', 39, 1),
	(1152, 'ela se pôs a gritar e a chamar os empregados, dizendo: “Vede! Trouxeram-nos esse hebreu para abusar de nós. Ele me abordou para dormir comigo, mas eu comecei a gritar em alta voz.', 14, b'0', 39, 1),
	(1153, 'Quando percebeu que levantei a voz e gritei, largou o manto aqui comigo e fugiu correndo para fora”.', 15, b'0', 39, 1),
	(1154, 'A mulher ficou com o manto de José até o marido voltar para casa.', 16, b'0', 39, 1),
	(1155, 'Então falou-lhe nos mesmos termos, dizendo: “Esse escravo hebreu que nos trouxeste abordou-me querendo abusar de mim.', 17, b'0', 39, 1),
	(1156, 'Quando levantei a voz e comecei a gritar, largou o manto aqui comigo e fugiu para fora”.', 18, b'0', 39, 1),
	(1157, 'Ao ouvir o que a mulher contava – “Assim é que me tratou teu escravo” – o marido ficou furioso.', 19, b'0', 39, 1),
	(1158, 'Mandou prender José e lançou-o no cárcere onde se guardavam os presos do rei. E José ficou no cárcere.', 20, b'0', 39, 1),
	(1159, 'Mas o Senhor estava com José e concedeu-lhe seu favor, atraindo-lhe a simpatia do carcereiro-chefe.', 21, b'0', 39, 1),
	(1160, 'Este confiou a seus cuidados todos os que se achavam presos. Era ele que organizava tudo quanto lá se fazia.', 22, b'0', 39, 1),
	(1161, 'O carcereiro-chefe não se preocupava com coisa alguma que lhe fora confiada, porque o Senhor estava com José e fazia prosperar tudo o que fazia.', 23, b'0', 39, 1),
	(1162, 'Sucedeu, depois, que o copeiro e o padeiro do rei do Egito ofenderam seu Senhor, o rei do Egito.', 1, b'0', 40, 1),
	(1163, 'O faraó encolerizou-se contra os dois ministros, o chefe dos copeiros e o chefe dos padeiros,', 2, b'0', 40, 1),
	(1164, 'e os lançou no cárcere da casa do chefe da guarda – o cárcere onde José estava preso.', 3, b'0', 40, 1),
	(1165, 'O chefe da guarda indicou-lhes José para servi-los. Passaram algum tempo no cárcere.', 4, b'0', 40, 1),
	(1166, 'Certa noite, o chefe dos copeiros e o chefe dos padeiros do rei do Egito, que estavam presos no cárcere, tiveram um sonho, cada qual com significado diferente.', 5, b'0', 40, 1),
	(1167, 'Ao entrar, pela manhã, José os encontrou com o rosto abatido.', 6, b'0', 40, 1),
	(1168, 'Perguntou então aos ministros do faraó, que com ele estavam presos na casa de seu Senhor: “Por que estais hoje com o rosto mais triste?”', 7, b'0', 40, 1),
	(1169, 'Eles responderam: “Tivemos um sonho e não há quem o interprete”. José disse: “Por acaso não cabe a Deus a interpretação dos sonhos? Contai-me os sonhos”.', 8, b'0', 40, 1),
	(1170, 'O chefe dos copeiros contou o sonho a José, dizendo: “No meu sonho, vi diante de mim uma videira', 9, b'0', 40, 1),
	(1171, 'com três ramos, e, logo que as folhas saíam, florescia e as uvas amadureciam.', 10, b'0', 40, 1),
	(1172, 'Como eu segurava em minhas mãos a taça do faraó, colhi os cachos, espremi as uvas na taça do faraó e a pus em suas  mãos”.', 11, b'0', 40, 1),
	(1173, 'José lhe disse: “Este é o significado do sonho: os três ramos são três dias.', 12, b'0', 40, 1),
	(1174, 'Dentro de três dias o faraó levantará tua cabeça: ele te reconduzirá a teu cargo, e porás a taça do faraó em suas mãos, como antes o fazias, quando eras copeiro.', 13, b'0', 40, 1),
	(1175, 'Mas, quando as coisas te correrem bem, lembra-te de mim e faze-me o favor de me recomendar ao faraó para que me tire desta prisão.', 14, b'0', 40, 1),
	(1176, 'Com efeito, fui seqüestrado da terra dos hebreus e, mesmo aqui, nada fiz para me trancarem na prisão”.', 15, b'0', 40, 1),
	(1177, 'Quando o chefe dos padeiros viu que José explicava bem o sonho, disse-lhe: “Eu também tive um sonho: carregava sobre a cabeça três balaios de pão branco.', 16, b'0', 40, 1),
	(1178, 'No balaio de cima havia toda sorte de guloseimas preparadas pelos padeiros para o faraó, e as aves comiam do balaio que eu levava sobre a cabeça”.', 17, b'0', 40, 1),
	(1179, 'José respondeu: “Este é o significado: os três balaios são três dias.', 18, b'0', 40, 1),
	(1180, 'Dentro de três dias o faraó levantará tua cabeça: ele te pendurará numa árvore para as aves comerem tua carne”.', 19, b'0', 40, 1),
	(1181, 'Ora, realmente, ao terceiro dia o faraó celebrava o aniversário e ofereceu um banquete a todos os servidores e, de fato, “levantou a cabeça” do chefe dos copeiros e do chefe dos padeiros entre os servidores:', 20, b'0', 40, 1),
	(1182, 'reconduziu o chefe dos copeiros ao cargo de servir a taça ao faraó;', 21, b'0', 40, 1),
	(1183, 'e quanto ao chefe dos padeiros, mandou enforcá-lo, conforme a interpretação que José lhe havia dado.', 22, b'0', 40, 1),
	(1184, 'Mas o chefe dos copeiros não pensou mais em José; esqueceu-o.', 23, b'0', 40, 1),
	(1185, 'Passados dois anos, o faraó teve um sonho: achava-se às margens do rio Nilo', 1, b'0', 41, 1),
	(1186, 'e viu subir dele sete vacas bonitas e gordas para pastar na várzea.', 2, b'0', 41, 1),
	(1187, 'Mas atrás delas subiam do rio outras sete vacas feias e magras, que se colocaram junto às sete que já estavam à margem do rio.', 3, b'0', 41, 1),
	(1188, 'As vacas feias e magras devoraram as sete vacas bonitas e gordas. Nisso o faraó acordou.', 4, b'0', 41, 1),
	(1189, 'Depois adormeceu e sonhou pela segunda vez: viu sete espigas bem graúdas e belas saindo do mesmo caule.', 5, b'0', 41, 1),
	(1190, 'Mas atrás delas brotaram sete espigas chochas, ressequidas pelo vento leste.', 6, b'0', 41, 1),
	(1191, 'As sete espigas chochas engoliram as sete espigas graúdas e cheias. Então o faraó acordou e viu que era um sonho.', 7, b'0', 41, 1),
	(1192, 'Pela manhã, com o espírito perturbado, mandou chamar todos os adivinhos e sábios do Egito. Contou-lhes os sonhos, mas não houve quem os interpretasse ao faraó.', 8, b'0', 41, 1),
	(1193, 'Então o copeiro-mor falou ao faraó e disse: “Hoje devo recordar minha falta.', 9, b'0', 41, 1),
	(1194, 'O faraó esteve irritado contra os servos e os mandou encarcerar na casa do chefe da guarda, a mim e ao chefe dos padeiros.', 10, b'0', 41, 1),
	(1195, 'Na mesma noite, ambos tivemos um sonho, cada qual com um sentido diferente.', 11, b'0', 41, 1),
	(1196, 'Havia lá conosco um jovem hebreu, escravo do chefe da guarda. Contamos os sonhos, e ele os interpretou, dando a cada um a sua interpretação.', 12, b'0', 41, 1),
	(1197, 'Aconteceu tal como nos interpretou: eu fui reconduzido ao cargo e o outro foi pendurado”.', 13, b'0', 41, 1),
	(1198, 'O faraó mandou chamar José, e depressa o tiraram da prisão. José barbeou-se, mudou de roupa e apresentou-se ao faraó.', 14, b'0', 41, 1),
	(1199, 'O faraó disse a José: “Tive um sonho, e não há quem o interprete. Ouvi dizer que, apenas ouves um sonho, logo o interpretas”.', 15, b'0', 41, 1),
	(1200, 'José respondeu ao faraó: “Não eu, mas Deus dará uma resposta plausível ao faraó”.', 16, b'0', 41, 1),
	(1201, 'Então o faraó contou a José: “Em meu sonho estava de pé, às margens do rio,', 17, b'0', 41, 1),
	(1202, 'e vi subir do rio sete vacas gordas, de bela aparência,que se puseram a pastar na várzea.', 18, b'0', 41, 1),
	(1203, 'E logo atrás delas subiram outras sete vacas miseráveis, de aparência muito feia, tão magras e feias como nunca tinha visto em todo o Egito.', 19, b'0', 41, 1),
	(1204, 'E as vacas magras e feias devoraram as sete primeiras vacas gordas,', 20, b'0', 41, 1),
	(1205, 'mas entraram-lhes no ventre sem que se notasse diferença alguma, pois seu aspecto continuou tão ruim como antes. Nisto acordei.', 21, b'0', 41, 1),
	(1206, 'Depois vi em sonho que saíam do mesmo caule sete espigas cheias e bonitas.', 22, b'0', 41, 1),
	(1207, 'Atrás delas surgiram sete espigas mirradas, chochas e ressequidas pelo vento leste.', 23, b'0', 41, 1),
	(1208, 'E as sete espigas chochas engoliram as sete espigas bonitas. Contei o sonho aos adivinhos, mas não há quem me revele o sentido”.', 24, b'0', 41, 1),
	(1209, 'José disse ao faraó: “O sonho do faraó constitui uma unidade. Deus deu a conhecer ao faraó o que vai fazer.', 25, b'0', 41, 1),
	(1210, 'As sete vacas bonitas são sete anos e as sete espigas bonitas são sete anos. Pois o sonho constitui uma unidade.', 26, b'0', 41, 1),
	(1211, 'As sete vacas magras e feias que subiam atrás das outras são outros sete anos, e as sete espigas chochas e ressequidas pelo vento leste correspondem a sete anos de fome.', 27, b'0', 41, 1),
	(1212, 'É como eu disse ao faraó: Deus lhe fez ver o que vai fazer.', 28, b'0', 41, 1),
	(1213, 'Virão sete anos de grande fartura em todo o Egito.', 29, b'0', 41, 1),
	(1214, 'Depois virão sete anos de carestia, que farão esquecer toda a fartura na terra do Egito, e a fome acabará com o país.', 30, b'0', 41, 1),
	(1215, 'Esquecerão que houve fartura no país, por causa da fome que seguirá, pois será terrível.', 31, b'0', 41, 1),
	(1216, 'A repetição do sonho por duas vezes significa que da parte de Deus o fato já está decretado e que ele se apressará em executá-lo.', 32, b'0', 41, 1),
	(1217, 'Portanto, o faraó procure um homem inteligente e sábio e o ponha à frente do Egito.', 33, b'0', 41, 1),
	(1218, 'Nomeie o faraó fiscais pelo país e recolha a quinta parte das colheitas do Egito durante os sete anos de fartura.', 34, b'0', 41, 1),
	(1219, 'Reúnam todos os víveres dos anos bons que virão e, por ordem do faraó, armazenem o trigo e o guardem como provisão nas cidades.', 35, b'0', 41, 1),
	(1220, 'Esses mantimentos servirão de provisão ao país para os sete anos de fome que virão sobre o Egito, a fim de que o país não pereça de fome”.', 36, b'0', 41, 1),
	(1221, 'Essas palavras agradaram ao faraó e a todos os seus servidores.', 37, b'0', 41, 1),
	(1222, 'E o faraó disse aos servidores: “Poderíamos por acaso encontrar outro homem como este, dotado do espírito de Deus?”', 38, b'0', 41, 1),
	(1223, 'E disse para José: “Uma vez que Deus te revelou essas coisas, não há pessoa tão inteligente e tão sábia como tu.', 39, b'0', 41, 1),
	(1224, 'Serás tu quem governará o meu palácio; a teu comando, todo o povo te obedecerá. Só pelo trono serei maior do que tu”.', 40, b'0', 41, 1),
	(1225, 'E o faraó disse ainda a José: “Olha, eu te ponho à frente de todo o Egito”.', 41, b'0', 41, 1),
	(1226, 'O faraó tirou o seu anel da mão e o colocou na mão de José. Mandou vesti-lo com vestes de linho fino e lhe pôs ao pescoço um colar de ouro.', 42, b'0', 41, 1),
	(1227, 'Depois o fez subir no seu segundo carro e proclamar à sua frente: “De joelhos!” Assim, José foi posto à frente de todo o Egito.', 43, b'0', 41, 1),
	(1228, 'O faraó disse a José: “Eu sou o faraó, mas sem ti ninguém moverá a mão nem o pé em todo o Egito”.', 44, b'0', 41, 1),
	(1229, 'O faraó deu a José o nome de  Safenat Fanec e deu-lhe em casamento Asenet, filha de Putifar, sacerdote de On. José viajou por todo a terra do Egito.', 45, b'0', 41, 1),
	(1230, 'José tinha trinta anos quando se pôs a serviço do faraó, rei do Egito. José saiu da presença do faraó e percorreu todo o Egito.', 46, b'0', 41, 1),
	(1231, 'Durante os sete anos de fartura o país conheceu grande fertilidade.', 47, b'0', 41, 1),
	(1232, 'José recolheu a produção dos sete anos em que houve fartura no Egito e armazenou-a nas cidades, depositando em cada uma a produção dos campos que a rodeavam.', 48, b'0', 41, 1),
	(1233, 'José chegou a reunir trigo em tamanha quantidade como as areias do mar, de maneira que desistiu de contá-lo, porque ultrapassava toda medida.', 49, b'0', 41, 1),
	(1234, 'Antes de chegar o primeiro ano da fome, José teve dois filhos com Asenet, filha de Putifar, sacerdote de On.', 50, b'0', 41, 1),
	(1235, 'Ao primeiro deu o nome de Manassés, pois dizia: “Deus me fez esquecer todos os meus sofrimentos e a família de meu pai”.', 51, b'0', 41, 1),
	(1236, 'Ao segundo chamou Efraim, dizendo: “Deus tornou-me fecundo na terra de minha aflição”.', 52, b'0', 41, 1),
	(1237, 'Terminados os sete anos de fartura no Egito,', 53, b'0', 41, 1),
	(1238, 'começaram a vir os sete anos de fome, como José havia dito. Em todos os países grassava a fome, mas no Egito inteiro havia o que comer.', 54, b'0', 41, 1),
	(1239, 'E quando também todo o Egito começou a sentir fome, o povo se pôs a clamar ao faraó, pedindo pão. O faraó disse à população: “Dirigi-vos a José e fazei o que ele vos disser”.', 55, b'0', 41, 1),
	(1240, 'Quando a fome se estendeu a todo o país, José abriu todos os armazéns e começou a vender o trigo aos egípcios, pois a fome se agravara também no Egito.', 56, b'0', 41, 1),
	(1241, 'De toda a terra vinham ao Egito comprar alimento de José, pois a fome era dura em toda a terra.', 57, b'0', 41, 1),
	(1242, 'Ao ver que havia cereais no Egito, Jacó disse aos filhos: “Por que ficais aí parados olhando uns para os outros?', 1, b'0', 42, 1),
	(1243, 'Ouvi dizer que no Egito há trigo. Descei até lá e comprai trigo para nós, a fim de nos mantermos vivos e não morrermos”.', 2, b'0', 42, 1),
	(1244, 'Assim, dez dos irmãos de José desceram para comprar trigo no Egito.', 3, b'0', 42, 1),
	(1245, 'Jacó, porém, não deixou Benjamim, irmão de José, ir com os irmãos, com medo que lhe acontecesse alguma desgraça.', 4, b'0', 42, 1),
	(1246, 'Os filhos de Israel chegaram com outros que também vinham comprar cereais, pois havia fome em Canaã.', 5, b'0', 42, 1),
	(1247, 'José governava o país e era ele quem vendia cereais a toda a população. Quando chegaram, os irmãos de José prostraram-se diante dele com o rosto em terra.', 6, b'0', 42, 1),
	(1248, 'Ao ver os irmãos, José os reconheceu, mas comportou-se com eles como um estranho e lhes falou com rispidez. “De onde estais vindo?”, perguntou. Eles responderam: “De Canaã, para comprar víveres”.', 7, b'0', 42, 1),
	(1249, 'José reconheceu os irmãos, mas eles não o reconheceram.', 8, b'0', 42, 1),
	(1250, 'José lembrou-se dos sonhos que teve a respeito deles e lhes falou: “Vós sois uns espiões. Viestes ver os pontos fracos do país”.', 9, b'0', 42, 1),
	(1251, 'Eles disseram: “Não, Senhor! Teus servos vieram comprar mantimentos.', 10, b'0', 42, 1),
	(1252, 'Todos nós somos filhos do mesmo pai, somos gente honesta; teus servos não são espiões”.', 11, b'0', 42, 1),
	(1253, 'Ele lhes replicou: “Não é verdade, viestes ver os pontos fracos do país”.', 12, b'0', 42, 1),
	(1254, 'Eles disseram: “Nós, teus  servos, éramos doze irmãos, filhos do mesmo pai na terra de Canaã. O mais novo ficou com o pai e um  \\dos doze já não existe”.', 13, b'0', 42, 1),
	(1255, 'José insistiu: “Sois mesmo o que vos disse: uns espiões.', 14, b'0', 42, 1),
	(1256, 'Mas vou pôr- vos à prova quanto a isso. Eu juro pela vida do faraó, não saireis daqui enquanto não vier vosso irmão  menor.', 15, b'0', 42, 1),
	(1257, 'Mandai um de vós buscar vosso irmão, e vós outros ficareis aqui presos. Assim provareis se  o que dizeis é verdade. Caso contrário, pela vida do faraó, \\juro, sois uns espiões!”', 16, b'0', 42, 1),
	(1258, 'E mandou metê- los na prisão durante três dias.', 17, b'0', 42, 1),
	(1259, 'No terceiro dia José disse-lhes: “Deveis fazer o seguinte para  salvardes a vida – pois eu temo a Deus –:', 18, b'0', 42, 1),
	(1260, 'se realmente sois gente honesta, fique um dos irmãos aqui no cárcere, e vós outros ide levar o trigo que comprastes para alimentar vossas casas.', 19, b'0', 42, 1),
	(1261, 'Mas trazei-me o vosso irmão mais novo, para que eu possa provar a verdade de vossas palavra se vós não preciseis morrer”. Eles aceitaram fazer assim', 20, b'0', 42, 1),
	(1262, 'e diziam uns aos outros: “É justo sofrermos estas coisas, pois pecamos contra o nosso irmão. Vimos sua angústia quando nos pedia compaixão e não o atendemos. É por isso que nos veio esta desgraça”.', 21, b'0', 42, 1),
	(1263, 'Rúben lhes disse: “Não vos adverti para que não pecásseis contra o menino? Mas vós não me escutastes, e agora nos pedem contas do seu sangue”.', 22, b'0', 42, 1),
	(1264, 'Eles não sabiam que José os entendia, pois lhes falava por meio de intérprete.', 23, b'0', 42, 1),
	(1265, 'Então José se afastou deles e chorou. Pouco depois voltou e falou com eles; escolheu Simeão e mandou amarrá-lo à vista deles.', 24, b'0', 42, 1),
	(1266, 'José mandou que lhes enchessem de trigo os sacos, colocassem neles o respectivo dinheiro e lhes dessem provisões para o caminho. E assim se fez.', 25, b'0', 42, 1),
	(1267, 'Eles carregaram o trigo sobre os jumentos e partiram.', 26, b'0', 42, 1),
	(1268, 'Quando, no lugar onde pernoitaram, um deles abriu o saco para dar ração ao jumento, viu que o dinheiro estava na boca do saco.', 27, b'0', 42, 1),
	(1269, 'Ele disse aos irmãos: “Devolveram-me o dinheiro, está aqui no saco”. Então perderam a coragem e muito preocupados diziam uns aos outros: “Que será isso que Deus está fazendo conosco?”', 28, b'0', 42, 1),
	(1270, 'Retornando junto ao pai Jacó, na terra de Canaã, contaram-lhe tudo o que havia acontecido e disseram:', 29, b'0', 42, 1),
	(1271, '“O homem que governa aquela terra falou-nos com dureza e nos tratou como espiões do país.', 30, b'0', 42, 1),
	(1272, 'Nós lhe dissemos: ‘Somos gente honesta, não somos espiões.', 31, b'0', 42, 1),
	(1273, 'Éramos doze irmãos, filhos do mesmo pai; um desapareceu e o menor está no momento com o pai na terra de Canaã’.', 32, b'0', 42, 1),
	(1274, 'E nos disse o homem que governa o país: ‘Nisto saberei se sois gente honesta:  deixai comigo um de vós, levai mantimentos para matar a fome de vossas famílias e parti.', 33, b'0', 42, 1),
	(1275, 'Trazei- me depois o irmão mais novo. Assim saberei que não sois espiões, mas gente honesta. Então vos  devolverei o irmão e podereis circular pelo país’”.', 34, b'0', 42, 1),
	(1276, 'Quando esvaziaram os sacos, encontraram a bolsa de dinheiro em cada saco. Vendo as bolsas com o dinheiro, tanto eles como o pai ficaram com medo.', 35, b'0', 42, 1),
	(1277, 'Jacó, o pai, lhes disse: “Ides deixar-me sem filhos! José desapareceu, Simeão já não está aqui e quereis levar Benjamim também? Tudo se volta contra mim!”', 36, b'0', 42, 1),
	(1278, 'Rúben disse ao pai: “Poderás matar meus dois filhos se não te devolver Benjamim. Confia-o a mim, que eu o devolverei a ti”.', 37, b'0', 42, 1),
	(1279, 'Ele lhe respondeu: “Meu filho não descerá convosco. Seu irmão morreu, só resta ele. Se na viagem, que ides fazer, lhe acontecer uma desgraça, de tanta dor fareis descer este velho de cabelos brancos à morada dos mortos”.', 38, b'0', 42, 1),
	(1280, 'Ora, a fome grassava pela terra.', 1, b'0', 43, 1),
	(1281, 'Acabadas as provisões trazidas do Egito, o pai lhes disse: “Voltai para comprar para nós alguns mantimentos”.', 2, b'0', 43, 1),
	(1282, 'Mas Judá respondeu-lhe: “Aquele homem nos jurou: ‘Não me apareçais sem vosso irmão’.', 3, b'0', 43, 1),
	(1283, 'Se deixas ir conosco nosso irmão, desceremos para te comprar as provisões.', 4, b'0', 43, 1),
	(1284, 'Se não o mandas, não vamos descer, pois aquele homem nos disse: ‘Não apareçais sem o vosso irmão’”.', 5, b'0', 43, 1),
	(1285, 'E Israel disse: “Por que me fizestes esse mal, contando àquele homem que tínheis outro irmão?”', 6, b'0', 43, 1),
	(1286, 'E eles lhe responderam: “Aquele homem nos interrogou insistentemente sobre nós e nossa família e nos perguntou: ‘Vosso pai ainda está vivo? Tendes algum outro irmão?’ E nós respondemos segundo as perguntas. Podíamos acaso saber que ele ia nos dizer: ‘Trazei vosso irmão’?”', 7, b'0', 43, 1),
	(1287, 'E Judá disse ao pai Israel: “Deixa ir comigo o menino para que possamos pôr-nos a caminho e conservar-nos vivos; do contrário, morreremos nós, tu e nossos filhos.', 8, b'0', 43, 1),
	(1288, 'Responsabilizo-me por ele, de mim tu o reclamarás. Se não o trouxer de volta, colocando-o em tua presença, serei culpado para sempre diante de ti.', 9, b'0', 43, 1),
	(1289, 'Se não nos tivéssemos atrasado tanto, já estaríamos de volta pela segunda vez”.', 10, b'0', 43, 1),
	(1290, 'Disse-lhes o pai Israel: “Sendo assim, fazei o seguinte: escolhei para bagagem alguns dos melhores produtos desta terra e levai-os como presente a esse homem: um pouco de bálsamo, um pouco de mel, especiarias, resina, terebinto e amêndoas.', 11, b'0', 43, 1),
	(1291, 'Levai convosco o dobro de dinheiro para devolver o que foi posto nos sacos, pois talvez tenha sido um engano.', 12, b'0', 43, 1),
	(1292, 'Tomai vosso irmão e retornai para junto desse homem.', 13, b'0', 43, 1),
	(1293, 'E o Deus Poderoso faça que esse homem vos mostre compaixão, para que deixe voltar convosco o irmão que ficou ali e também Benjamim. Quanto a mim, se tiver de ser privado de meus filhos, que seja”.', 14, b'0', 43, 1),
	(1294, 'Levaram consigo presentes, o dobro de dinheiro e Benjamim, desceram para o Egito e apresentaram-se a José.', 15, b'0', 43, 1),
	(1295, 'Assim que José viu Benjamim com eles, disse ao mordomo: “Faze entrar estes homens em casa; mata um animal e prepara-o, pois estes homens comerão comigo ao meio-dia”.', 16, b'0', 43, 1),
	(1296, 'O mordomo fez o que José lhe tinha ordenado e os introduziu na casa de José.', 17, b'0', 43, 1),
	(1297, 'Enquanto entravam na casa de José, cheios de temor diziam entre si: “É por causa do dinheiro da outra vez, colocado em nossos sacos, que nos trazem aqui. É um pretexto para nos espoliar e cair sobre nós, fazendo-nos escravos com nossos jumentos”.', 18, b'0', 43, 1),
	(1298, 'Aproximando-se do mordomo, falaram-lhe à entrada da casa,', 19, b'0', 43, 1),
	(1299, 'dizendo: “Perdão, Senhor! Nós já viemos aqui uma vez para comprar mantimentos.', 20, b'0', 43, 1),
	(1300, 'Ao chegarmos ao lugar onde na volta passamos a noite, abrimos os sacos e vimos que o dinheiro de cada um estava na boca do respectivo saco. Nós o trouxemos de volta,', 21, b'0', 43, 1),
	(1301, 'com outra quantia igual para comprar provisões. Não sabemos quem pôs o dinheiro no saco”.', 22, b'0', 43, 1),
	(1302, '“Ficai tranqüilos – disse-lhes o mordomo – não temais! Foi vosso Deus, o Deus de vosso pai quem vos pôs este tesouro nos sacos. Eu recebi vosso dinheiro”. E mandou trazer-lhes Simeão.', 23, b'0', 43, 1),
	(1303, 'Depois de fazê-los entrar na residência de José, deu-lhes água para lavarem os pés e deu também ração aos jumentos.', 24, b'0', 43, 1),
	(1304, 'Eles prepararam os presentes, esperando que José viesse ao meio-dia, pois haviam sido avisados de que comeriam ali.', 25, b'0', 43, 1),
	(1305, 'Quando José chegou em casa, eles lhe apresentaram os presentes que haviam trazido consigo,  prostrando-se por terra diante dele.', 26, b'0', 43, 1),
	(1306, 'Perguntou-lhes se estavam bem e lhes disse:“ Vosso velho pai, de quem me falastes, está bem? Ainda vive?”', 27, b'0', 43, 1),
	(1307, 'Eles lhe responderam: “Teu servo está bem, nosso pai ainda vive”, e inclinaram-se profundamente.', 28, b'0', 43, 1),
	(1308, 'José ergueu os olhos e viu Benjamim, seu irmão, filho de sua mãe, e disse: “É este vosso irmão mais novo do qual me falastes?” E acrescentou: “Deus te seja favorável, meu filho”.', 29, b'0', 43, 1),
	(1309, 'Ficou todo comovido por causa do irmão e estava prestes a chorar. Entrou por isso apressadamente nos aposentos, onde desatou em prantos.', 30, b'0', 43, 1),
	(1310, 'Depois de lavar o rosto, reapareceu, fazendo esforços para se conter e disse: “Servi a comida”.', 31, b'0', 43, 1),
	(1311, 'Serviram separadamente a José, aos irmãos e também aos egípcios que com ele comiam, pois os egípcios não podem comer com os hebreus, por ser isso coisa abominável para eles.', 32, b'0', 43, 1),
	(1312, 'Assentaram-se diante dele por ordem de idade,  desde o mais velho até o mais novo, olhando espantados uns para os outros.', 33, b'0', 43, 1),
	(1313, 'José mandou servir- lhes porções de sua mesa, mas a porção de Benjamim era cinco vezes maior do que a dos outros. Eles  beberam e ficaram muito alegres em sua companhia.', 34, b'0', 43, 1),
	(1314, 'José deu ao seu administrador esta ordem: “Enche os sacos destes homens de víveres quanto couber e põe o dinheiro de cada um na boca do saco.', 1, b'0', 44, 1),
	(1315, 'Põe também minha taça, a taça de prata, na boca do saco do mais moço, juntamente com o dinheiro do trigo”. O mordomo fez o que José lhe havia ordenado.', 2, b'0', 44, 1),
	(1316, 'Ao amanhecer, deixaram partir os hebreus com seus jumentos.', 3, b'0', 44, 1),
	(1317, 'Estando eles ainda não muito longe, pois apenas tinham saído da cidade, José disse ao seu administrador: “Sai em perseguição desses homens e dize-lhes quando os alcançares: ‘Por que pagastes o bem com o mal?', 4, b'0', 44, 1),
	(1318, 'Não é por acaso esta a taça em que bebe meu patrão? É com ela que ele se põe a adivinhar. Fizestes mal em agir assim’”.', 5, b'0', 44, 1),
	(1319, 'Quando os alcançou, repetiu-lhes o mordomo estas mesmas palavras.', 6, b'0', 44, 1),
	(1320, 'Eles responderam: “Por que fala assim o meu Senhor? Longe de teus servos fazer uma coisa dessas!', 7, b'0', 44, 1),
	(1321, 'Até o dinheiro achado na boca de nossos sacos te trouxemos de volta da terra de Canaã, como então iríamos furtar ouro ou prata da casa de teu Senhor?', 8, b'0', 44, 1),
	(1322, 'Morra aquele de teus servos em cujo poder se encontrar a taça, e nós sejamos reduzidos a escravos de teu Senhor”.', 9, b'0', 44, 1),
	(1323, 'E ele respondeu: “Quanto ao que dizeis, fica assim:aquele com quem se encontrar a taça será meu escravo, e vós outros estareis livres”.', 10, b'0', 44, 1),
	(1324, 'Cada um descarregou depressa o saco em terra e o abriu.', 11, b'0', 44, 1),
	(1325, 'Começando pelo mais velho e acabando pelo mais novo, o mordomo os examinou e a taça foi encontrada no saco de Benjamim.', 12, b'0', 44, 1),
	(1326, 'Então, num gesto de dor, eles rasgaram as vestes, carregaram de novo os jumentos e voltaram à cidade.', 13, b'0', 44, 1),
	(1327, 'Quando Judá chegou com os irmãos à residência de José que ainda ali estava, prostraram-se com o rosto em terra.', 14, b'0', 44, 1),
	(1328, 'José lhes perguntou: “O que foi que fizestes? Não sabíeis que um homem como eu é capaz de adivinhar?”', 15, b'0', 44, 1),
	(1329, 'Judá respondeu: “O que podemos dizer a meu Senhor? Como falar, como mostrar nossa inocência? Deus descobriu a culpa de teus servos. Tanto nós como aquele em cujo poder foi  encontrada a taça nos tornamos agora teus escravos”.', 16, b'0', 44, 1),
	(1330, 'Ele, porém, respondeu: “Longe de mim fazer isso! Aquele com quem se encontrou a taça será meu escravo. Quanto a vós, voltai em paz junto de vosso pai”.', 17, b'0', 44, 1),
	(1331, 'Aproximou-se então Judá e, com confiança, disse: “Perdão, meu Senhor! Permite ao teu servo falar uma palavra aos teus ouvidos, sem que se acenda tua cólera contra mim. Pois tu és como o próprio faraó.', 18, b'0', 44, 1),
	(1332, 'Foi meu Senhor quem perguntou a seus servos: ‘Ainda tendes pai e algum outro irmão?’', 19, b'0', 44, 1),
	(1333, 'E nós respondemos: ‘Temos um pai já velho e temos o irmão mais novo, nascido em sua velhice. Este tinha um irmão, que morreu; ele é o único filho que resta de sua mãe, e seu pai o ama com muita ternura.', 20, b'0', 44, 1),
	(1334, 'Tu disseste a teus servos: ‘Trazei-o a mim para que eu possa vê-lo’.', 21, b'0', 44, 1),
	(1335, 'Nós te dissemos: ‘O menino não pode deixar o pai. Se o deixar, o pai morrerá’.', 22, b'0', 44, 1),
	(1336, 'Mas tu disseste a teus servos: ‘Se não vier convosco o irmão mais novo, não me apareçais aqui’.', 23, b'0', 44, 1),
	(1337, 'Quando,pois, voltamos para junto de teu servo, nosso pai, contamos-lhe tudo o que meu Senhor tinha dito.', 24, b'0', 44, 1),
	(1338, 'Mais tarde disse-nos o pai: ‘Voltai para comprar alguns mantimentos’,', 25, b'0', 44, 1),
	(1339, 'e nós lhe respondemos: ‘Não podemos ir, a não ser que o irmão mais novo vá conosco. Se o irmão não nos acompanhar, não poderemos apresentar-nos àquele homem’.', 26, b'0', 44, 1),
	(1340, 'E o teu servo, nosso pai, respondeu: ‘Bem sabeis que minha mulher me deu apenas dois filhos.', 27, b'0', 44, 1),
	(1341, 'Um deles saiu de casa e eu disse: um animal feroz o devorou. Até agora não apareceu.', 28, b'0', 44, 1),
	(1342, 'Se me levardes também este e lhe acontecer alguma desgraça, fareis descer de desgosto meus cabelos brancos à morada dos mortos’.', 29, b'0', 44, 1),
	(1343, 'Se eu voltar agora para teu servo meu pai, sem o menino, a quem está intimamente afeiçoado,', 30, b'0', 44, 1),
	(1344, 'quando der pela falta do menino, morrerá. E nós teremos feito descer, de tristeza, à morada dos mortos teu servo de cabelos brancos, nosso pai.', 31, b'0', 44, 1),
	(1345, 'Eu, teu servo, me tornei responsável pelo menino ao tirá-lo do pai e disse: ‘Se não o trouxer de volta, serei eternamente culpado perante meu pai’.', 32, b'0', 44, 1),
	(1346, 'Deixa, pois, que teu servo fique como escravo de meu Senhor em lugar do menino, para que ele possa subir de volta com os irmãos.', 33, b'0', 44, 1),
	(1347, 'Do contrário, como poderei voltar para junto de meu pai sem o menino? Não gostaria de ver meu pai atingido pela desgraça”.', 34, b'0', 44, 1),
	(1348, 'Então José não pôde mais conter-se diante de todos os que o rodeavam e gritou: “Mandai sair toda a gente”. E assim, ninguém mais ficou com ele quando se deu a conhecer aos irmãos.', 1, b'0', 45, 1),
	(1349, 'José rompeu num choro tão forte, que os egípcios o ouviram e até mesmo a casa do faraó.', 2, b'0', 45, 1),
	(1350, 'E José disse a seus irmãos: “Eu sou José! Meu pai ainda vive?” Mas os irmãos não conseguiam responder nada, pois ficaram estarrecidos diante dele.', 3, b'0', 45, 1),
	(1351, 'José, cheio de clemência, disse aos irmãos: “Aproximai-vos de mim”. Tendo eles se aproximado, ele repetiu: “Eu sou José, vosso irmão, que vendestes para o Egito.', 4, b'0', 45, 1),
	(1352, 'Entretanto, não vos aflijais, nem vos atormenteis por me terdes vendido a este país, pois foi para conservar-vos a vida que Deus me enviou à vossa frente.', 5, b'0', 45, 1),
	(1353, 'De fato este é o segundo ano de fome no  país, e durante outros cinco não haverá semeadura nem colheita.', 6, b'0', 45, 1),
	(1354, 'Deus me enviou à vossa frente para assegurar-vos a sobrevivência no país e preservar-vos as vidas para uma libertação grandiosa.', 7, b'0', 45, 1),
	(1355, 'Portanto não fostes vós que me enviastes para cá, mas Deus. Ele me constituiu tutor do faraó, administrador de todo o palácio e governador de todo o Egito.', 8, b'0', 45, 1),
	(1356, 'Voltai depressa para dizer a meu pai: ‘Assim diz teu filho José: Deus me constituiu administrador de todo o Egito. Desce, pois, para junto de mim sem tardar.', 9, b'0', 45, 1),
	(1357, 'Habitarás na terra de Gessen e estarás perto de mim com os filhos e netos, com as ovelhas e bois e tudo o que tens.', 10, b'0', 45, 1),
	(1358, 'Lá eu te sustentarei, pois ainda restam outros cinco anos de fome, para que não venhas a cair na indigência com a família e tudo o que tens’.', 11, b'0', 45, 1),
	(1359, 'Com os vossos próprios lhos estais vendo, e meu irmão Benjamim o vê com os seus que sou eu mesmo que vos falo.', 12, b'0', 45, 1),
	(1360, 'Contai a meu pai quanto é o meu prestígio no Egito e tudo o que vistes, e apressai-vos em trazer para cá meu pai”.', 13, b'0', 45, 1),
	(1361, 'Então abraçou seu irmão Benjamim e pôs-se a chorar. Benjamim também chorava abraçado a José.', 14, b'0', 45, 1),
	(1362, 'Em seguida beijou todos os irmãos, chorando enquanto os abraçava. Depois os irmãos conversaram com ele.', 15, b'0', 45, 1),
	(1363, 'Correu pela casa do faraó a notícia: “Chegaram os irmãos de José”. Isto trouxe satisfação ao faraó e a seus servidores.', 16, b'0', 45, 1),
	(1364, 'E o faraó disse a José: “Dize a teus irmãos: ‘Fazei o seguinte: carregai os animais e retornai à terra de Canaã.', 17, b'0', 45, 1),
	(1365, 'Buscai vosso pai e vossas famílias e voltai a mim. Eu vos darei as terras mais férteis do Egito e comereis o que há de melhor no país’.', 18, b'0', 45, 1),
	(1366, 'Tu, transmite a teus irmãos esta ordem: “Levai do Egito carros para transportar os filhos e as mulheres, e voltai quanto antes para cá, trazendo vosso pai.', 19, b'0', 45, 1),
	(1367, 'Não tenhais pena de deixar vossos bens, pois o que de melhor há no Egito será para vós”.', 20, b'0', 45, 1),
	(1368, 'Assim fizeram os filhos de Israel, e José lhes deu carros, segundo a ordem do faraó, e provisões para a viagem.', 21, b'0', 45, 1),
	(1369, 'Deu-lhes também a cada um mudas de roupa e a Benjamim trezentas moedas de prata e cinco vestes.', 22, b'0', 45, 1),
	(1370, 'Enviou também ao pai dez jumentos carregados com o que de melhor havia no Egito e dez jumentas carregadas de trigo, de pão e víveres para a viagem do pai.', 23, b'0', 45, 1),
	(1371, 'Depois, ao despedir-se dos irmãos, quando iam partir, disse-lhes: “Não fiqueis perturbados pelo caminho”.', 24, b'0', 45, 1),
	(1372, 'Saíram, pois, do Egito e chegaram à terra de Canaã, para junto do pai,', 25, b'0', 45, 1),
	(1373, 'e lhe comunicaram a notícia: “José ainda está vivo. Ele é o governador de todo o Egito”. Mas Jacó ficou perplexo, pois não podia acreditar neles.', 26, b'0', 45, 1),
	(1374, 'Eles, então, contaram tudo o que José lhes dissera. Depois, ao ver os carros que José lhe mandava para transportá-lo, reanimou-se o espírito de seu pai Jacó.', 27, b'0', 45, 1),
	(1375, 'E Israel disse: “Basta! Meu filho José ainda vive! Irei vê-lo antes de morrer”.', 28, b'0', 45, 1),
	(1376, 'Israel partiu com tudo o que tinha. Ao chegar a Bersabéia, ofereceu sacrifícios ao Deus de seu pai Isaac.', 1, b'0', 46, 1),
	(1377, 'Deus falou a Israel em visão noturna, dizendo-lhe: “Jacó! Jacó!” E ele respondeu: “Aqui estou!”', 2, b'0', 46, 1),
	(1378, 'E Deus lhe falou: “Eu sou Deus, o Deus de teu pai. Não tenhas medo de descer ao Egito,pois lá farei de ti uma grande nação.', 3, b'0', 46, 1),
	(1379, 'Eu mesmo descerei contigo ao Egito e de lá te farei subir, e é José  que te fechará os olhos”.', 4, b'0', 46, 1),
	(1380, 'Jacó levantou-se e deixou Bersabéia. Os filhos de Israel puseram o pai Jacó, as crianças e as mulheres sobre os carros que o faraó enviara para os transportar.', 5, b'0', 46, 1),
	(1381, 'Levaram também consigo os rebanhos e os bens que possuíam na terra de Canaã, e Jacó se encaminhou para o Egito com toda a descendência:', 6, b'0', 46, 1),
	(1382, 'os filhos e netos, as filhas e netas, toda a descendência, ele os levou consigo para o Egito.', 7, b'0', 46, 1),
	(1383, 'Eis aqui os nomes dos israelitas, Jacó e os filhos, que entraram no Egito. O primogênito de Jacó, Rúben.', 8, b'0', 46, 1),
	(1384, 'Filhos de Rúben: Henoc, Falu, Hesron e Carmi.', 9, b'0', 46, 1),
	(1385, 'Filhos de Simeão: Jamuel, Jamin, Aod, Jaquin, Soar e Saul, filho da cananéia.', 10, b'0', 46, 1),
	(1386, 'Filhos de Levi: Gérson, Caat e Merari.', 11, b'0', 46, 1),
	(1387, 'Filhos de Judá: Her, Onã, Sela, Farés e Zara; porém, Her e Onã morreram na terra de Canaã. Filhos de Farés: Hesron e Hamul.', 12, b'0', 46, 1),
	(1388, 'Filhos de Issacar: Tola, Fua, Jasub e Semron.', 13, b'0', 46, 1),
	(1389, 'Filhos de Zabulon: Sared, Elon e Jaelel.', 14, b'0', 46, 1),
	(1390, 'São esses os filhos que Lia deu a Jacó em Padã-Aram, além da filha Dina. Total de seus filhos e filhas: trinta e três pessoas.', 15, b'0', 46, 1),
	(1391, 'Filhos de Gad: Safon, Hagi, Suni, Esebon, Eri, Arodi e Areli.', 16, b'0', 46, 1),
	(1392, 'Filhos de Aser: Jamne, Jesua, Jessui, Beria e a irmã Sera. Filhos de Beria eram Héber e Melquiel.', 17, b'0', 46, 1),
	(1393, 'São esses os filhos de Zelfa, a escrava que Labão havia dado à sua filha Lia, e que ela deu a Jacó: dezesseis pessoas.', 18, b'0', 46, 1),
	(1394, 'Filhos de Raquel, mulher de Jacó: José e Benjamim.', 19, b'1', 46, 1),
	(1395, 'No Egito José teve Manassés e Efraim, nascidos de Asenet, filha de Putifar, sacerdote de On.', 20, b'0', 46, 1),
	(1396, 'Filhos de Benjamim: Bela, Bocor, Asbel, Gera, Naamã, Equi, Ros, Mofim, Ofim e Ared.', 21, b'0', 46, 1),
	(1397, 'São esses os filhos de Raquel, os que ela deu a Jacó: catorze pessoas ao todo.', 22, b'0', 46, 1),
	(1398, 'Filhos de Dã: Husim.', 23, b'0', 46, 1),
	(1399, 'Filhos de Neftali: Jasiel, Guni, Jeser e Salém.', 24, b'0', 46, 1),
	(1400, 'São esses os filhos de Bala, a escrava que Labão deu à filha Raquel. Ao todo, deu sete pessoas a Jacó.', 25, b'0', 46, 1),
	(1401, 'O total das pessoas que emigraram com Jacó para o Egito, descendentes dele, sem contar as mulheres dos filhos, era de sessenta e seis pessoas.', 26, b'0', 46, 1),
	(1402, 'Os filhos de José nascidos no Egito eram dois. A casa toda de Jacó, que emigrou para o Egito, constava de setenta pessoas. Encontro de Jacó com José e o faraó', 27, b'0', 46, 1),
	(1403, 'Jacó enviou Judá na frente para avisar José e fazê-lo vir ao seu encontro em Gessen. Quando chegaram à terra de Gessen,', 28, b'0', 46, 1),
	(1404, 'José mandou atrelar seu carro e dirigiuse a Gessen ao encontro de seu pai Israel. Logo que o viu, lançou-se ao seu pescoço e, abraçado, chorou longamente.', 29, b'0', 46, 1),
	(1405, 'Israel disse a José: “Agora que vi teu rosto, posso morrer, pois ainda estás vivo”.', 30, b'0', 46, 1),
	(1406, 'José disse aos irmãos e à família do pai: “Vou subir para informar ao faraó e lhe dizer: ‘Vieram meus irmãos com toda a família de meu pai, que estavam na terra de Canaã.', 31, b'0', 46, 1),
	(1407, 'São pastores e têm rebanhos de ovelhas e bois, e vieram trazendo tudo consigo’.', 32, b'0', 46, 1),
	(1408, 'Por isso, quando o faraó vos chamar e vos perguntar qual a vossa profissão,', 33, b'0', 46, 1),
	(1409, 'respondereis: ‘Nós, teus servos, somos proprietários de rebanhos desde a infância até agora, como o foram nossos pais’. Assim podereis habitar na região de Gessen”. (Os egípcios, de fato, abominam todos os pastores de ovelhas.)', 34, b'0', 46, 1),
	(1410, 'José foi anunciar ao faraó: “Meu pai e meus irmãos vieram da terra de Canaã, com as ovelhas e bois e tudo o que têm. Estão agora na terra de Gessen”.', 1, b'0', 47, 1),
	(1411, 'Tendolevado cinco dos irmãos, apresentou-os ao faraó.', 2, b'0', 47, 1),
	(1412, 'E o faraó lhes perguntou: “Qual é vossa profissão?” Eles responderam ao faraó: “Nós, teus servos, somos pastores de ovelhas,como já o foram nossos pais”.', 3, b'0', 47, 1),
	(1413, 'Disseram-lhe também: “Viemos para viver como migrantes neste país, pois não há mais pastagens para os rebanhos, e a fome é grande na terra de Canaã. Permite, pois, que teus servos sejam assentados na terra de Gessen”.', 4, b'0', 47, 1),
	(1414, 'O faraó disse a José: “Teu pai e teus irmãos vieram a ti.', 5, b'0', 47, 1),
	(1415, 'Tens à disposição a terra do Egito. Instala teu pai e irmãos na melhor parte do país: que habitem na terra de Gessen. Se conheces alguns homens experimentados entre eles, coloca-os como responsáveis pelos meus rebanhos”.', 6, b'0', 47, 1),
	(1416, 'José mandou vir seu pai Jacó e apresentou-o ao faraó. Jacó abençoou o faraó.', 7, b'0', 47, 1),
	(1417, 'Então o faraó lhe perguntou: “Quantos anos tens?”', 8, b'0', 47, 1),
	(1418, 'Jacó respondeu: “Cento e trinta são os anos de minha vida migrante pela terra. Poucos e difíceis foram os anos de minha vida, que não chegaram a igualar os anos vividos por meus pais em suas migrações”.', 9, b'0', 47, 1),
	(1419, 'Jacó tornou a abençoar o faraó e retirou-se de sua presença.', 10, b'0', 47, 1),
	(1420, 'José instalou o pai e os irmãos, dando-lhes uma propriedade no Egito, na melhor parte do país, no distrito de Ramsés, como o faraó havia determinado.', 11, b'0', 47, 1),
	(1421, 'Forneceu também víveres ao pai, aos irmãos e a toda a família do pai segundo o número dos filhos.', 12, b'0', 47, 1),
	(1422, 'Faltava comida em todo o país, pois a fome se agravava. Tanto o Egito como Canaã estavam esgotados pela fome.', 13, b'0', 47, 1),
	(1423, 'Com a vendado trigo, José chegou a recolher todo o dinheiro que havia no Egito e em Canaã, e depositou-o no palácio do faraó.', 14, b'0', 47, 1),
	(1424, 'Esgotado o dinheiro do Egito e de Canaã, os egípcios em peso recorriam a José, pedindo: “Dá-nos pão! Ou será que teremos de morrer em tua presença porque o dinheiro acabou?”', 15, b'0', 47, 1),
	(1425, 'José lhes respondia: “Já que vos falta dinheiro, trazei-me vossos rebanhos, e eu vos darei pão em troca”.', 16, b'0', 47, 1),
	(1426, 'Eles trouxeram os animais, e José lhes deu pão em troca de cavalos,ovelhas, bois e jumentos. Naquele ano forneceu-lhes pão em troca de todos os rebanhos.', 17, b'0', 47, 1),
	(1427, 'Passado aquele ano, vieram no ano seguinte e lhe disseram: “Não podemos esconder ao Senhor que nosso dinheiro acabou. Ora, já te demos nosso gado, não resta outra coisa para oferecer-te senão nosso corpo e nossas terras.', 18, b'0', 47, 1),
	(1428, 'Por que haveríamos de perecer diante de ti, nós e nossas terras? Compra-nos junto com as terras em troca de pão, e nós com as terras serviremos ao faraó. Dá-nos sementes para que possamos viver e não morramos, e nossas terras não fiquem abandonadas”.', 19, b'0', 47, 1),
	(1429, 'Então José adquiriu para o faraó todas as terras do Egito, porque os egípcios eram obrigados, por causa da fome, a vender cada um seu campo. Assim a terra veio a ser propriedade do faraó,', 20, b'0', 47, 1),
	(1430, 'e José submeteu o povo à servidão do faraó, de um extremo ao outro do território do Egito.', 21, b'0', 47, 1),
	(1431, 'Só deixou de comprar as terras dos sacerdotes porque eles recebiam do faraó uma subvenção. Como vivessem da subvenção, não tiveram de venderas terras.', 22, b'0', 47, 1),
	(1432, 'E José disse ao povo: “Hoje vos comprei junto com as terras para o faraó. Aqui tendes as sementes com que semeareis as terras.', 23, b'0', 47, 1),
	(1433, 'No tempo da colheita  dareis a quinta parte ao faraó. As outras quatro partes servirão para semear os campos e para sustento vosso, de vossas famílias e de vossos filhos”.', 24, b'0', 47, 1),
	(1434, 'Eles disseram: “Devolveste-nos a vida. Alcançamos teu favor e nos tornaremos escravos do faraó”.', 25, b'0', 47, 1),
	(1435, 'José fez disso uma lei que existe ainda hoje. Por esta lei pertence ao faraó a quinta parte do produto das terras do Egito. Somente as terras dos sacerdotes não passaram para o faraó. Últimas vontades de Jacó', 26, b'0', 47, 1),
	(1436, 'Israel estabeleceu-se no Egito, na região de Gessen. Ali adquiriram propriedades, tornando-se fecundos e muito numerosos.', 27, b'0', 47, 1),
	(1437, 'Jacó viveu  dezessete anos no Egito, e a duração de sua vida foi de cento e quarenta e sete anos.', 28, b'0', 47, 1),
	(1438, 'Aproximando- se o dia da morte de Israel, ele chamou seu filho José e lhe disse: “Se realmente ganhei o teu favor, põe  a mão debaixo de minha coxa e promete tratar-me com amor e fidelidade: não me sepultes no Egito.', 29, b'0', 47, 1),
	(1439, 'Quando eu descansar com meus pais, leva-me do Egito e enterra-me na sepultura deles”. José respondeu: “Farei o que pediste”.', 30, b'0', 47, 1),
	(1440, 'E seu pai insistiu: “Jura-me!” E ele jurou. Então Israel inclinou-se sobre a cabeceira do seu leito.', 31, b'0', 47, 1),
	(1441, 'Depois desses acontecimentos comunicaram a José: “Teu pai está doente”. José tomou consigo os dois filhos, Manassés e Efraim,', 1, b'0', 48, 1),
	(1442, 'e mandou avisar a Jacó: “Teu filho José veio te visitar”. Então Israel, com muito esforço, sentou-se no leito.', 2, b'0', 48, 1),
	(1443, 'Depois Jacó disse a José: “O Deus Poderoso me apareceu em Luza, na terra de Canaã, abençoou-me', 3, b'0', 48, 1),
	(1444, 'e me disse: ‘Eu te farei fecundo e numeroso, transformando-te numa comunidade de povos; à tua descendência darei esta terra como propriedade perpétua’.', 4, b'0', 48, 1),
	(1445, 'Quanto aos dois filhos que tiveste no Egito, antes de vir para junto de ti, serão meus. Efraim e Manasses serão meus como Rúben e Simeão.', 5, b'0', 48, 1),
	(1446, 'Os que te nascerem depois deles serão teus e em nome de seus irmãos receberão a herança.', 6, b'0', 48, 1),
	(1447, 'É que, ao voltar de Padã-Aram, morreu me Raquel durante a viagem, na terrade Canaã, pouco antes de chegar a Éfrata. Eu a sepultei ali mesmo no caminho de Éfrata, que é Belém”.', 7, b'0', 48, 1),
	(1448, 'Ao ver os filhos de José, Israel perguntou:“ Quem são estes?”', 8, b'0', 48, 1),
	(1449, 'José respondeu: “São meus filhos que Deus me deu aqui”. “Faze-os aproximar-se de mim para que os abençoe”, disse Jacó.', 9, b'0', 48, 1),
	(1450, 'Israel tinha os olhos enfraquecidos pela idade e enxergava mal. José, então, os fez aproximar-se, e Israel os beijou e abraçou,', 10, b'0', 48, 1),
	(1451, 'dizendo a José: “Não esperava ver nem teu rosto e eis que Deus ainda me fez ver também teus filhos”.', 11, b'0', 48, 1),
	(1452, 'José os tirou do colo do pai e prostrou-se com o rosto em terra.', 12, b'0', 48, 1),
	(1453, 'Depois, tomando os dois, Efraim à direita e Manasses à esquerda, aproximou-se de Israel para que Manassés ficasse à direita de Israel e Efraim à esquerda.', 13, b'0', 48, 1),
	(1454, 'Mas Israel estendeu a mão direita e a colocou sobre a cabeça de Efraim, que era o mais moço, e a esquerda sobre a cabeça de Manassés, cruzando as mãos, embora Manasses fosse o primogênito.', 14, b'0', 48, 1),
	(1455, 'Abençoou a José dizendo: “O Deus em cuja presença andaram meus pais Abraão e Isaac, o Deus que foi meu pastor desde que existo até hoje,', 15, b'0', 48, 1),
	(1456, 'o Anjo que me livrou de todo mal, abençoe estes meninos. Que por meio deles seja recordado o meu nome e o nome de meus  pais, Abraão e Isaac, e que eles se tornem uma multidão na terra”.', 16, b'0', 48, 1),
	(1457, 'José viu o pai colocando a mão direita sobre a cabeça de Efraim e ficou contrariado. Pegou a mão do pai, removendo-a da cabeça de Efraim para a de Manassés,', 17, b'0', 48, 1),
	(1458, 'e lhe disse: “Não é assim, meu pai! O primogênitoé este! Põe a mão direita sobre a cabeça dele”.', 18, b'0', 48, 1),
	(1459, 'Mas o pai se recusou, dizendo: “Eu sei, meu filho,eu sei. Também ele se tornará um povo, também ele será grande. Não obstante, seu irmão menor será maior do que ele e sua descendência se tornará uma multidão de nações”.', 19, b'0', 48, 1),
	(1460, 'Abençoou-os naquele dia, dizendo: “Por vós o povo de Israel pronunciará bênçãos e dirá: Deus te faça semelhante a Efraim e Manassés”. Assim Jacó pôs Efraim à frente de Manassés.', 20, b'0', 48, 1),
	(1461, 'Depois Israel disse a José: “Eu vou morrer, mas Deus estará convosco e vos reconduzirá à terra de vossos pais.', 21, b'0', 48, 1),
	(1462, 'Dou-te uma parte a mais que teus irmãos, Siquém, que tomei aos amorreus com minha espada e arco”.', 22, b'0', 48, 1),
	(1463, 'Jacó chamou os filhos e lhes disse: “Reuni-vos para que eu vos anuncie o que sucederá nos dias vindouros:', 1, b'0', 49, 1),
	(1464, 'Reuni-vos e escutai, filhos de Jacó, escutai Israel, vosso pai.', 2, b'0', 49, 1),
	(1465, 'Rúben, tu és o meu primogênito, minha força e o primeiro fruto de meu vigor, primeiro em autoridade e primeiro em poder.', 3, b'0', 49, 1),
	(1466, 'Impetuoso como a água, não manterás a primazia! Pois subiste na cama de teu pai, profanando então o meu leito.', 4, b'0', 49, 1),
	(1467, 'Simeão e Levi são irmãos. Instrumentos de violência são suas espadas.', 5, b'0', 49, 1),
	(1468, 'Jamais assistirei a seu conselho, nem minha honra será cúmplice de suas tramas. Porque no furor degolaram homens, e por capricho a touros cortaram os tendões.', 6, b'0', 49, 1),
	(1469, 'Maldita, cólera tão violenta, maldito, furor tão cruel. Vou reparti-los em Jacó e dispersá-los em Israel.', 7, b'0', 49, 1),
	(1470, 'A ti, Judá, teus irmãos renderão homenagem, tua mão pesará sobre a nuca de teus inimigos. Diante de ti se prostrarão os filhos de teu pai.', 8, b'0', 49, 1),
	(1471, 'Judá, filhote de leão! Voltaste, meu filho, da pilhagem. Agacha-se e deita-se, como leão e como leoa; quem o despertará?', 9, b'0', 49, 1),
	(1472, 'O cetro não será tirado de Judá nem o bastão de comando de entre seus pés, até que venha aquele a quem pertencem e a quem obedecerão os povos.', 10, b'0', 49, 1),
	(1473, 'Ele ata à videira o jumentinho, à parreira escolhida o filho da jumenta; lava no vinho a veste e no sangue das uvas a roupa.', 11, b'0', 49, 1),
	(1474, 'Seus olhos são mais escuros que o vinho e os dentes mais brancos que o leite.', 12, b'0', 49, 1),
	(1475, 'Zabulon habita na costa do mar, serve de porto aos navios, e sua fronteira irá até Sidônia.', 13, b'0', 49, 1),
	(1476, 'Issacar é um jumento robusto deitado no meio dos currais.', 14, b'0', 49, 1),
	(1477, 'Vendo que o repouso era bom e que a terra era excelente, ofereceu o lombo à carga, foi submetido a trabalho escravo.', 15, b'0', 49, 1),
	(1478, 'Dã julgará seu povo, como qualquer uma das tribos de Israel.', 16, b'0', 49, 1),
	(1479, 'Dã seja como serpente no caminho, como víbora no atalho, que morde os calcanhares do cavalo e faz cair para trás o cavaleiro.', 17, b'0', 49, 1),
	(1480, 'Tua salvação espero, ó Senhor!', 18, b'0', 49, 1),
	(1481, 'Gad: assaltado por bandos de guerrilheiros, ele também os ataca pelas costas.', 19, b'0', 49, 1),
	(1482, 'Aser: seu pão é nutritivo, fornece produtos deliciosos aos reis.', 20, b'0', 49, 1),
	(1483, 'Neftali é uma corça em liberdade, que tem crias graciosas.', 21, b'0', 49, 1),
	(1484, 'José é como planta nova, árvore frutífera junto à fonte, seus galhos sobem pelo muro.', 22, b'0', 49, 1),
	(1485, 'Provocaram-no com flechas, atacaram-no os atiradores de setas.', 23, b'0', 49, 1),
	(1486, 'Mas permanece retesado seu arco, e ágeis se mostram as mãos. Foi pelas mãos do Soberano de Jacó, pelo nome do Pastor, a Rocha de Israel.', 24, b'0', 49, 1),
	(1487, 'Pelo Deus de teu pai, que te ajude, pelo Deus Poderoso, que te abençoe com bênçãos que descem do céu; bênçãos do Abismo sob a terra, bênçãos dos peitos e do ventre;', 25, b'0', 49, 1),
	(1488, 'bênçãos de teu pai, superiores às bênçãos dos montes antigos, às delícias das colinas eternas. Desçam elas sobre a cabeça de José, sobre a fronte do consagrado entre os irmãos.', 26, b'0', 49, 1),
	(1489, 'Benjamim é lobo voraz: pela manhã devora a presa e à tarde reparte despojos”.', 27, b'0', 49, 1),
	(1490, 'São essas as doze tribos de Israel, e isso foi o que lhes falou o pai ao abençoá-los, dando a cada um sua bênção.', 28, b'0', 49, 1),
	(1491, 'Depois, Jacó deu lhes esta instrução: “Vou reunir-me a meus antepassados; sepultai-me com meus pais na gruta de Macpela, no campo de Efron, o heteu,', 29, b'0', 49, 1),
	(1492, '30 na gruta que fica no campo de Macpela, em frente de Mambré, na terra de Canaã. É a gruta que Abraão comprou a Efron, o heteu, junto com o campo, como propriedade funerária.', 30, b'0', 49, 1),
	(1493, 'Lá foram sepultados Abraão e sua mulher Sara, Isaac e sua mulher Rebeca, e foi lá que sepultei Lia”.', 31, b'0', 49, 1),
	(1494, '(Trata-se do campo com a gruta comprados dos heteus.)', 32, b'0', 49, 1),
	(1495, 'Quando Jacó acabou de dar essas instruções aos filhos, recolheu os pés sobre a cama e expirou. E foi reunir-se aos seus antepassados.', 33, b'0', 49, 1),
	(1496, 'Então José lançou-se sobre o rosto do pai, chorando e beijando-o.', 1, b'0', 50, 1),
	(1497, 'Mandou os médicos, que tinha a seu serviço, embalsamar o pai. E os médicos embalsamaram Israel.', 2, b'0', 50, 1),
	(1498, 'Gastaram nisso quarenta dias, o tempo que se leva para embalsamar. E os egípcios guardaram luto durante setenta dias.', 3, b'0', 50, 1),
	(1499, 'Passados os dias do luto, José falou assim ao pessoal da casa do faraó: “Se me quereis fazer um favor, fazei chegar aos ouvidos do faraó', 4, b'0', 50, 1),
	(1500, 'o juramento que meu pai me pediu, dizendo: ‘Quando eu morrer, me sepultarás na sepultura que escavei para mim em Canaã’. Quero portanto, agora, subir para sepultar meu pai e depois voltarei”.', 5, b'0', 50, 1),
	(1501, 'E o faraó lhe respondeu: “Sobe e sepulta teu pai conforme ele te fez jurar”.', 6, b'0', 50, 1),
	(1502, 'José subiu então para sepultar o pai, acompanhado por todos os anciãos a serviço do faraó e todos os anciãos do Egito,', 7, b'0', 50, 1),
	(1503, 'toda a família de José, seus irmãos e a família do pai. Deixaram na região de Gessen apenas as crianças, as ovelhas e os bois.', 8, b'0', 50, 1),
	(1504, 'Subiram também com ele carros e cavaleiros, de modo que o cortejo era muito imponente.', 9, b'0', 50, 1),
	(1505, 'Quando chegaram à eira de debulhar o trigo, em Atad, do outro lado do Jordão, organizaram ali um grande e solene funeral, e José fez um luto de sete dias pelo pai.', 10, b'0', 50, 1),
	(1506, 'Os moradores da terra, os cananeus, ao verem esse luto na eira de Atad, comentaram: “Este foi um grande luto para o Egito”. Por isso se deu a esse lugar, no Além-Jordão, o nome de Abelmesraim, Luto do Egito”.', 11, b'0', 50, 1),
	(1507, 'Os filhos de Jacó fizeram com o pai assim como ele os havia instruído.', 12, b'0', 50, 1),
	(1508, 'Levaram-no a Canaã e o sepultaram na gruta do campo de Macpela, que Abraão tinha comprado ao heteu Efron, como propriedade funerária diante de Mambré.', 13, b'0', 50, 1),
	(1509, 'Depois de sepultar o pai, José voltou para o Egito com os irmãos e com todos os que haviam subido com ele para o enterro do pai.', 14, b'0', 50, 1),
	(1510, 'Ao verem que o pai tinha morrido, os irmãos de José disseram entre si: “Não aconteça que José se lembre da injúria que sofreu e nos faça pagar todo o mal que lhe fizemos”.', 15, b'0', 50, 1),
	(1511, 'E mandaram dizer a José: “Teu pai, antes de morrer, ordenou-nos', 16, b'0', 50, 1),
	(1512, 'que te disséssemos estas palavras: ‘Peço-te que esqueças o crime de teus irmãos, o pecado e a maldade que usaram contra ti’. Portanto, perdoa o crime dos servidores do Deus de teu pai”. Ouvindo isso, José pôs-se a chorar.', 17, b'0', 50, 1),
	(1513, 'Vieram seus irmãos e prostraram-se diante dele, dizendo: “Somos teus servos”.', 18, b'0', 50, 1),
	(1514, 'José lhes disse: “Não tenhais medo! Estou eu, por acaso, no lugar de Deus?', 19, b'0', 50, 1),
	(1515, 'Vós planejastes fazer o mal contra mim. Deus, porém, converteu-o em bem: quis exaltar-me para dar vida a um povo numeroso, como hoje estais vendo.', 20, b'0', 50, 1),
	(1516, 'Não temais, pois. Continuarei sustentando-vos junto com os vossos filhos”. Assim os confortou, falando-lhes com doçura e mansidão.', 21, b'0', 50, 1),
	(1517, 'José ficou morando no Egito junto com a família de seu pai e viveu cento e dez anos.', 22, b'0', 50, 1),
	(1518, 'José viu os filhos de Efraim até à terceira geração, bem como os filhos de Maquir filho de Manassés. Ao nascerem, adotou-os, recebendo-os sobre os joelhos.', 23, b'0', 50, 1),
	(1519, 'Depois José disse a seus irmãos: “Eu vou morrer, mas Deus intervirá em vosso favor e vos fará subir deste país para a terra que ele jurou dar a Abraão, Isaac e Jacó”.', 24, b'0', 50, 1),
	(1520, 'José fez os filhos de Israel jurarem, dizendo-lhes: “Quando Deus vos visitar, levai daqui meus ossos convosco”.', 25, b'0', 50, 1),
	(1521, 'José morreu no Egito aos cento e dez anos; foi embalsamado e posto num sarcófago no Egito.', 26, b'0', 50, 1),
	(1522, 'Estes são os nomes dos filhos de Israel que vieram com Jacó para o Egito, cada um com sua família:', 1, b'0', 51, 1),
	(1523, 'Rúben, Simeão, Levi e Judá;', 2, b'0', 51, 1),
	(1524, 'Issacar, Zabulon e Benjamim;', 3, b'0', 51, 1),
	(1525, 'Dã e Neftali, Gad e Aser.', 4, b'0', 51, 1),
	(1526, 'Os descendentes diretos de Jacó eram setenta ao todo. Isso era quando José já estava no Egito.', 5, b'0', 51, 1),
	(1527, 'Depois morreu José, assim como seus irmãos e toda aquela geração.', 6, b'0', 51, 1),
	(1528, 'Os israelitas foram fecundos, proliferaram, multiplicaram-se e tornaram-se cada vez mais poderosos, de modo que o país ficou repleto deles.', 7, b'0', 51, 1),
	(1529, 'Surgiu no Egito um novo rei, que não conhecera José.', 8, b'0', 51, 1),
	(1530, 'Ele disse a seu povo: “Olhai como a população israelita ficou mais numerosa e mais forte do que nós.', 9, b'0', 51, 1),
	(1531, 'Vamos tomar providências em relação a eles, para impedir que continuem crescendo e, em caso de guerra, se unam aos nossos inimigos, lutem contra nós e acabem saindo do país”.', 10, b'0', 51, 1),
	(1532, 'Estabeleceram, assim, feitores de trabalho forçado para que os oprimissem com tarefas compulsórias. Foi assim que construíram para o faraó as cidades-entreposto de Pitom e Ramsés.', 11, b'0', 51, 1),
	(1533, 'Mas, quanto mais os oprimiam, tanto mais cresciam e se multiplicavam.', 12, b'0', 51, 1),
	(1534, 'Obcecados pelo medo dos israelitas, os egípcios impuseram-lhes uma dura escravidão.', 13, b'0', 51, 1),
	(1535, 'Tornaram-lhes a vida amarga com o pesado trabalho de preparar barro e tijolos e com toda sorte de trabalhos no campo e outros serviços, que lhes impunham à força.', 14, b'0', 51, 1),
	(1536, 'Depois, o rei do Egito disse às parteiras dos hebreus, chamadas Sefra e Fua:', 15, b'0', 51, 1),
	(1537, '“Quando assistirdes as mulheres hebréias no parto e chegar o tempo do parto, se for menino, matai- o; se for menina, deixai-a viver”.', 16, b'0', 51, 1),
	(1538, 'Mas as parteiras tinham temor de Deus: não faziam o que o rei do Egito lhes tinha mandado e  2 deixavam viver os meninos.', 17, b'0', 51, 1),
	(1539, 'Então o rei do Egito mandou chamar as parteiras e lhes disse: “Por que agistes desse modo e deixastes os meninos viver?”', 18, b'0', 51, 1),
	(1540, 'As parteiras responderam ao faraó: “As mulheres hebréias não são como as egípcias. Elas são robustas e, antes de a parteira chegar, já dão à luz”.', 19, b'0', 51, 1),
	(1541, 'Deus recompensou as parteiras. O povo continuou crescendo e tornando-se muito forte.', 20, b'0', 51, 1),
	(1542, 'Como as parteiras temeram a Deus, deu-lhes também família.', 21, b'0', 51, 1),
	(1543, 'Então o faraó deu esta ordem a todo o seu povo: “Lançai ao rio todos os meninos hebreus recém-nascidos, mas poupai a vida das meninas”.', 22, b'0', 51, 1),
	(1544, 'Um homem da casa de Levi casou-se com uma mulher de seu clã.', 1, b'0', 52, 1),
	(1545, 'A mulher concebeu e deu à luz um filho. Ao ver que era um belo menino, manteve-o escondido durante três meses.', 2, b'0', 52, 1),
	(1546, 'Não podendo escondê-lo por mais tempo, pegou uma cesta de papiro, calafetou-a com betume e piche, pôs dentro dela o menino e deixou-o entre os juncos na margem do rio.', 3, b'0', 52, 1),
	(1547, 'A irmã do menino ficou parada à distância para ver o que ia acontecer.', 4, b'0', 52, 1),
	(1548, 'A filha do faraó desceu para se banhar no rio, enquanto suas companheiras passeavam na margem. Ela viu a cesta no meio dos juncos e mandou que uma criada a apanhasse.', 5, b'0', 52, 1),
	(1549, 'Quando abriu a cesta, viu a criança: era um menino, que chorava. Ficou com pena dele e disse: “É uma das crianças dos hebreus”.', 6, b'0', 52, 1),
	(1550, 'A irmã do menino disse, então, à filha do faraó: “Queres que te vá chamar uma mulher hebréia, que possa amamentar o menino?”', 7, b'0', 52, 1),
	(1551, '“Vai”, respondeu-lhe a filha do faraó. E a menina foi chamar a mãe do menino.', 8, b'0', 52, 1),
	(1552, 'A filha do faraó disse à mulher: “Leva este menino, amamenta-o para mim, e eu te pagarei o teu salário”. A mulher levou o menino e o criou.', 9, b'0', 52, 1),
	(1553, 'Quando o menino estava crescido, levou-o à filha do faraó, que o adotou como filho. Ela deu- lhe o nome de Moisés, porque, disse ela, “eu o tirei das águas”.', 10, b'0', 52, 1),
	(1554, 'Certo dia, quando já adulto, Moisés dirigiu-se para junto de seus irmãos hebreus e viu sua aflição e como um egípcio maltratava um deles.', 11, b'0', 52, 1),
	(1555, 'Olhou para os lados e, não vendo ninguém, matou o egípcio e escondeu-o na areia.', 12, b'0', 52, 1),
	(1556, 'No dia seguinte, saiu de novo e viu dois hebreus brigando. Disse ao agressor: “Por que bates no teu companheiro?”', 13, b'0', 52, 1),
	(1557, 'Ele respondeu: “Quem foi que te nomeou chefe e juiz sobre nós? Queres, talvez, matar-me como mataste o  3 egípcio?” Então Moisés assustou-se e disse consigo: “Com certeza o fato tornou-se conhecido”.', 14, b'0', 52, 1),
	(1558, 'Quando o faraó soube do acontecido, procurou matar Moisés. Este, porém, fugiu do faraó e foi parar na terra de Madiã. Ali ficou sentado junto a um poço.', 15, b'0', 52, 1),
	(1559, 'Ora, o sacerdote de Madiã tinha sete filhas. Estas vieram tirar água e encher os bebedouros para dar de beber ao rebanho do pai.', 16, b'0', 52, 1),
	(1560, 'Chegaram uns pastores e queriam expulsá-las. Mas Moisés levantou-se em defesa delas e deu de beber ao rebanho.', 17, b'0', 52, 1),
	(1561, 'Ao voltarem para junto de Ragüel, seu pai, este lhes perguntou: “Por que voltastes mais cedo hoje?”', 18, b'0', 52, 1),
	(1562, 'Elas responderam: “É que um egípcio nos livrou dos pastores; ele mesmo tirou água para nós e deu de beber ao rebanho”.', 19, b'0', 52, 1),
	(1563, 'Elas responderam: “É que um egípcio nos livrou dos pastores; ele mesmo tirou água para nós e deu de beber ao rebanho”.', 20, b'0', 52, 1),
	(1564, 'Moisés concordou em morar com ele, e Ragüel deu-lhe sua filha  Séfora em casamento.', 21, b'0', 52, 1),
	(1565, 'Ela teve um filho, a quem ele chamou Gérson, pois disse: “Tornei- me hóspede em terra estrangeira”.', 22, b'0', 52, 1),
	(1566, 'Passado muito tempo, morreu o rei do Egito. Os israelitas continuavam gemendo e clamando sob dura escravidão, e, do meio da escravidão, seu grito de socorro subiu até Deus.', 23, b'0', 52, 1),
	(1567, 'Deus ouviu os seus lamentos e lembrou-se da aliança com Abraão, Isaac e Jacó.', 24, b'0', 52, 1),
	(1568, 'Deus olhou para os israelitas e tomou conhecimento.', 25, b'0', 52, 1),
	(1569, 'Moisés era pastor das ovelhas de Jetro, seu sogro, sacerdote de Madiã. Certo dia, levou as ovelhas deserto adentro e chegou ao monte de Deus, o Horeb.', 1, b'0', 53, 1),
	(1570, 'Apareceu-lhe o anjo do Senhor numa chama de fogo, do meio de uma sarça. Moisés notou que a sarça estava em chamas, mas não se consumia.', 2, b'0', 53, 1),
	(1571, 'Pensou: “Vou aproximar-me para admirar esta visão maravilhosa: como é que a sarça não pára de queimar?”', 3, b'0', 53, 1),
	(1572, 'Vendo o Senhor que Moisés se aproximava para observar, Deus o chamou do meio da sarça: “Moisés! Moisés!” Ele  4 respondeu: “Aqui estou!”', 4, b'0', 53, 1),
	(1573, 'Deus lhe disse: “Não te aproximes daqui! Tira as sandálias dos pés, porque o lugar onde estás é chão sagrado”.', 5, b'0', 53, 1),
	(1574, 'E acrescentou: “Eu sou o Deus de teu pai, o Deus de Abraão, o Deus de Isaac, o Deus de Jacó”. Moisés cobriu o rosto, pois temia olhar para Deus.', 6, b'0', 53, 1),
	(1575, 'O Senhor lhe disse: “Eu vi a opressão de meu povo no Egito, ouvi o grito de aflição diante dos opressores e tomei conhecimento de seus sofrimentos.', 7, b'0', 53, 1),
	(1576, 'Desci para libertá-los das mãos dos egípcios e fazê-los sair desse país para uma terra boa e espaçosa, terra onde corre leite e mel: para a região dos cananeus e dos heteus, dos amorreus e dos fereseus, dos heveus e dos jebuseus.', 8, b'0', 53, 1),
	(1577, 'O grito de aflição dos israelitas chegou até mim. Eu vi a opressão que os egípcios fazem pesar sobre eles.', 9, b'0', 53, 1),
	(1578, 'E agora, vai! Eu te envio ao faraó para que faças sair o meu povo, os israelitas, do Egito”.', 10, b'0', 53, 1),
	(1579, 'Moisés disse a Deus: “Quem sou eu para ir ao faraó e fazer sair os israelitas do Egito?”', 11, b'0', 53, 1),
	(1580, 'Deus lhe disse: “Eu estarei contigo; e este será para ti o sinal de que eu te envio: quando tiveres tirado do Egito o povo, vós servireis a Deus sobre esta montanha”.', 12, b'0', 53, 1),
	(1581, 'Moisés disse a Deus: “Mas, se eu for aos israelitas e lhes disser: ‘O Deus de vossos pais enviou-me a vós’, e eles me perguntarem: ‘Qual é o seu nome?’, que devo responder?”', 13, b'0', 53, 1),
	(1582, 'Deus disse a Moisés: “Eu sou aquele que sou”. E acrescentou: “Assim responderás aos israelitas: ‘Eu sou’ envia-me a vós”.', 14, b'0', 53, 1),
	(1583, 'Deus disse ainda a Moisés: “Assim dirás aos israelitas: O Senhor, o Deus de vossos pais, o Deus de Abraão, Deus de Isaac e Deus de Jacó, enviou-me a vós. Este é o meu nome para sempre, e assim serei lembrado de geração em geração.', 15, b'0', 53, 1),
	(1584, 'Vai e reúne os anciãos de Israel para dizer-lhes: ‘O Senhor, o Deus de vossos pais, o Deus de Abraão, de Isaac e de Jacó, apareceu-me, dizendo: Eu vos visitei e vi tudo o que vos sucede no Egito.', 16, b'0', 53, 1),
	(1585, 'Decidi, portanto, tirar-vos da opressão egípcia e conduzir-vos à terra dos cananeus, dos heteus, dos amorreus, dos fereseus, dos heveus e dos jebuseus, terra onde corre leite e mel!’', 17, b'0', 53, 1),
	(1586, 'Eles te escutarão, e tu, com os anciãos de Israel, irás ao rei do Egito. Então lhe direis: ‘O Senhor, o Deus dos hebreus, marcou um encontro conosco. Deixa-nos, pois, caminhar três dias deserto adentro, a  5 fim de oferecer sacrifícios ao Senhor nosso Deus’.', 18, b'0', 53, 1),
	(1587, 'Bem sei que o rei do Egito não vos deixará partir, se não for obrigado por mão poderosa.', 19, b'0', 53, 1),
	(1588, 'Mas eu estenderei minha mão e castigarei o Egito com toda sorte de prodígios que farei no meio deles. Depois disso, vos mandará sair.', 20, b'0', 53, 1),
	(1589, 'Farei este povo conquistar as boas graças dos egípcios, de modo que, ao sairdes, não ireis de mãos vazias.', 21, b'0', 53, 1),
	(1590, 'Cada mulher pedirá à vizinha e à mulher que mora em sua casa objetos de prata e de ouro e vestidos, que poreis em vossos filhos e em vossas filhas, levando assim os despojos do Egito”.', 22, b'0', 53, 1),
	(1591, 'Moisés respondeu: “Mas se eles não acreditarem em mim, nem me atenderem, mas disserem:‘O Senhor não te apareceu’?”', 1, b'0', 54, 1),
	(1592, 'O Senhor perguntou-lhe: “O que tens na mão?” – “Uma vara”, respondeu.', 2, b'0', 54, 1),
	(1593, '“Joga-a no chão”, disse o Senhor. Ele jogou-a no chão, e a vara se tornou uma serpente. Moisés recuou diante dela.', 3, b'0', 54, 1),
	(1594, '“Joga-a no chão”, disse o Senhor. Ele jogou-a no chão, e a vara se tornou uma serpente. Moisés recuou diante dela.', 4, b'0', 54, 1),
	(1595, '“É para eles acreditarem que o Senhor, o Deus de seus pais, o Deus de Abraão, de Isaac e de Jacó te apareceu”.', 5, b'0', 54, 1),
	(1596, 'Disse-lhe ainda o Senhor: “Mete a mão no peito”. Ele meteu a mão e, quando a tirou,estava coberta de lepra, branca como a neve.', 6, b'0', 54, 1),
	(1597, 'O Senhor lhe disse: “Mete de novo a mão no peito”. Ele a meteu novamente e, ao tirá-la,a mão estava normal como o resto do corpo.', 7, b'0', 54, 1),
	(1598, '“Se não acreditarem em ti nem te escutarem ao primeiro sinal, acreditarão à vista do segundo.', 8, b'0', 54, 1),
	(1599, 'Mas se não acreditarem nem mesmo com estes dois sinais e não te escutarem, apanharás água do rio e a derramarás em terra seca; a água que apanhares virará sangue na terra seca”.', 9, b'0', 54, 1),
	(1600, 'Moisés disse ao Senhor: “Pobre de mim, Senhor! Nunca tive facilidade para falar, nem antes, nem agora que falas a teu servo. Tenho boca e língua pesadas”.', 10, b'0', 54, 1),
	(1601, 'O Senhor respondeu-lhe: “E quem é que dá a boca ao ser humano? Quem faz o surdo e o mudo, o cego e o aquele que vê? Por acaso não sou eu, o Senhor?', 11, b'0', 54, 1),
	(1602, 'Vai, portanto, que eu estarei com tua boca e te ensinarei o que deverás dizer”.', 12, b'0', 54, 1),
	(1603, 'Moisés replicou: “Pobre de mim, Senhor! Por favor, manda um outro”.', 13, b'0', 54, 1),
	(1604, 'O Senhor ficou irritado com Moisés e disse: “Não tens teu irmão Aarão, o levita? Eu sei que ele fala muito bem. Ele está vindo pessoalmente a teu encontro e ficará alegre em te ver.', 14, b'0', 54, 1),
	(1605, 'Tu lhe falarás e lhe transmitirás as mensagens, e eu estarei com os dois para falardes, e vos mostrarei o que deveis fazer.', 15, b'0', 54, 1),
	(1606, 'Ele  6 falará por ti ao povo e será teu porta voz, e tu serás um deus para ele.', 16, b'0', 54, 1),
	(1607, 'Leva contigo esta vara. É com ela que deverás realizar os sinais”.', 17, b'0', 54, 1),
	(1608, 'Moisés retornou para junto de seu sogro Jetro e disse-lhe: “Quero voltar aos meus irmãos no Egito, para ver se ainda vivem”. Jetro disse a Moisés: “Vai em paz”.', 18, b'0', 54, 1),
	(1609, 'Ainda na terra de Madiã, o Senhor disse a Moisés: “Volta ao Egito, pois já morreram todos os que queriam tirar-te a vida”.', 19, b'0', 54, 1),
	(1610, 'Moisés levou consigo a mulher e os filhos, ajudou-os a montar num jumento, e voltou ao Egito. Moisés levava na mão a vara de poder divino.', 20, b'0', 54, 1),
	(1611, 'O Senhor lhe disse: “Voltando ao Egito, cuida de fazer diante do faraó todos os prodígios que pus à tua disposição. Mas eu endurecerei o seu coração, e ele não deixará o povo partir.', 21, b'0', 54, 1),
	(1612, 'Tu lhe dirás: ‘Assim fala o Senhor: Israel é meu filho, meu primogênito.', 22, b'0', 54, 1),
	(1613, 'Por isso eu te ordeno que deixes partir o meu filho para servir-me. Se te recusares a deixá-lo partir, eu matarei teu filho primogênito’”. Moisés não pode agir incircunciso', 23, b'0', 54, 1),
	(1614, 'Durante a viagem, num lugar de ousada, o Senhor encontrou-se com Moisés e queria matá-lo.', 24, b'0', 54, 1),
	(1615, 'Séfora, então, pegou uma faca de pedra, cortou o prepúcio do filho, tocou-o nas virilhas de Moisés e disse: “Tu és para mim um marido de sangue”.', 25, b'0', 54, 1),
	(1616, 'E o Senhor deixou-o em paz, quando ela disse “marido de sangue”, em relação à circuncisão. Moisés e Aarão com os anciãos', 26, b'0', 54, 1),
	(1617, 'O Senhor disse para Aarão: “Vai ao encontro de Moisés no deserto”. Aarão foi, encontrou-se com o irmão na montanha de Deus e beijou-o.', 27, b'0', 54, 1),
	(1618, 'Moisés contou a Aarão tudo o que o Senhor lhe tinha dito ao incumbi-lo da missão. Falou-lhes também dos sinais que lhe havia mandado fazer.', 28, b'0', 54, 1),
	(1619, 'Moisés e Aarão foram e reuniram todos os anciãos dos israelitas.', 29, b'0', 54, 1),
	(1620, 'Aarão contou tudo o que o Senhor havia dito a Moisés, e este realizou os sinais à vista do povo,', 30, b'0', 54, 1),
	(1621, 'e o povo acreditou. E ao ouvir que o Senhor dava atenção aos israelitas e olhava para sua aflição, prostraram-se em adoração.', 31, b'0', 54, 1),
	(1622, 'Em seguida, Moisés e Aarão apresentaram-se ao faraó e lhe disseram: “Assim diz o Senhor, Deus de Israel: Deixa partir o meu povo, para que me celebre uma festa no deserto”.', 1, b'0', 55, 1),
	(1623, 'Mas o faraó respondeu: “E quem é ‘o Senhor’ para que eu lhe deva obedecer, deixando Israel partir?  7 Não conheço ‘o Senhor’, nem deixarei Israel partir”.', 2, b'0', 55, 1),
	(1624, 'Eles disseram: “O Deus dos hebreus veio ao nosso encontro. Deixa-nos ir a três dias de caminho no deserto, para oferecermos sacrifícios ao Senhor nosso Deus. Do contrário, a peste e a espada nos atingirão”.', 3, b'0', 55, 1),
	(1625, 'Mas o rei do Egito lhes disse: “Por que vós, Moisés e Aarão, levais o povo a descuidar dos seus trabalhos? Ide para vossas tarefas!”', 4, b'0', 55, 1),
	(1626, 'Mas o rei do Egito lhes disse: “Por que vós, Moisés e Aarão, levais o povo a descuidar dos seus trabalhos? Ide para vossas tarefas!”', 5, b'0', 55, 1),
	(1627, 'Naquele mesmo dia o faraó deu aos inspetores do povo e aos capatazes a seguinte ordem:', 6, b'0', 55, 1),
	(1628, '“Não forneçais mais palha a essa gente para fazer tijolos, como antes fazíeis. Eles mesmos devem ir juntar a palha.', 7, b'0', 55, 1),
	(1629, 'Mas exigi a mesma quantia de tijolos de costume, sem tirar nada. São uns preguiçosos e por isso reclamam: ‘Queremos ir oferecer sacrifícios ao nosso Deus’.', 8, b'0', 55, 1),
	(1630, 'Carregai esses homens com mais trabalho, para que estejam ocupados e não dêem ouvidos a palavras mentirosas”.', 9, b'0', 55, 1),
	(1631, 'Os inspetores e os capatazes foram, pois, dizer ao povo: “Assim diz o faraó: Não vos darei mais a palha.', 10, b'0', 55, 1),
	(1632, 'Devereis ir recolher a palha onde a puderdes encontrar. Nada, porém, será diminuído do vosso serviço”.', 11, b'0', 55, 1),
	(1633, 'O povo espalhou-se por todo o Egito em busca de palha.', 12, b'0', 55, 1),
	(1634, 'Mas os inspetores pressionavam-nos dizendo:“Terminai a tarefa marcada para cada dia, como quando havia palha”.', 13, b'0', 55, 1),
	(1635, 'Os inspetores do faraó açoitaram os capatazes israelitas que eles haviam nomeado, alegando: “Por que nem ontem, nem hoje, completastes a quota costumeira de tijolos que produzíeis antes?”', 14, b'0', 55, 1),
	(1636, 'Os capatazes israelitas foram queixar-se ao faraó, dizendo: “Como podes proceder assim com teus servos?', 15, b'0', 55, 1),
	(1637, 'Não se fornece palha a teus servos, e nos mandam fazer tijolos. Nós somos açoitados, mas o culpado é a tua gente”.', 16, b'0', 55, 1),
	(1638, 'O faraó respondeu: “Sois mesmo uns preguiçosos e por isso dizeis: ‘Queremos ir oferecer sacrifícios ao Senhor’.', 17, b'0', 55, 1),
	(1639, 'E, agora,ide trabalhar! Não vos será dada a palha, mas devereis produzir a mesma quantia de tijolos”.', 18, b'0', 55, 1),
	(1640, 'Os capatazes israelitas se viram em má situação com a ordem de não diminuir em nada a quota diária de tijolos.', 19, b'0', 55, 1),
	(1641, 'Encontraram-se com Moisés e Aarão, que os estavam esperando na saída do palácio do faraó,', 20, b'0', 55, 1),
	(1642, 'e lhes disseram: “Que o Senhor vos examine e julgue: vós nos tornastes odiosos diante do faraó e dos seus servidores e lhes pusestes na mão a espada para nos matar”.', 21, b'0', 55, 1),
	(1643, 'Então Moisés voltou-se para o Senhor, dizendo: “Meu Senhor, por que maltratas este povo? Para que foi que me enviaste?', 22, b'0', 55, 1),
	(1644, 'Desde que me apresentei ao faraó para lhe falar em teu nome, ele ficou maltratando o povo, e tu nada fizeste para libertá-lo”.', 23, b'0', 55, 1),
	(1645, 'O Senhor disse a Moisés: “Agora verás o que vou fazer ao faraó. Por mão poderosa será forçado a deixá-los ir; será coagido a expulsá-los do país”. Renovação da vocação de Moisés', 1, b'0', 56, 1),
	(1646, 'Deus falou a Moisés e lhe disse: “Eu sou o Senhor.', 2, b'0', 56, 1),
	(1647, 'Apareci a Abraão, a Isaac e a Jacó como o Deus Poderoso, mas não lhes dei a conhecer meu nome ‘o Senhor’.', 3, b'0', 56, 1),
	(1648, 'Com eles estabeleci a minha aliança, para dar-lhes a terra de Canaã, a terra em que viveram como migrantes e estrangeiros.', 4, b'0', 56, 1),
	(1649, '5 Eu também ouvi os gemidos dos israelitas, que os egípcios escravizaram, e lembrei-me da minha aliança.', 5, b'0', 56, 1),
	(1650, 'Dize, portanto, aos israelitas: Eu sou o Senhor. Eu vos tirarei dos trabalhos impostos pelos egípcios, vos libertarei da escravidão e vos resgatarei com braço estendido e grandiosos atos de juízo.', 6, b'0', 56, 1),
	(1651, 'Eu vos tomarei como meu povo e serei o vosso Deus. Assim sabereis que eu sou o Senhor, vosso Deus, que vos liberta dos trabalhos impostos pelos egípcios.', 7, b'0', 56, 1),
	(1652, 'Eu vos introduzirei na terra que, com mão levantada, jurei dar a Abraão, a Isaac e a Jacó, e vo-la darei em possessão – eu, o Senhor”.', 8, b'0', 56, 1),
	(1653, 'Moisés falou deste modo aos israelitas, mas eles não o escutaram, porque estavam com o ânimo abatido pela dura escravidão.', 9, b'0', 56, 1),
	(1654, 'O Senhor falou, então, a Moisés e lhe disse:', 10, b'0', 56, 1),
	(1655, '“Vai falar com o faraó, rei do Egito, para que deixe sair os israelitas do país.', 11, b'0', 56, 1),
	(1656, 'Mas Moisés protestou diante do Senhor: “Se nem os israelitas me escutam, como me atenderá o faraó, a mim que tenho dificuldade de falar?”', 12, b'0', 56, 1),
	(1657, 'O Senhor falou a Moisés e a Aarão e deu-lhes ordens para os israelitas e para o faraó, rei do Egito, com o fim de os fazer sair do Egito.', 13, b'0', 56, 1),
	(1658, 'Estes são os chefes das casas patriarcais: Filhos de Rúben, primogênito de Israel: Henoc, Falu, Hesron e Carmi; são esses os clãs de Rúben.', 14, b'0', 56, 1),
	(1659, 'Filhos de Simeão: Jamuel, Jamin, Aod, Jaquin, Soar e Saul, filho de uma cananéia; são esses os clãs de Simeão.', 15, b'0', 56, 1),
	(1660, 'Estes são os nomes dos filhos de Levi, segundo as descendências: Gérson, Caat e Merari. Levi viveu cento e trinta e sete anos.', 16, b'0', 56, 1),
	(1661, 'Filhos de Gérson: Lobni e Semei, segundo seus clãs.', 17, b'0', 56, 1),
	(1662, 'Filhos de Caat: Amram, Isaar, Hebron e Oziel. Caat viveu cento e trinta e três anos.', 18, b'0', 56, 1),
	(1663, 'Filhos de Merari: Mooli e Musi. São esses os clãs dos levitas, segundo suas descendências.', 19, b'0', 56, 1),
	(1664, 'Amram casou-se com Jocabed, sua tia, da qual lhe nasceram Aarão e Moisés. Amram viveu cento e trinta e sete anos.', 20, b'0', 56, 1),
	(1665, 'Filhos de Isaar: Coré, Nefeg e Zecri.', 21, b'0', 56, 1),
	(1666, 'Filhos de Oziel: Misael, Elisafã e Setri.', 22, b'0', 56, 1),
	(1667, 'Aarão casou-se com Isabel, filha de Aminadab e irmã de Naasson; dela lhe nasceram Nadab e Abiú, Eleazar e Itamar.', 23, b'0', 56, 1),
	(1668, 'Aarão casou-se com Isabel, filha de Aminadab e irmã de Naasson; dela lhe nasceram Nadab e Abiú, Eleazar e Itamar.', 24, b'0', 56, 1),
	(1669, 'Eleazar filho de Aarão casou-se com uma das filhas de Futiel, da qual lhe nasceu Finéias. São esses os chefes das casas dos levitas, segundo seus clãs.', 25, b'0', 56, 1),
	(1670, 'Tais  9 são, portanto, Aarão e Moisés a quem o Senhor disse: “Fazei sair do Egito os israelitas, por exércitos”.', 26, b'0', 56, 1),
	(1671, 'Foram os mesmos Moisés e Aarão que falaram ao faraó, rei do Egito, para fazer sair os israelitas do Egito.', 27, b'0', 56, 1),
	(1672, 'No dia em que o Senhor falou a Moisés, no Egito,', 28, b'0', 56, 1),
	(1673, 'disse-lhe: “Eu sou o Senhor. Transmite ao faraó, rei do Egito, tudo o que te digo”.', 29, b'0', 56, 1),
	(1674, 'E Moisés respondeu ao Senhor: “Tenho dificuldade de falar. Como me ouvirá o faraó?”', 30, b'0', 56, 1),
	(1675, 'O Senhor disse a Moisés: “Olha, eu te faço \\como um deus para o faraó, e Aarão, teu irmão, será teu profeta.', 1, b'0', 57, 1),
	(1676, 'Dirás tudo o que eu mandar, e teu irmão Aarão falará ao faraó, para que deixe sair os israelitas do país.', 2, b'0', 57, 1),
	(1677, 'Quanto a mim, vou endurecer o coração do faraó e multiplicar sinais e prodígios no Egito.', 3, b'0', 57, 1),
	(1678, 'O faraó não vos atenderá, mas eu porei minha mão sobre o Egito e farei sair do Egito os meus exércitos, o meu povo, os israelitas, com grandiosos atos de juízo.', 4, b'0', 57, 1),
	(1679, 'Os egípcios ficarão sabendo que eu sou o Senhor, quando eu estender minha mão contra o Egito e tirar os israelitas do meio deles”.', 5, b'0', 57, 1),
	(1680, 'Moisés e Aarão fizeram exatamente o que o Senhor lhes havia ordenado.', 6, b'0', 57, 1),
	(1681, 'Moisés tinha oitenta anos, e Aarão oitenta e três, quando foram falar ao faraó.', 7, b'0', 57, 1),
	(1682, 'O Senhor disse a Moisés e Aarão:', 8, b'0', 57, 1),
	(1683, '“Quando o faraó vos pedir que façais algum prodígio, mandarás Aarão pegar a vara e jogá-la diante do faraó, e ela se transformará em serpente”.', 9, b'0', 57, 1),
	(1684, 'Moisés e Aarão se apresentaram ao faraó e fizeram como o Senhor tinha mandado. Aarão jogou a vara diante do faraó e de seus ministros, e a vara virou uma serpente.', 10, b'0', 57, 1),
	(1685, 'Mas o faraó convocou os sábios e os feiticeiros, e também eles – os magos do Egito – fizeram o mesmo com seus encantamentos:', 11, b'0', 57, 1),
	(1686, 'cada qual jogou sua vara, que se transformava em serpente. Mas a vara de Aarão engoliu as varas dos outros.', 12, b'0', 57, 1),
	(1687, 'Todavia o coração do faraó ficou endurecido, e ele não lhes atendeu o pedido, conforme o Senhor tinha predito.', 13, b'0', 57, 1),
	(1688, 'O Senhor disse a Moisés: “O coração do faraó endureceu e ele não quer deixar o povo partir.', 14, b'0', 57, 1),
	(1689, 'Vai ao faraó amanhã cedo. Quando ele sair para a água, estarás à sua espera à beira do rio, levando contigo a vara que foi transformada em serpente.', 15, b'0', 57, 1),
	(1690, 'Tu lhe dirás: ‘O Senhor, o Deus dos hebreus, enviou-me a ti com esta ordem: Deixa partir o meu povo para me prestar culto no deserto. Mas até agora não me escutaste.', 16, b'0', 57, 1),
	(1691, 'Portanto, assim diz o Senhor: Deste modo saberás que eu sou o Senhor: com a vara que tenho na mão vou bater nas águas do rio Nilo, e elas se mudarão em sangue.', 17, b'0', 57, 1),
	(1692, 'Os peixes que estão no rio morrerão, e o rio ficará tão poluído que os egípcios sentirão nojo de beber a água do Nilo’”.', 18, b'0', 57, 1),
	(1693, 'O Senhor disse a Moisés: “Dize a Aarão: ‘Toma a vara na mão e estende a mão sobre as águas do Egito: sobre os rios, os canais, os pântanos e sobre todos os reservatórios de água. Toda a água se transformará em sangue, e haverá sangue por todo o Egito, até mesmo nas vasilhas de madeira e nos recipientes de pedra”.', 19, b'0', 57, 1),
	(1694, 'Moisés e Aarão fizeram como o Senhor lhes tinha ordenado. Erguendo a vara, Aarão feriu as águas do Nilo à vista do faraó e de todos os seus ministros, e toda a água do rio virou sangue.', 20, b'0', 57, 1),
	(1695, 'Morreram os peixes que havia no rio, e o rio ficou poluído, de modo que os egípcios não podiam beber de sua água, e houve sangue em toda a terra do Egito.', 21, b'0', 57, 1),
	(1696, 'Mas os magos do Egito fizeram o mesmo com seus encantamentos, de modo que o coração do faraó continuou endurecido e ele não atendeu ao pedido de Moisés e Aarão, conforme o Senhor tinha dito.', 22, b'0', 57, 1),
	(1697, 'O faraó retornou ao palácio sem preocupar-se com o caso.', 23, b'0', 57, 1),
	(1698, 'Os egípcios cavaram nas margens do rio à procura de água potável, pois não podiam beber da água do rio.', 24, b'0', 57, 1),
	(1699, 'Passados sete dias depois de ter ferido o rio Nilo,', 25, b'0', 57, 1),
	(1700, 'O Senhor disse a Moisés: “Apresenta- te ao faraó e dize-lhe: Deixa partir o meu povo para me prestar culto.', 26, b'0', 57, 1),
	(1701, 'Se te recusares a  deixá-lo ir, vou infestar de rãs todo o teu território.', 27, b'0', 57, 1),
	(1702, 'O rio fervilhará de rãs. Elas sairão do rio e penetrarão em teu palácio, no quarto de dormir e até sobre o leito; nas casas dos ministros e do povo, até nos fornos e nas amassadeiras.', 28, b'0', 57, 1),
	(1703, 'As rãs virão sobre ti, sobre os ministros e sobre todo o povo”.', 29, b'0', 57, 1),
	(1704, 'Então o Senhor disse a Moisés: “Dize a Aarão: Estende com a mão a vara sobre os rios, os canais e os pântanos, e faze as rãs invadir o Egito”.', 1, b'0', 58, 1),
	(1705, 'Aarão estendeu a mão sobre as águas do Egito, e as rãs saíram e cobriram o Egito.', 2, b'0', 58, 1),
	(1706, 'Os magos, porém, conseguiram o mesmo com seus encantamentos, fazendo as rãs subir por sobre o Egito.', 3, b'0', 58, 1),
	(1707, 'O faraó chamou Moisés e Aarão e lhes disse: “Suplicai ao Senhor que afaste as rãs de mim e de meu povo, e eu deixarei vosso povo ir oferecer sacrifícios ao Senhor”.', 4, b'0', 58, 1),
	(1708, 'Moisés disse ao faraó: “Digna-te indicar-me o dia em que devo suplicar por ti, teus ministros e teu povo, para que sejam afastadas as rãs de ti e de teu palácio e fiquem apenas no rio”.', 5, b'0', 58, 1),
	(1709, '“Amanhã”, respondeu ele. Moisés lhe disse: “Será como pedes, para que saibas que não há ninguém como o Senhor nosso Deus.', 6, b'0', 58, 1),
	(1710, 'As rãs se afastarão de ti, de tuas casas, de teus ministros e de teu povo e ficarão apenas no rio”.', 7, b'0', 58, 1),
	(1711, 'Tendo Moisés e Aarão saído da presença do faraó, Moisés suplicou ao Senhor por causa das rãs, como tinha prometido ao faraó.', 8, b'0', 58, 1),
	(1712, 'O Senhor fez como lhe pedia Moisés: morreram as rãs que estavam nas casas, nos pátios e nos campos.', 9, b'0', 58, 1),
	(1713, 'Ajuntavam-se rãs aos montes, e o ar todo ficou poluído.', 10, b'0', 58, 1),
	(1714, 'Mas o faraó, vendo que houve trégua, endureceu o coração e não escutou Moisés e Aarão, conforme o Senhor havia predito.', 11, b'0', 58, 1),
	(1715, 'O Senhor disse a Moisés: “Dize a Aarão: Estende a vara e golpeia a poeira da terra, para que se transforme em mosquitos no Egito inteiro”.', 12, b'0', 58, 1),
	(1716, 'Assim o fizeram. Aarão estendeu a vara com a mão e golpeou o pó do chão, e vieram mosquitos sobre homens e animais. Toda a poeira do chão, no Egito inteiro, transformou-se em mosquitos.', 13, b'0', 58, 1),
	(1717, 'Os magos tentaram fazer o mesmo com encantamentos a fim de produzir mosquitos, mas não foram capazes. Os mosquitos atacavam homens e animais.', 14, b'0', 58, 1),
	(1718, 'Então os magos disseram ao faraó: “Aqui está o dedo de Deus”. Mas o faraó continuou obstinado, conforme o Senhor havia dito, e não os atendeu.', 15, b'0', 58, 1),
	(1719, 'O Senhor disse a Moisés: “Levanta-te cedo, apresenta-te ao faraó quando ele sair para o rio e dize-lhe: Assim diz o Senhor: Deixa partir meu povo para me prestar culto.', 16, b'0', 58, 1),
	(1720, 'Se não deixares meu povo partir, vou manda contra ti, contra os ministros, contra o povo e contra  12 tuas casas, moscas varejeiras. As casas dos egípcios e até mesmo o solo em que pisam ficarão cheias de moscas-varejeiras.', 17, b'0', 58, 1),
	(1721, 'Mas farei nesse dia uma exceção para a terra de Gessen onde habita o meu povo. Ali não haverá moscas-varejeiras, para que saibas que eu, o Senhor, estou nessa terra.', 18, b'0', 58, 1),
	(1722, 'Farei distinção entre o meu povo e o teu. Amanhã se realizará este sinal”.', 19, b'0', 58, 1),
	(1723, 'E assim o Senhor fez: nuvens de moscas-varejeiras invadiram o palácio do faraó, as casas dos ministros e todo o território do Egito. O país ficou infectado por causa das moscas-varejeiras.', 20, b'0', 58, 1),
	(1724, 'O faraó mandou chamar Moisés e Aarão e lhes disse: “Ide oferecer sacrifícios ao vosso Deus sem sair do país”.', 21, b'0', 58, 1),
	(1725, 'Moisés respondeu: “Não convém fazer assim, pois o sacrifício que nós oferecemos ao Senhor nosso Deus é abominação para os egípcios. Se oferecermos à vista dos egípcios sacrifícios que eles abominam, eles vão nos apedrejar.', 22, b'0', 58, 1),
	(1726, 'Temos de caminhar três dias pelo deserto para oferecermos sacrifícios ao Senhor nosso Deus, como ele nos mandou”.', 23, b'0', 58, 1),
	(1727, 'O faraó respondeu: “Eu vos deixarei ir oferecer sacrifícios ao Senhor vosso Deus no deserto, com a condição de não vos afastardes longe demais. Suplicai por mim”.', 24, b'0', 58, 1),
	(1728, 'Moisés respondeu: “Está bem. Ao sair daqui, eu pedirei por ti ao Senhor, e amanhã as moscas-varejeiras se afastarão do faraó, dos ministros e do povo. Mas que o faraó não nos engane de novo não deixando o povo ir oferecer sacrifícios ao Senhor”.', 25, b'0', 58, 1),
	(1729, 'Moisés saiu da presença do faraó e suplicou ao Senhor.', 26, b'0', 58, 1),
	(1730, 'O Senhor fez o que Moisés pedia, de modo que as moscas-varejeiras afastaram-se do faraó, dos ministros e do povo, sem ficar uma só.', 27, b'0', 58, 1),
	(1731, 'Mas o faraó endureceu o coração, ainda desta vez, e não deixou o povo sair.', 28, b'0', 58, 1),
	(1732, 'O Senhor disse a Moisés: “Apresenta-te ao faraó e fala-lhe: Assim diz o Senhor, o Deus dos hebreus: Deixa partir o meu povo para me prestar culto.', 1, b'0', 59, 1),
	(1733, 'Se te recusares a deixá-los partir, persistindo em detê-los,', 2, b'0', 59, 1),
	(1734, 'a mão do Senhor se fará sentir sobre teus rebanhos que estão nos campos, sobre os cavalos, jumentos,camelos, bois e ovelhas, como uma peste mortífera.', 3, b'0', 59, 1),
	(1735, 'Mas o Senhor fará distinção entre os rebanhos de Israel e os rebanhos dos egípcios.Nada do que pertence aos israelitas morrerá.', 4, b'0', 59, 1),
	(1736, 'O Senhor fixou um prazo: Amanhã ele fará isto no país”.', 5, b'0', 59, 1),
	(1737, 'De fato, o Senhor assim fez no dia seguinte. Pereceram todos os rebanhos dos  egípcios, mas não morreu um só animal dos rebanhos israelitas.', 6, b'0', 59, 1),
	(1738, 'O faraó mandou informar- se: de fato, nenhum animal dos israelitas tinha morrido. Mas o coração do faraó manteve-se  endurecido e não deixou o povo partir.', 7, b'0', 59, 1),
	(1739, 'O Senhor disse a Moisés e Aarão: “Recolhei um punhado de fuligem de forno, e que Moisés a jogue para o céu, à vista do faraó.', 8, b'0', 59, 1),
	(1740, 'Ela se tornará, sobre toda a terra do Egito, um pó fino que cairá sobre as pessoas e os animais, formando tumores que provocarão pústulas”.', 9, b'0', 59, 1),
	(1741, 'Eles recolheram fuligem de forno e pararam na frente do faraó. Moisés atirou a fuligem para o céu, provocando tumores e pústulas nas pessoas e nos animais.', 10, b'0', 59, 1),
	(1742, 'Nem os magos puderam comparecer à presença de Moisés devido aos tumores, porque estes se formaram nos magos como nos demais egípcios.', 11, b'0', 59, 1),
	(1743, 'O Senhor endureceu o coração do faraó, que não atendeu ao pedido de Moisés e Aarão, como o Senhor tinha dito a Moisés.', 12, b'0', 59, 1),
	(1744, 'O Senhor disse a Moisés: “Levanta-te cedo, apresenta-te ao faraó e dize-lhe: Assim diz o Senhor, o Deus dos hebreus: Deixa partir o meu povo para me prestar culto.', 13, b'0', 59, 1),
	(1745, 'Pois desta vez vou desencadear todas as minhas pragas contra ti mesmo, teus ministros e teu povo, para que saibas que não há ninguém como eu em toda a terra.', 14, b'0', 59, 1),
	(1746, 'Se agora eu já tivesse estendido a minha mão para te ferir, a ti e a teu povo, com a peste, terias desaparecido da terra.', 15, b'0', 59, 1),
	(1747, 'Entretanto eu te poupei precisamente para mostrar-te o meu poder e para que o meu nome seja celebrado em toda a terra.', 16, b'0', 59, 1),
	(1748, 'Mas tu ainda continuas usando de prepotência contra o meu povo, não o deixando partir!', 17, b'0', 59, 1),
	(1749, 'Pois fica sabendo que amanhã a esta hora farei cair uma chuva de pedra, tão pesada como nunca houve no Egito em toda a sua história.', 18, b'0', 59, 1),
	(1750, 'Manda, pois, pôr a salvo o teu gado e tudo o que tens no campo. Toda pessoa ou animal que se encontrar no campo e não for recolhido sob um teto morrerá quando cair o granizo”.', 19, b'0', 59, 1),
	(1751, 'Alguns dos ministros do faraó que temiam a palavra do Senhor mandaram os escravos e o gado refugiar-se sob um teto.', 20, b'0', 59, 1),
	(1752, 'Mas os que não deram importância à palavra do Senhor deixaram os escravos e o gado no campo.', 21, b'0', 59, 1),
	(1753, 'O Senhor disse a Moisés: “Estende a mão para o céu, para que caia granizo em todo o Egito sobre as pessoas, animais e sobre toda a vegetação do Egito”.', 22, b'0', 59, 1),
	(1754, 'Moisés apontou a vara para o céu, e o Senhor mandou uma trovoada de granizo: caíram raios sobre o país, e o Senhor fez chover granizo sobre o Egito.', 23, b'0', 59, 1),
	(1755, 'Caiu uma chuva de pedra, acompanhada de raios e relâmpagos, tão forte como nunca houve no Egito em toda a sua história.', 24, b'0', 59, 1),
	(1756, 'O granizo castigou, em todo o território do Egito, tudo o que estava nos campos, tanto pessoas como animais. Atingiu também toda a vegetação e destroçou todas as  14 árvores do campo.', 25, b'0', 59, 1),
	(1757, 'Só na terra de Gessen, onde moravam os israelitas, não caiu granizo.', 26, b'0', 59, 1),
	(1758, 'Então o faraó mandou chamar Moisés e Aarão e lhes disse: “Desta vez eu pequei. O Senhor é que está com a razão; eu e o meu povo somos os culpados.', 27, b'0', 59, 1),
	(1759, 'Suplicai ao Senhor! Basta dessas terríveis trovoadas de granizo! Eu vos deixarei partir; não ficareis aqui por mais tempo”.', 28, b'0', 59, 1),
	(1760, 'Moisés disse: “Quando eu tiver saído da cidade estenderei as mãos ao Senhor; cessarão os trovões, e deixará de chover pedras, para que saibas que ao Senhor pertence a terra.', 29, b'0', 59, 1),
	(1761, 'Mas sei que tu e teus ministros ainda não temeis ao Senhor Deus”.', 30, b'0', 59, 1),
	(1762, '(Perderam-se a cevada e o linho, pois a cevada ainda estava em espiga e o linho em flor;', 31, b'0', 59, 1),
	(1763, 'mas o trigo e o centeio não se perderam, por serem tardios.)', 32, b'0', 59, 1),
	(1764, 'Moisés retirou-se da presença do faraó e da cidade com as mãos estendidas ao Senhor. Cessaram as trovoadas e o granizo, e parou de chover sobre a terra.', 33, b'0', 59, 1),
	(1765, 'Vendo o faraó que haviam cessado a chuva, o granizo e os trovões, tornou a pecar. Ele e seus ministros endureceram o coração.', 34, b'0', 59, 1),
	(1766, 'O coração do faraó permaneceu endurecido e não deixou partir os israelitas, como o Senhor havia ordenado por meio de Moisés.', 35, b'0', 59, 1),
	(1767, 'Senhor disse a Moisés: “Apresenta-te ao faraó, porque eu endureci o coração do faraó e de seus ministros para realizar no meio deles os meus prodígios.', 1, b'0', 60, 1),
	(1768, 'Assim poderás contar a teus filhos e netos a maneira implacável como tratei os egípcio se os prodígios que realizei no meio deles. Assim sabereis que eu sou o Senhor”.', 2, b'0', 60, 1),
	(1769, 'Moisés e Aarão apresentaram-se ao faraó e lhe disseram: “Assim diz o Senhor, o Deus dos hebreus: Até quando recusarás submeter-te a mim? Deixa partir o meu povo para me prestar culto.', 3, b'0', 60, 1),
	(1770, 'Se recusares deixar o meu povo partir, amanhã trarei gafanhotos para o teu território.', 4, b'0', 60, 1),
	(1771, 'Eles encobrirão de tal modo a superfície do solo que não se poderá ver o chão. Comerão o resto que sobrou, poupado pelo granizo, devorando todas as árvores que crescem no campo.', 5, b'0', 60, 1),
	(1772, 'Encherão tuas casas, as casas dos ministros e de todos os egípcios, como nunca o viram teus pais, nem teus avós, desde que começaram a existir sobre a terra até hoje”. Moisés voltou as costas e saiu da presença do faraó.', 6, b'0', 60, 1),
	(1773, 'Os ministros do faraó disseram-lhe: “Até quando este indivíduo será para nós uma armadilha? Deixa essa gente sair para que prestem culto ao Senhor seu Deus. Ainda não vês que o Egito está sendo arruinado?”', 7, b'0', 60, 1),
	(1774, 'Mandaram pois Moisés e Aarão voltar à presença do faraó, que lhes disse: “Ide prestar culto ao Senhor vosso Deus. Quem são os que vão?”', 8, b'0', 60, 1),
	(1775, 'Moisés respondeu: “Iremos com as crianças e os velhos, com nossos filhos e filhas, com as ovelhas e os bois, pois é uma festa do Senhor para nós”.', 9, b'0', 60, 1),
	(1776, 'E o faraó respondeu: “Pudesse o Senhor estar convosco, bem como eu vos deixar sair com os filhos! Vê-se que tendes más intenções.', 10, b'0', 60, 1),
	(1777, 'Não será assim! Ide somente vós, os homens, e prestai culto ao Senhor, pois foi isso que pedistes”. E assim foram expulsos da presença do faraó.', 11, b'0', 60, 1),
	(1778, 'Então o Senhor disse a Moisés: “Estende a mão sobre o Egito, para que os gafanhotos invadam a terra e devorem toda a vegetação do país, tudo o que o granizo poupou”.', 12, b'0', 60, 1),
	(1779, 'Moisés estendeu a vara sobre o Egito, e o Senhor fez soprar o vento oriental sobre o país durante o dia todo e a noite inteira. De manhã, o vento oriental tinha trazido os gafanhotos.', 13, b'0', 60, 1),
	(1780, 'Os gafanhotos invadiram todo o Egito, pousando sobre todo o território do Egito em tão grande quantidade como nunca havia acontecido antes, nem jamais acontecerá.', 14, b'0', 60, 1),
	(1781, 'Encobriram de tal modo a superfície do solo que escureceu. Devoraram toda a vegetação do país, os frutos das árvores e tudo o que o granizo havia deixado. Em todo o Egito não ficou nada de verde nas árvores e nas pastagens.', 15, b'0', 60, 1),
	(1782, 'O faraó mandou chamar com urgência Moisés e Aarão e disse: “Pequei contra o Senhor vosso Deus e contra vós.', 16, b'0', 60, 1),
	(1783, 'Perdoai só mais esta vez o meu pecado e suplicai ao Senhor vosso Deus que afaste de mim ao menos esta praga mortal”.', 17, b'0', 60, 1),
	(1784, 'Moisés saiu da presença do faraó e suplicou ao Senhor.', 18, b'0', 60, 1),
	(1785, 'O Senhor mudou a direção do vento, que começou a soprar muito forte do ocidente, arrastando os gafanhotos e lançando-os no mar Vermelho. Não ficou um só gafanhoto em todo o território do Egito.', 19, b'0', 60, 1),
	(1786, 'O Senhor, porém, endureceu o coração do faraó, que não deixou os israelitas partir.', 20, b'0', 60, 1),
	(1787, 'O Senhor disse a Moisés: “Estende a mão para o céu, e faça-se tal escuridão sobre a terra do Egito, que se possa apalpá-la”.', 21, b'0', 60, 1),
	(1788, 'Moisés estendeu a mão para o céu, e fez-se densa escuridão em todo o Egito durante três dias.', 22, b'0', 60, 1),
	(1789, 'Um não podia ver o outro e, durante três dias, ninguém se moveu do lugar onde estava. Mas onde moravam os israelitas havia luz.', 23, b'0', 60, 1),
	(1790, 'O faraó mandou chamar Moisés e disse: “Ide prestar culto ao Senhor. Também as crianças podem ir convosco, contanto que fiquem aqui as ovelhas e os bois”.', 24, b'0', 60, 1),
	(1791, 'Moisés respondeu: “Mesmo que nos desses as vítimas dos sacrifícios e os holocaustos para oferecer ao Senhor nosso Deus,', 25, b'0', 60, 1),
	(1792, 'ainda assim o nosso gado deveria ir conosco. Não ficará nenhum animal, porque precisamos deles para prestar culto ao Senhor nosso Deus. Pois enquanto não chegarmos lá, nós nem sequer sabemos o que deveremos oferecer ao Senhor”.', 26, b'0', 60, 1),
	(1793, 'Mas o Senhor endureceu o coração do faraó, e este negou-se a deixá-los partir.', 27, b'0', 60, 1),
	(1794, 'O faraó disse a  16 Moisés: “Afasta-te de mim e cuida-te de não tornar a ver a minha face, pois no dia em que vires minha face morrerás”.', 28, b'0', 60, 1),
	(1795, 'Moisés respondeu: “Falaste bem! Nunca mais verei a tua face!”', 29, b'0', 60, 1),
	(1796, 'O Senhor disse a Moisés: “Farei vir mais uma praga sobre o faraó e sobre o Egito. Depois, ele vos deixará partir daqui, e não só vos deixará partir, como vos expulsará definitivamente daqui.', 1, b'0', 61, 1),
	(1797, 'Comunica, pois, ao povo para que cada homem peça ao vizinho e cada mulher à vizinha objetos de prata e de ouro”.', 2, b'0', 61, 1),
	(1798, 'O Senhor fez com que o povo conquistasse as boas graças dos egípcios. O próprio Moisés também era um homem muito considerado na terra do  Egito pelos ministros do faraó e pelo povo.', 3, b'0', 61, 1),
	(1799, 'Moisés disse: “Assim diz o Senhor: À meia- noite farei uma incursão entre os egípcios,', 4, b'0', 61, 1),
	(1800, 'e morrerão todos os primogênitos do Egito, desde  o primogênito do faraó, o herdeiro do seu trono, até o primogênito da escrava que gira a mó do moinho, e até os primogênitos do gado.', 5, b'0', 61, 1),
	(1801, 'Então haverá, em toda a terra do Egito, tamanho grito de aflição como nunca se ouviu, nem jamais se ouvirá.', 6, b'0', 61, 1),
	(1802, 'Mas contra os israelitas nem mesmo um cão latirá, nem contra as pessoas, nem contra os animais, para que saibais que o Senhor faz distinção entre egípcios e israelitas.', 7, b'0', 61, 1),
	(1803, 'Então descerão a mim todos estes teus ministros e se prostrarão diante de mim, dizendo: ‘Sai com todo o povo que te segue!’ Só então eu sairei”. E, fervendo de indignação, Moisés retirou-se da presença do faraó.', 8, b'0', 61, 1),
	(1804, 'O Senhor havia dito a Moisés: “O faraó não vos atenderá, para que se multipliquem os meus prodígios na terra do Egito”.', 9, b'0', 61, 1),
	(1805, 'De fato, Moisés e Aarão tinham realizado todos esses prodígios diante do faraó, mas o Senhor endureceu o coração do faraó, e ele não deixou que os israelitas saíssem de sua terra.', 10, b'0', 61, 1),
	(1806, 'O Senhor disse a Moisés e a Aarão no Egito:', 1, b'0', 62, 1),
	(1807, '“Este mês será para vós o começo dos meses, será o primeiro mês do ano.', 2, b'0', 62, 1),
	(1808, 'Falai assim a toda a comunidade de Israel: No dia dez  17 deste mês, cada um tome um animal por família – um animal para cada casa.', 3, b'0', 62, 1),
	(1809, 'Se a gente da casa for pouca para comer um animal, convidará também o vizinho mais próximo, de acordo com o número de pessoas. Para cada animal deveis calcular o número de pessoas que vão comer.', 4, b'0', 62, 1),
	(1810, 'Animal será sem defeito, macho de um ano. Podereis escolher tanto um cordeiro', 5, b'0', 62, 1),
	(1811, 'Devereis guardá-lo até o dia catorze deste mês, quando, ao cair da tarde, toda a comunidade de Israel reunida o imolará.', 6, b'0', 62, 1),
	(1812, 'Tomarão um pouco do sangue e untarão as ombreiras da porta das casas onde comerem.', 7, b'0', 62, 1),
	(1813, 'Comerão a carne nesta mesma noite. Deverão comê-la assada ao fogo, com pães sem fermento e ervas amargas.', 8, b'0', 62, 1),
	(1814, 'Não deveis comer dessa carne nada de cru, ou cozido em água, mas assado ao fogo, inteiro, com cabeça, pernas e vísceras.', 9, b'0', 62, 1),
	(1815, 'Não deixareis nada para o dia seguinte. O que sobrar, devereis queimá-lo no fogo.', 10, b'0', 62, 1),
	(1816, 'Assim devereis comê-lo: com os cintos na cintura, os pés calçados, o cajado na mão; e comereis às pressas, pois é a Páscoa (isto é, Passagem) do Senhor.', 11, b'0', 62, 1),
	(1817, 'Nessa noite eu passarei pela terra do Egito e matarei todos os primogênitos no país, tanto das pessoas como dos animais. Farei justiça contra todos os deuses do Egito – eu, o Senhor.', 12, b'0', 62, 1),
	(1818, 'O sangue servirá de sinal nas casas onde estiverdes. Ao ver o sangue, passarei adiante, e não vos atingirá a praga exterminadora quando eu ferir a terra do Egito.', 13, b'0', 62, 1),
	(1819, 'Este dia será para vós um memorial em honra do Senhor, que haveis de celebrar por todas as gerações, como instituição perpétua.', 14, b'0', 62, 1),
	(1820, '“Durante sete dias comereis pães sem fermento. Já no primeiro dia fareis desaparecer o fermento de vossas casas, pois quem, entre o primeiro e o sétimo dia, comer pão fermentado, será eliminado de Israel.', 15, b'0', 62, 1),
	(1821, 'No primeiro e no sétimo dia tereis uma assembléia sagrada. Nesses dias não fareis nenhum trabalho, exceto preparar-vos a comida que cada um vai comer.', 16, b'0', 62, 1),
	(1822, 'Assim observareis a festa dos Pães sem Fermento, pois foi nesse dia que eu fiz sair os vossos exércitos do Egito. Guardareis esse dia, por todas as gerações, como instituição perpétua.', 17, b'0', 62, 1),
	(1823, 'Comereis pães sem fermento desde a tarde do dia catorze do primeiro mês até a tarde do dia vinte e um.', 18, b'0', 62, 1),
	(1824, 'Durante sete dias não haja fermento em vossas casas; quem comer pão fermentado será eliminado da comunidade de Israel, seja estrangeiro ou natural do país.', 19, b'0', 62, 1),
	(1825, 'Não comereis coisa alguma fermentada. Em todas as vossas moradias comereis pães sem fermento”.', 20, b'0', 62, 1),
	(1826, 'Moisés convocou todos os anciãos de Israel e lhes disse: “Ide, tomai um animal para cada família e imolai a vítima da Páscoa.', 21, b'0', 62, 1),
	(1827, 'Tomai um ramo de hissopo, molhai-o no sangue que estiver na bacia e marcai com o sangue a moldura das portas. Mas ninguém de vós saia fora de casa até ao amanhecer.', 22, b'0', 62, 1),
	(1828, 'Quando o Senhor passar pelo Egito para castigá-lo, e reparar o sangue sobre a moldura das portas, passará por vossas portas e não permitirá que o Exterminador entre em vossas casas para causar dano.', 23, b'0', 62, 1),
	(1829, 'Observareis este preceito como decreto perpétuo para vós e vossos filhos.', 24, b'0', 62, 1),
	(1830, 'Quando tiverdes entrado na terra que o Senhor vos dará, conforme prometeu, observareis este rito.', 25, b'0', 62, 1),
	(1831, 'Quando vossos filhos vos perguntarem: ‘Que significa este rito?’', 26, b'0', 62, 1),
	(1832, 'respondereis: ‘É o sacrifício da Páscoa do Senhor, que passou ao lado das casas dos israelitas no Egito, quando feriu os egípcios e salvou as nossas casas’”. Então o povo prostrou-se em adoração,', 27, b'0', 62, 1),
	(1833, 'e saindo dali, os israelitas fizeram o que o Senhor tinha ordenado a Moisés e Aarão.', 28, b'0', 62, 1),
	(1834, 'Era meia-noite quando o Senhor feriu todos os primogênitos no Egito, desde o primogênito do faraó, herdeiro de seu trono, até o primogênito do prisioneiro no cárcere, e todos os primogênitos dos animais.', 29, b'0', 62, 1),
	(1835, 'Naquela noite, o faraó levantou-se e, com ele, todos os ministros e todos os egípcios. E ouviu-se no Egito um grande clamor, pois não havia casa onde não houvesse um morto.', 30, b'0', 62, 1),
	(1836, 'faraó chamou Moisés e Aarão de noite e disse: “Ide. Saí do meio de meu povo, tanto vós como os israelitas! Ide sacrificar ao Senhor, como dissestes.', 31, b'0', 62, 1),
	(1837, 'Levai convosco também as ovelhas e o gado, como pedistes; e ao partir abençoai-me”.', 32, b'0', 62, 1),
	(1838, 'Os egípcios pressionavam o povo, urgindo sua saída de sua terra, pois diziam: “Vamos morrer todos!”', 33, b'0', 62, 1),
	(1839, 'Por isso, o povo teve de levar a massa do pão antes de fermentar, carregando aos ombros as amassadeiras envolvidas nos mantos.', 34, b'0', 62, 1),
	(1840, 'Os israelitas tinham feito o que Moisés lhes havia dito e pediram aos egípcios objetos de ouro e de prata e roupas.', 35, b'0', 62, 1),
	(1841, 'O Senhor os fez conquistar as boas graças dos egípcios, que lhes deram o que eles pediram. Assim espoliaram os egípcios.', 36, b'0', 62, 1),
	(1842, 'Os israelitas partiram de Ramsés para Sucot. Eram cerca de seiscentos mil homens a pé, sem contar as crianças.', 37, b'0', 62, 1),
	(1843, 'Além disso, muita outra gente subiu com eles, assim como um numerosíssimo rebanho de ovelhas e bois.', 38, b'0', 62, 1),
	(1844, 'Com a massa trazida do Egito assaram pães sem fermento, pois a massa não pudera fermentar, já que foram expulsos do Egito e não puderam esperar, nem preparar provisões.', 39, b'0', 62, 1),
	(1845, 'A permanência dos israelitas no Egito foi de quatrocentos e trinta anos.', 40, b'0', 62, 1),
	(1846, 'Foi no mesmo dia em que se completaram quatrocentos e trinta anos que todos os exércitos do Senhor saíram da terra do Egito.', 41, b'0', 62, 1),
	(1847, 'Aquela foi uma noite de vigília para o Senhor, quando os fez sair da terra do Egito. Essa mesma noite do Senhor deve ser observada por todos os israelitas, por todas as gerações.', 42, b'0', 62, 1),
	(1848, 'O Senhor disse a Moisés e Aarão: “Eis a lei da Páscoa. Nenhum estrangeiro dela poderá comer.', 43, b'0', 62, 1),
	(1849, 'Todo escravo comprado a dinheiro, depois de circuncidado, poderá comê-la.', 44, b'0', 62, 1),
	(1850, 'O hóspede e o assalariado não poderão dela participar.', 45, b'0', 62, 1),
	(1851, 'O cordeiro será consumido numa só casa. Não levareis para fora da casa nada das carnes, nem lhe quebrareis osso algum.', 46, b'0', 62, 1),
	(1852, 'Toda a comunidade de Israel celebrará a Páscoa.', 47, b'0', 62, 1),
	(1853, 'Se um estrangeiro que vive contigo quiser celebrar a Páscoa do Senhor, fará circuncidar todos os homens da família, e só então poderá participar como se fosse um nativo do país. Mas nenhum incircunciso poderá tomar parte.', 48, b'0', 62, 1),
	(1854, 'a mesma lei servirá para o nativo do país e para o estrangeiro que mora em vosso meio”.', 49, b'0', 62, 1),
	(1855, 'Todos os israelitas fizeram como o Senhor tinha ordenado a Moisés e Aarão.', 50, b'0', 62, 1),
	(1856, 'Foi naquele mesmo dia que o Senhor fez sair do Egito os israelitas, por exércitos.', 51, b'0', 62, 1),
	(1857, 'O Senhor falou a Moisés:', 1, b'0', 63, 1),
	(1858, '“Consagra-me todo primogênito: todo o primeiro parto entre os  israelitas, tanto de homens como de animais, será meu”.', 2, b'0', 63, 1),
	(1859, 'Moisés disse ao povo: “Lembrai- vos do dia em que saístes do Egito, da casa da escravidão, quando, com mão poderosa, o  Senhor vos tirou de lá. Não se comerá nada fermentado.', 3, b'0', 63, 1),
	(1860, 'O dia da saída é no mês de Abib, mes do Trigo novo.', 4, b'0', 63, 1),
	(1861, 'Quando o Senhor te introduzir na terra dos cananeus, heteus, amorreus, heveus e jebuseus, terra que jurou a teus pais te dar, terra onde corre leite e mel, observarás  20 neste mesmo mês este rito:', 5, b'0', 63, 1),
	(1862, 'Durante sete dias comereis pão sem fermento, e no sétimo dia haverá uma festa em honra do Senhor.', 6, b'0', 63, 1),
	(1863, 'Durante os sete dias comer-se-á pão sem fermento, e não se verá pão fermentado, nem fermento em todo o território.', 7, b'0', 63, 1),
	(1864, 'Naquele dia explicarás a teu filho: ‘Isto é pelo que o Senhor fez por mim ao sair do Egito’.', 8, b'0', 63, 1),
	(1865, 'Servirá para ti de sinal em tua mão e de lembrança em tua fronte, para que tenhas na boca a lei do Senhor, porque com mão poderosa o Senhor te fez sair do Egito.', 9, b'0', 63, 1),
	(1866, 'Observarás este decreto cada ano no tempo fixado.', 10, b'0', 63, 1),
	(1867, 'Quando o Senhor te houver introduzido na terra dos cananeus e a tiver dado a ti, conforme jurou a ti e aos teus pais,', 11, b'0', 63, 1),
	(1868, 'separarás para o Senhor todo o primeiro parto do ventre materno, e toda a primeira cria masculinados teus animais pertence ao Senhor.', 12, b'0', 63, 1),
	(1869, 'A primeira cria dos jumentos resgatarás por um cordeiro; se não a resgatares, deverás matá-la. Resgatarás também todo primogênito entre os teus filhos.', 13, b'0', 63, 1),
	(1870, 'E quando teu filho, amanhã, te perguntar:‘ Que significa isto?’ tu lhe dirás: ‘Com mão poderosa o Senhor nos tirou do Egito, da casada escravidão.', 14, b'0', 63, 1),
	(1871, 'Como o faraó teimasse em não nos deixar partir, o Senhor matou todos os primogênitos na terra do Egito, tanto os primogênitos dos homens como os primogênitos dos animais. Por isso eu sacrifico ao Senhor todo primogênito macho dos animais, enquanto resgato todo primogênito de meus filhos’.', 15, b'0', 63, 1),
	(1872, 'Isto servirá como sinal em tua mão e como faixa escrita em tua fronte; pois foi com mão poderosa que o Senhor nos tirou do Egito”.', 16, b'0', 63, 1),
	(1873, 'Quando o faraó deixou sair o povo, Deus não guiou o povo pelo caminho que passa pela terra dos filisteus, embora mais curto, pois achava que, diante de um combate, o povo poderia arrepender-se e voltar para o Egito.', 17, b'0', 63, 1),
	(1874, 'Deus fez o povo dar uma volta pela rota do deserto do mar Vermelho. E os israelitas saíram do Egito bem armados.', 18, b'0', 63, 1),
	(1875, 'Moisés levou consigo os ossos de José, pois este tinha feito jurar os filhos de Israel: “Quando Deus vos visitar, levai embora convosco os meus ossos!”', 19, b'0', 63, 1),
	(1876, 'Partiram de Sucot e acamparam em Etam, na periferia do deserto.', 20, b'0', 63, 1),
	(1877, 'O Senhor os precedia, de dia, numa coluna de nuvem, para lhes mostrar o caminho; de noite, numa coluna de fogo para iluminar, a fim de que pudesse mandar de dia e de noite.', 21, b'0', 63, 1),
	(1878, 'De dia não se afastava do povo a coluna de nuvem, nem de noite a coluna de fogo.', 22, b'0', 63, 1),
	(1879, 'O Senhor falou a Moisés:', 1, b'0', 64, 1),
	(1880, '“Ordena aos israelitas para que mudem de rumo e acampem diante de Piairot, entre Magdol e o mar, diante de Baal Sefon. Ali acampareis perto do mar.', 2, b'0', 64, 1),
	(1881, 'O faraó pensará a respeito dos israelitas: ‘Eles andam perdidos pelo país: o deserto fecha-lhes a passagem’.', 3, b'0', 64, 1),
	(1882, 'Vou endurecer o coração do faraó para que os persiga. Mas eu me cobrirei de glória às custas do faraó e de todo o seu exército, e os egípcios saberão que eu sou o Senhor”. E os israelitas assim fizeram.', 4, b'0', 64, 1),
	(1883, 'O rei do Egito foi informado que o povo tinha fugido. O faraó e os ministros mudaram, então, de atitude em relação ao povo e disseram: “Que fizemos? Deixamos Israel partir, privando-nos dos seus serviços!”', 5, b'0', 64, 1),
	(1884, 'O faraó mandou preparar o seu carro e levou consigo as suas tropas.', 6, b'0', 64, 1),
	(1885, 'Tomou seiscentos carros escolhidos e todos os carros do Egito, com os respectivos escudeiros.', 7, b'0', 64, 1),
	(1886, 'O Senhor endureceu o coração do faraó, rei do Egito, e este perseguiu os israelitas, enquanto eles saíam livremente.', 8, b'0', 64, 1),
	(1887, 'Os egípcios perseguiram-nos com os cavalos e carros do faraó, com os cavaleiros e o exército, e alcançaram-nos acampados perto do mar, na altura de Piairot, defronte de Baal Sefon.', 9, b'0', 64, 1),
	(1888, 'Enquanto o faraó se aproximava, os israelitas, levantando os olhos, viram os egípcios que vinham chegando pela retaguarda. Aterrorizados, os israelitas clamaram ao Senhor', 10, b'0', 64, 1),
	(1889, 'e disseram a Moisés: “Foi por não haver sepulturas no Egito que nos trouxeste para morrermos no deserto? Que vantagem nos deste tirando-nos do Egito?', 11, b'0', 64, 1),
	(1890, 'Não te falávamos assim no Egito: ‘Deixa-nos em paz servir aos egípcios’? Era melhor servir como escravos aos egípcios do que morrer no deserto”.', 12, b'0', 64, 1),
	(1891, 'Moisés respondeu ao povo: “Não temais! Permanecei firmes e vereis a vitória que o Senhor hoje vos dará. Pois os egípcios que hoje estais vendo, nunca mais os tornareis a ver.', 13, b'0', 64, 1),
	(1892, 'O Senhor combaterá por vós; e vós, ficai tranqüilos”.', 14, b'0', 64, 1),
	(1893, 'O Senhor disse a Moisés: “Por que Clamas a mim por socorro? Dize aos israelitas que se ponham em marcha.', 15, b'0', 64, 1),
	(1894, 'Quanto a ti, ergue a tua vara, estende a mão sobre o mar e divide-o, para que os israelitas passem em seco pelo meio do mar.', 16, b'0', 64, 1),
	(1895, 'De minha parte, vou endurecer o coração dos egípcios para que os persigam, e eu seja glorificado às custas do faraó e de todo seu exército, seus carros e cavaleiros.', 17, b'0', 64, 1),
	(1896, 'Os egípcios saberão que eu sou o Senhor, quando eu for glorificado às custas do faraó, dos seus carros e cavaleiros”.', 18, b'0', 64, 1),
	(1897, 'Então o anjo de Deus, que  22 caminhava à frente das tropas de Israel, tomou posição atrás deles: a coluna de nuvem que estava na frente postou-se atrás,', 19, b'0', 64, 1),
	(1898, 'inserindo-se entre o acampamento dos egípcios e o de Israel – a nuvem era tenebrosa, mas iluminava a noite – de modo que durante a noite inteira uns não podiam ver os outros.', 20, b'0', 64, 1),
	(1899, 'Moisés estendeu a mão sobre o mar, e durante a noite inteira o Senhor fez soprar sobre o mar um vento leste muito forte, fazendo recuar o mar e transformando- o em terra seca. As águas se dividiram', 21, b'0', 64, 1),
	(1900, 'e os israelitas entraram pelo meio do mar a pé enxuto,enquanto as águas formavam uma muralha à direita e outra à esquerda deles.', 22, b'0', 64, 1),
	(1901, 'Os egípcios puseram-se a persegui-los, e todos os cavalos do faraó, carros e cavaleiros os seguiram mar adentro.', 23, b'0', 64, 1),
	(1902, 'Na vigília da manhã, de cima da coluna de fogo e de nuvem, o Senhor lançou um olhar sobre as tropas egípcias e as pôs em pânico.', 24, b'0', 64, 1),
	(1903, 'Emperrou as rodas dos carros, de modo que só a muito custo podiam avançar. Então os egípcios disseram: “Vamos fugir de Israel, pois o Senhor combate a favor deles, contra nós”.', 25, b'0', 64, 1),
	(1904, 'Mas o Senhor disse a Moisés: “Estende a mão sobre o mar, e as águas se voltarão contra os egípcios, seus carros e cavaleiros”.', 26, b'0', 64, 1),
	(1905, 'Moisés estendeu a mão sobre o mar e, ao romper da manhã, o mar voltou ao estado normal, enquanto os egípcios em fuga corriam ao encontro das águas. Assim o Senhor lançou os egípcios ao meio do mar.', 27, b'0', 64, 1),
	(1906, 'As águas voltaram e cobriram carros, cavaleiros e todo o exército do faraó, que tinha entrado no mar em perseguição a Israel. Não escapou um só.', 28, b'0', 64, 1),
	(1907, 'Os israelitas, ao contrário, tinham passado a pé enxuto pelo meio do mar, enquanto as águas formavam uma muralha à direita e outra à esquerda deles.', 29, b'0', 64, 1),
	(1908, 'Naquele dia o Senhor livrou Israel da mão dos egípcios, e Israel viu os egípcios mortos nas praias do mar.', 30, b'0', 64, 1),
	(1909, 'Israel viu a mão poderosa do Senhor agir contra o Egito.O povo temeu o Senhor e teve fé no Senhor e em Moisés, seu servo.', 31, b'0', 64, 1),
	(1910, 'Então Moisés e os israelitas cantaram ao Senhor este cântico: “Cantarei ao Senhor porque estupenda foi a vitória; cavalo e cavaleiro ele jogou no mar.', 1, b'0', 65, 1),
	(1911, 'Minha força e meu canto é o Senhor, ele foi para mim a salvação. Ele é meu Deus, eu o glorificarei; o Deus de meu pai, eu o exaltarei.', 2, b'0', 65, 1),
	(1912, 'O Senhor é um guerreiro, seu nome é Senhor.', 3, b'0', 65, 1),
	(1913, 'Precipitou no mar os carros do faraó e seu exército; a elite das tropas afogou-se no mar Vermelho.', 4, b'0', 65, 1),
	(1914, 'Vagalhões os encobriram; mergulharam nas profundezas como pedra.', 5, b'0', 65, 1),
	(1915, 'Tua direita, Senhor, majestosa em poder, tua direita, Senhor, destroça o inimigo.', 6, b'0', 65, 1),
	(1916, 'Com tua grande majestade arrasas o adversário, desencadeias teu furor, que os consome como palha.', 7, b'0', 65, 1),
	(1917, 'Ao sopro de tua ira amontoaram-se as águas, as ondas ergueram-se como um dique, as vagas congelaram no coração do mar.', 8, b'0', 65, 1),
	(1918, 'O inimigo tinha dito: ‘Vou perseguir, alcançar, repartir os despojos, saciar-me deles. Vou tirar minha espada e despojá-los com minha mão’.', 9, b'0', 65, 1),
	(1919, 'Sopraste com teu vento, e o mar os cobriu; afundaram como chumbo em águas profundas.', 10, b'0', 65, 1),
	(1920, 'Quem entre os deuses é como tu, Senhor? Quem como tu, magnífico na santidade, terrível nas proezas, autor de prodígios?', 11, b'0', 65, 1),
	(1921, 'Estendeste tua direita, e a terra os tragou.', 12, b'0', 65, 1),
	(1922, 'Guiaste com amor o povo que resgataste, conduziste-o com poder à tua morada santa.', 13, b'0', 65, 1),
	(1923, 'Os povos ouviram e se alarmaram, o terror apoderou-se dos habitantes da Filistéia.', 14, b'0', 65, 1),
	(1924, 'Então os chefes de Edom estremeceram de medo e os fortes de Moab foram tomados de tremor; perderam a coragem todos os habitantes de Canaã.', 15, b'0', 65, 1),
	(1925, 'Caíram sobre eles o espanto e o pavor. Pela força de teu braço ficaram imóveis como pedra, enquanto teu povo passava, ó Senhor, enquanto passava o povo que adquiriste.', 16, b'0', 65, 1),
	(1926, 'Tu os introduzirás e os plantarás no monte da tua herança, no lugar que preparaste para tua morada, Senhor, no santuário, ó Senhor, que tuas mãos fundaram.', 17, b'0', 65, 1),
	(1927, 'O Senhor reina por todo o sempre!”', 18, b'0', 65, 1),
	(1928, 'De fato, apenas os cavalos do faraó, carros e cavaleiros tinham entrado no mar, o Senhor fez voltar sobre eles as águas do mar. Ao passo que os israelitas passaram pelo meio do mar a pé enxuto.', 19, b'0', 65, 1),
